INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 39, 535, 2, 54, 27, 'ثُلَّةٌ مِنَ الْأَوَّلِينَ', 'ثلة من الاولين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 40, 535, 2, 54, 27, 'وَثُلَّةٌ مِنَ الْآخِرِينَ', 'وثلة من الاخرين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 41, 535, 2, 54, 27, 'وَأَصْحَابُ الشِّمَالِ مَا أَصْحَابُ الشِّمَالِ', 'واصحاب الشمال ما اصحاب الشمال');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 42, 535, 2, 54, 27, 'فِي سَمُومٍ وَحَمِيمٍ', 'في سموم وحميم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 43, 535, 2, 54, 27, 'وَظِلٍّ مِنْ يَحْمُومٍ', 'وظل من يحموم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 44, 535, 2, 54, 27, 'لَا بَارِدٍ وَلَا كَرِيمٍ', 'لا بارد ولا كريم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 45, 535, 2, 54, 27, 'إِنَّهُمْ كَانُوا قَبْلَ ذَلِكَ مُتْرَفِينَ', 'انهم كانوا قبل ذلك مترفين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 46, 535, 2, 54, 27, 'وَكَانُوا يُصِرُّونَ عَلَى الْحِنْثِ الْعَظِيمِ', 'وكانوا يصرون على الحنث العظيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 47, 535, 2, 54, 27, 'وَكَانُوا يَقُولُونَ أَئِذَا مِتْنَا وَكُنَّا تُرَابًا وَعِظَامًا أَإِنَّا لَمَبْعُوثُونَ', 'وكانوا يقولون ائذا متنا وكنا ترابا وعظاما اانا لمبعوثون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 48, 535, 2, 54, 27, 'أَوَآبَاؤُنَا الْأَوَّلُونَ', 'اواباؤنا الاولون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 49, 535, 2, 54, 27, 'قُلْ إِنَّ الْأَوَّلِينَ وَالْآخِرِينَ', 'قل ان الاولين والاخرين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 50, 535, 2, 54, 27, 'لَمَجْمُوعُونَ إِلَى مِيقَاتِ يَوْمٍ مَعْلُومٍ', 'لمجموعون الى ميقات يوم معلوم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 51, 536, 2, 54, 27, 'ثُمَّ إِنَّكُمْ أَيُّهَا الضَّالُّونَ الْمُكَذِّبُونَ', 'ثم انكم ايها الضالون المكذبون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 52, 536, 2, 54, 27, 'لَآكِلُونَ مِنْ شَجَرٍ مِنْ زَقُّومٍ', 'لاكلون من شجر من زقوم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 53, 536, 2, 54, 27, 'فَمَالِئُونَ مِنْهَا الْبُطُونَ', 'فمالئون منها البطون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 54, 536, 2, 54, 27, 'فَشَارِبُونَ عَلَيْهِ مِنَ الْحَمِيمِ', 'فشاربون عليه من الحميم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 55, 536, 2, 54, 27, 'فَشَارِبُونَ شُرْبَ الْهِيمِ', 'فشاربون شرب الهيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 56, 536, 2, 54, 27, 'هَذَا نُزُلُهُمْ يَوْمَ الدِّينِ', 'هذا نزلهم يوم الدين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 57, 536, 2, 54, 27, 'نَحْنُ خَلَقْنَاكُمْ فَلَوْلَا تُصَدِّقُونَ', 'نحن خلقناكم فلولا تصدقون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 58, 536, 2, 54, 27, 'أَفَرَأَيْتُمْ مَا تُمْنُونَ', 'افرايتم ما تمنون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 59, 536, 2, 54, 27, 'أَأَنْتُمْ تَخْلُقُونَهُ أَمْ نَحْنُ الْخَالِقُونَ', 'اانتم تخلقونه ام نحن الخالقون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 60, 536, 2, 54, 27, 'نَحْنُ قَدَّرْنَا بَيْنَكُمُ الْمَوْتَ وَمَا نَحْنُ بِمَسْبُوقِينَ', 'نحن قدرنا بينكم الموت وما نحن بمسبوقين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 61, 536, 2, 54, 27, 'عَلَى أَنْ نُبَدِّلَ أَمْثَالَكُمْ وَنُنْشِئَكُمْ فِي مَا لَا تَعْلَمُونَ', 'على ان نبدل امثالكم وننشئكم في ما لا تعلمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 62, 536, 2, 54, 27, 'وَلَقَدْ عَلِمْتُمُ النَّشْأَةَ الْأُولَى فَلَوْلَا تَذَكَّرُونَ', 'ولقد علمتم النشاة الاولى فلولا تذكرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 63, 536, 2, 54, 27, 'أَفَرَأَيْتُمْ مَا تَحْرُثُونَ', 'افرايتم ما تحرثون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 64, 536, 2, 54, 27, 'أَأَنْتُمْ تَزْرَعُونَهُ أَمْ نَحْنُ الزَّارِعُونَ', 'اانتم تزرعونه ام نحن الزارعون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 65, 536, 2, 54, 27, 'لَوْ نَشَاءُ لَجَعَلْنَاهُ حُطَامًا فَظَلْتُمْ تَفَكَّهُونَ', 'لو نشاء لجعلناه حطاما فظلتم تفكهون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 66, 536, 2, 54, 27, 'إِنَّا لَمُغْرَمُونَ', 'انا لمغرمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 67, 536, 2, 54, 27, 'بَلْ نَحْنُ مَحْرُومُونَ', 'بل نحن محرومون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 68, 536, 2, 54, 27, 'أَفَرَأَيْتُمُ الْمَاءَ الَّذِي تَشْرَبُونَ', 'افرايتم الماء الذي تشربون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 69, 536, 2, 54, 27, 'أَأَنْتُمْ أَنْزَلْتُمُوهُ مِنَ الْمُزْنِ أَمْ نَحْنُ الْمُنْزِلُونَ', 'اانتم انزلتموه من المزن ام نحن المنزلون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 70, 536, 2, 54, 27, 'لَوْ نَشَاءُ جَعَلْنَاهُ أُجَاجًا فَلَوْلَا تَشْكُرُونَ', 'لو نشاء جعلناه اجاجا فلولا تشكرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 71, 536, 2, 54, 27, 'أَفَرَأَيْتُمُ النَّارَ الَّتِي تُورُونَ', 'افرايتم النار التي تورون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 72, 536, 2, 54, 27, 'أَأَنْتُمْ أَنْشَأْتُمْ شَجَرَتَهَا أَمْ نَحْنُ الْمُنْشِئُونَ', 'اانتم انشاتم شجرتها ام نحن المنشئون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 73, 536, 2, 54, 27, 'نَحْنُ جَعَلْنَاهَا تَذْكِرَةً وَمَتَاعًا لِلْمُقْوِينَ', 'نحن جعلناها تذكرة ومتاعا للمقوين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 74, 536, 2, 54, 27, 'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيمِ', 'فسبح باسم ربك العظيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 75, 536, 3, 54, 27, 'فَلَا أُقْسِمُ بِمَوَاقِعِ النُّجُومِ', 'فلا اقسم بمواقع النجوم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 76, 536, 3, 54, 27, 'وَإِنَّهُ لَقَسَمٌ لَوْ تَعْلَمُونَ عَظِيمٌ', 'وانه لقسم لو تعلمون عظيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 77, 537, 3, 54, 27, 'إِنَّهُ لَقُرْآنٌ كَرِيمٌ', 'انه لقران كريم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 78, 537, 3, 54, 27, 'فِي كِتَابٍ مَكْنُونٍ', 'في كتاب مكنون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 79, 537, 3, 54, 27, 'لَا يَمَسُّهُ إِلَّا الْمُطَهَّرُونَ', 'لا يمسه الا المطهرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 80, 537, 3, 54, 27, 'تَنْزِيلٌ مِنْ رَبِّ الْعَالَمِينَ', 'تنزيل من رب العالمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 81, 537, 3, 54, 27, 'أَفَبِهَذَا الْحَدِيثِ أَنْتُمْ مُدْهِنُونَ', 'افبهذا الحديث انتم مدهنون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 82, 537, 3, 54, 27, 'وَتَجْعَلُونَ رِزْقَكُمْ أَنَّكُمْ تُكَذِّبُونَ', 'وتجعلون رزقكم انكم تكذبون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 83, 537, 3, 54, 27, 'فَلَوْلَا إِذَا بَلَغَتِ الْحُلْقُومَ', 'فلولا اذا بلغت الحلقوم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 84, 537, 3, 54, 27, 'وَأَنْتُمْ حِينَئِذٍ تَنْظُرُونَ', 'وانتم حينئذ تنظرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 85, 537, 3, 54, 27, 'وَنَحْنُ أَقْرَبُ إِلَيْهِ مِنْكُمْ وَلَكِنْ لَا تُبْصِرُونَ', 'ونحن اقرب اليه منكم ولكن لا تبصرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 86, 537, 3, 54, 27, 'فَلَوْلَا إِنْ كُنْتُمْ غَيْرَ مَدِينِينَ', 'فلولا ان كنتم غير مدينين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 87, 537, 3, 54, 27, 'تَرْجِعُونَهَا إِنْ كُنْتُمْ صَادِقِينَ', 'ترجعونها ان كنتم صادقين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 88, 537, 3, 54, 27, 'فَأَمَّا إِنْ كَانَ مِنَ الْمُقَرَّبِينَ', 'فاما ان كان من المقربين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 89, 537, 3, 54, 27, 'فَرَوْحٌ وَرَيْحَانٌ وَجَنَّتُ نَعِيمٍ', 'فروح وريحان وجنت نعيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 90, 537, 3, 54, 27, 'وَأَمَّا إِنْ كَانَ مِنْ أَصْحَابِ الْيَمِينِ', 'واما ان كان من اصحاب اليمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 91, 537, 3, 54, 27, 'فَسَلَامٌ لَكَ مِنْ أَصْحَابِ الْيَمِينِ', 'فسلام لك من اصحاب اليمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 92, 537, 3, 54, 27, 'وَأَمَّا إِنْ كَانَ مِنَ الْمُكَذِّبِينَ الضَّالِّينَ', 'واما ان كان من المكذبين الضالين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 93, 537, 3, 54, 27, 'فَنُزُلٌ مِنْ حَمِيمٍ', 'فنزل من حميم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 94, 537, 3, 54, 27, 'وَتَصْلِيَةُ جَحِيمٍ', 'وتصلية جحيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 95, 537, 3, 54, 27, 'إِنَّ هَذَا لَهُوَ حَقُّ الْيَقِينِ', 'ان هذا لهو حق اليقين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 56, 96, 537, 3, 54, 27, 'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيمِ', 'فسبح باسم ربك العظيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 1, 537, 3, 54, 27, 'سَبَّحَ لِلَّهِ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ وَهُوَ الْعَزِيزُ الْحَكِيمُ', 'سبح لله ما في السماوات والارض وهو العزيز الحكيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 2, 537, 3, 54, 27, 'لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ يُحْيِي وَيُمِيتُ وَهُوَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ', 'له ملك السماوات والارض يحيي ويميت وهو على كل شيء قدير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 3, 537, 3, 54, 27, 'هُوَ الْأَوَّلُ وَالْآخِرُ وَالظَّاهِرُ وَالْبَاطِنُ وَهُوَ بِكُلِّ شَيْءٍ عَلِيمٌ', 'هو الاول والاخر والظاهر والباطن وهو بكل شيء عليم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 4, 538, 3, 54, 27, 'هُوَ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوَى عَلَى الْعَرْشِ يَعْلَمُ مَا يَلِجُ فِي الْأَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنْزِلُ مِنَ السَّمَاءِ وَمَا يَعْرُجُ فِيهَا وَهُوَ مَعَكُمْ أَيْنَ مَا كُنْتُمْ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ', 'هو الذي خلق السماوات والارض في ستة ايام ثم استوى على العرش يعلم ما يلج في الارض وما يخرج منها وما ينزل من السماء وما يعرج فيها وهو معكم اين ما كنتم والله بما تعملون بصير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 5, 538, 3, 54, 27, 'لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ وَإِلَى اللَّهِ تُرْجَعُ الْأُمُورُ', 'له ملك السماوات والارض والى الله ترجع الامور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 6, 538, 3, 54, 27, 'يُولِجُ اللَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي اللَّيْلِ وَهُوَ عَلِيمٌ بِذَاتِ الصُّدُورِ', 'يولج الليل في النهار ويولج النهار في الليل وهو عليم بذات الصدور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 7, 538, 3, 54, 27, 'آمِنُوا بِاللَّهِ وَرَسُولِهِ وَأَنْفِقُوا مِمَّا جَعَلَكُمْ مُسْتَخْلَفِينَ فِيهِ فَالَّذِينَ آمَنُوا مِنْكُمْ وَأَنْفَقُوا لَهُمْ أَجْرٌ كَبِيرٌ', 'امنوا بالله ورسوله وانفقوا مما جعلكم مستخلفين فيه فالذين امنوا منكم وانفقوا لهم اجر كبير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 8, 538, 3, 54, 27, 'وَمَا لَكُمْ لَا تُؤْمِنُونَ بِاللَّهِ وَالرَّسُولُ يَدْعُوكُمْ لِتُؤْمِنُوا بِرَبِّكُمْ وَقَدْ أَخَذَ مِيثَاقَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ', 'وما لكم لا تؤمنون بالله والرسول يدعوكم لتؤمنوا بربكم وقد اخذ ميثاقكم ان كنتم مؤمنين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 9, 538, 3, 54, 27, 'هُوَ الَّذِي يُنَزِّلُ عَلَى عَبْدِهِ آيَاتٍ بَيِّنَاتٍ لِيُخْرِجَكُمْ مِنَ الظُّلُمَاتِ إِلَى النُّورِ وَإِنَّ اللَّهَ بِكُمْ لَرَءُوفٌ رَحِيمٌ', 'هو الذي ينزل على عبده ايات بينات ليخرجكم من الظلمات الى النور وان الله بكم لرءوف رحيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 10, 538, 3, 54, 27, 'وَمَا لَكُمْ أَلَّا تُنْفِقُوا فِي سَبِيلِ اللَّهِ وَلِلَّهِ مِيرَاثُ السَّمَاوَاتِ وَالْأَرْضِ لَا يَسْتَوِي مِنْكُمْ مَنْ أَنْفَقَ مِنْ قَبْلِ الْفَتْحِ وَقَاتَلَ أُولَئِكَ أَعْظَمُ دَرَجَةً مِنَ الَّذِينَ أَنْفَقُوا مِنْ بَعْدُ وَقَاتَلُوا وَكُلًّا وَعَدَ اللَّهُ الْحُسْنَى وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ', 'وما لكم الا تنفقوا في سبيل الله ولله ميراث السماوات والارض لا يستوي منكم من انفق من قبل الفتح وقاتل اولئك اعظم درجة من الذين انفقوا من بعد وقاتلوا وكلا وعد الله الحسنى والله بما تعملون خبير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 11, 538, 3, 54, 27, 'مَنْ ذَا الَّذِي يُقْرِضُ اللَّهَ قَرْضًا حَسَنًا فَيُضَاعِفَهُ لَهُ وَلَهُ أَجْرٌ كَرِيمٌ', 'من ذا الذي يقرض الله قرضا حسنا فيضاعفه له وله اجر كريم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 12, 539, 3, 54, 27, 'يَوْمَ تَرَى الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ يَسْعَى نُورُهُمْ بَيْنَ أَيْدِيهِمْ وَبِأَيْمَانِهِمْ بُشْرَاكُمُ الْيَوْمَ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ذَلِكَ هُوَ الْفَوْزُ الْعَظِيمُ', 'يوم ترى المؤمنين والمؤمنات يسعى نورهم بين ايديهم وبايمانهم بشراكم اليوم جنات تجري من تحتها الانهار خالدين فيها ذلك هو الفوز العظيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 13, 539, 3, 54, 27, 'يَوْمَ يَقُولُ الْمُنَافِقُونَ وَالْمُنَافِقَاتُ لِلَّذِينَ آمَنُوا انْظُرُونَا نَقْتَبِسْ مِنْ نُورِكُمْ قِيلَ ارْجِعُوا وَرَاءَكُمْ فَالْتَمِسُوا نُورًا فَضُرِبَ بَيْنَهُمْ بِسُورٍ لَهُ بَابٌ بَاطِنُهُ فِيهِ الرَّحْمَةُ وَظَاهِرُهُ مِنْ قِبَلِهِ الْعَذَابُ', 'يوم يقول المنافقون والمنافقات للذين امنوا انظرونا نقتبس من نوركم قيل ارجعوا وراءكم فالتمسوا نورا فضرب بينهم بسور له باب باطنه فيه الرحمة وظاهره من قبله العذاب');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 14, 539, 3, 54, 27, 'يُنَادُونَهُمْ أَلَمْ نَكُنْ مَعَكُمْ قَالُوا بَلَى وَلَكِنَّكُمْ فَتَنْتُمْ أَنْفُسَكُمْ وَتَرَبَّصْتُمْ وَارْتَبْتُمْ وَغَرَّتْكُمُ الْأَمَانِيُّ حَتَّى جَاءَ أَمْرُ اللَّهِ وَغَرَّكُمْ بِاللَّهِ الْغَرُورُ', 'ينادونهم الم نكن معكم قالوا بلى ولكنكم فتنتم انفسكم وتربصتم وارتبتم وغرتكم الاماني حتى جاء امر الله وغركم بالله الغرور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 15, 539, 3, 54, 27, 'فَالْيَوْمَ لَا يُؤْخَذُ مِنْكُمْ فِدْيَةٌ وَلَا مِنَ الَّذِينَ كَفَرُوا مَأْوَاكُمُ النَّارُ هِيَ مَوْلَاكُمْ وَبِئْسَ الْمَصِيرُ', 'فاليوم لا يؤخذ منكم فدية ولا من الذين كفروا ماواكم النار هي مولاكم وبئس المصير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 16, 539, 4, 54, 27, 'أَلَمْ يَأْنِ لِلَّذِينَ آمَنُوا أَنْ تَخْشَعَ قُلُوبُهُمْ لِذِكْرِ اللَّهِ وَمَا نَزَلَ مِنَ الْحَقِّ وَلَا يَكُونُوا كَالَّذِينَ أُوتُوا الْكِتَابَ مِنْ قَبْلُ فَطَالَ عَلَيْهِمُ الْأَمَدُ فَقَسَتْ قُلُوبُهُمْ وَكَثِيرٌ مِنْهُمْ فَاسِقُونَ', 'الم يان للذين امنوا ان تخشع قلوبهم لذكر الله وما نزل من الحق ولا يكونوا كالذين اوتوا الكتاب من قبل فطال عليهم الامد فقست قلوبهم وكثير منهم فاسقون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 17, 539, 4, 54, 27, 'اعْلَمُوا أَنَّ اللَّهَ يُحْيِ الْأَرْضَ بَعْدَ مَوْتِهَا قَدْ بَيَّنَّا لَكُمُ الْآيَاتِ لَعَلَّكُمْ تَعْقِلُونَ', 'اعلموا ان الله يحي الارض بعد موتها قد بينا لكم الايات لعلكم تعقلون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 18, 539, 4, 54, 27, 'إِنَّ الْمُصَّدِّقِينَ وَالْمُصَّدِّقَاتِ وَأَقْرَضُوا اللَّهَ قَرْضًا حَسَنًا يُضَاعَفُ لَهُمْ وَلَهُمْ أَجْرٌ كَرِيمٌ', 'ان المصدقين والمصدقات واقرضوا الله قرضا حسنا يضاعف لهم ولهم اجر كريم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 19, 540, 4, 54, 27, 'وَالَّذِينَ آمَنُوا بِاللَّهِ وَرُسُلِهِ أُولَئِكَ هُمُ الصِّدِّيقُونَ وَالشُّهَدَاءُ عِنْدَ رَبِّهِمْ لَهُمْ أَجْرُهُمْ وَنُورُهُمْ وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَئِكَ أَصْحَابُ الْجَحِيمِ', 'والذين امنوا بالله ورسله اولئك هم الصديقون والشهداء عند ربهم لهم اجرهم ونورهم والذين كفروا وكذبوا باياتنا اولئك اصحاب الجحيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 20, 540, 4, 54, 27, 'اعْلَمُوا أَنَّمَا الْحَيَاةُ الدُّنْيَا لَعِبٌ وَلَهْوٌ وَزِينَةٌ وَتَفَاخُرٌ بَيْنَكُمْ وَتَكَاثُرٌ فِي الْأَمْوَالِ وَالْأَوْلَادِ كَمَثَلِ غَيْثٍ أَعْجَبَ الْكُفَّارَ نَبَاتُهُ ثُمَّ يَهِيجُ فَتَرَاهُ مُصْفَرًّا ثُمَّ يَكُونُ حُطَامًا وَفِي الْآخِرَةِ عَذَابٌ شَدِيدٌ وَمَغْفِرَةٌ مِنَ اللَّهِ وَرِضْوَانٌ وَمَا الْحَيَاةُ الدُّنْيَا إِلَّا مَتَاعُ الْغُرُورِ', 'اعلموا انما الحياة الدنيا لعب ولهو وزينة وتفاخر بينكم وتكاثر في الاموال والاولاد كمثل غيث اعجب الكفار نباته ثم يهيج فتراه مصفرا ثم يكون حطاما وفي الاخرة عذاب شديد ومغفرة من الله ورضوان وما الحياة الدنيا الا متاع الغرور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 21, 540, 4, 54, 27, 'سَابِقُوا إِلَى مَغْفِرَةٍ مِنْ رَبِّكُمْ وَجَنَّةٍ عَرْضُهَا كَعَرْضِ السَّمَاءِ وَالْأَرْضِ أُعِدَّتْ لِلَّذِينَ آمَنُوا بِاللَّهِ وَرُسُلِهِ ذَلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ', 'سابقوا الى مغفرة من ربكم وجنة عرضها كعرض السماء والارض اعدت للذين امنوا بالله ورسله ذلك فضل الله يؤتيه من يشاء والله ذو الفضل العظيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 22, 540, 4, 54, 27, 'مَا أَصَابَ مِنْ مُصِيبَةٍ فِي الْأَرْضِ وَلَا فِي أَنْفُسِكُمْ إِلَّا فِي كِتَابٍ مِنْ قَبْلِ أَنْ نَبْرَأَهَا إِنَّ ذَلِكَ عَلَى اللَّهِ يَسِيرٌ', 'ما اصاب من مصيبة في الارض ولا في انفسكم الا في كتاب من قبل ان نبراها ان ذلك على الله يسير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 23, 540, 4, 54, 27, 'لِكَيْلَا تَأْسَوْا عَلَى مَا فَاتَكُمْ وَلَا تَفْرَحُوا بِمَا آتَاكُمْ وَاللَّهُ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُورٍ', 'لكيلا تاسوا على ما فاتكم ولا تفرحوا بما اتاكم والله لا يحب كل مختال فخور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 24, 540, 4, 54, 27, 'الَّذِينَ يَبْخَلُونَ وَيَأْمُرُونَ النَّاسَ بِالْبُخْلِ وَمَنْ يَتَوَلَّ فَإِنَّ اللَّهَ هُوَ الْغَنِيُّ الْحَمِيدُ', 'الذين يبخلون ويامرون الناس بالبخل ومن يتول فان الله هو الغني الحميد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 25, 541, 4, 54, 27, 'لَقَدْ أَرْسَلْنَا رُسُلَنَا بِالْبَيِّنَاتِ وَأَنْزَلْنَا مَعَهُمُ الْكِتَابَ وَالْمِيزَانَ لِيَقُومَ النَّاسُ بِالْقِسْطِ وَأَنْزَلْنَا الْحَدِيدَ فِيهِ بَأْسٌ شَدِيدٌ وَمَنَافِعُ لِلنَّاسِ وَلِيَعْلَمَ اللَّهُ مَنْ يَنْصُرُهُ وَرُسُلَهُ بِالْغَيْبِ إِنَّ اللَّهَ قَوِيٌّ عَزِيزٌ', 'لقد ارسلنا رسلنا بالبينات وانزلنا معهم الكتاب والميزان ليقوم الناس بالقسط وانزلنا الحديد فيه باس شديد ومنافع للناس وليعلم الله من ينصره ورسله بالغيب ان الله قوي عزيز');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 26, 541, 4, 54, 27, 'وَلَقَدْ أَرْسَلْنَا نُوحًا وَإِبْرَاهِيمَ وَجَعَلْنَا فِي ذُرِّيَّتِهِمَا النُّبُوَّةَ وَالْكِتَابَ فَمِنْهُمْ مُهْتَدٍ وَكَثِيرٌ مِنْهُمْ فَاسِقُونَ', 'ولقد ارسلنا نوحا وابراهيم وجعلنا في ذريتهما النبوة والكتاب فمنهم مهتد وكثير منهم فاسقون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 27, 541, 4, 54, 27, 'ثُمَّ قَفَّيْنَا عَلَى آثَارِهِمْ بِرُسُلِنَا وَقَفَّيْنَا بِعِيسَى ابْنِ مَرْيَمَ وَآتَيْنَاهُ الْإِنْجِيلَ وَجَعَلْنَا فِي قُلُوبِ الَّذِينَ اتَّبَعُوهُ رَأْفَةً وَرَحْمَةً وَرَهْبَانِيَّةً ابْتَدَعُوهَا مَا كَتَبْنَاهَا عَلَيْهِمْ إِلَّا ابْتِغَاءَ رِضْوَانِ اللَّهِ فَمَا رَعَوْهَا حَقَّ رِعَايَتِهَا فَآتَيْنَا الَّذِينَ آمَنُوا مِنْهُمْ أَجْرَهُمْ وَكَثِيرٌ مِنْهُمْ فَاسِقُونَ', 'ثم قفينا على اثارهم برسلنا وقفينا بعيسى ابن مريم واتيناه الانجيل وجعلنا في قلوب الذين اتبعوه رافة ورحمة ورهبانية ابتدعوها ما كتبناها عليهم الا ابتغاء رضوان الله فما رعوها حق رعايتها فاتينا الذين امنوا منهم اجرهم وكثير منهم فاسقون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 28, 541, 4, 54, 27, 'يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَآمِنُوا بِرَسُولِهِ يُؤْتِكُمْ كِفْلَيْنِ مِنْ رَحْمَتِهِ وَيَجْعَلْ لَكُمْ نُورًا تَمْشُونَ بِهِ وَيَغْفِرْ لَكُمْ وَاللَّهُ غَفُورٌ رَحِيمٌ', 'يا ايها الذين امنوا اتقوا الله وامنوا برسوله يؤتكم كفلين من رحمته ويجعل لكم نورا تمشون به ويغفر لكم والله غفور رحيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 57, 29, 541, 4, 54, 27, 'لِئَلَّا يَعْلَمَ أَهْلُ الْكِتَابِ أَلَّا يَقْدِرُونَ عَلَى شَيْءٍ مِنْ فَضْلِ اللَّهِ وَأَنَّ الْفَضْلَ بِيَدِ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ', 'لئلا يعلم اهل الكتاب الا يقدرون على شيء من فضل الله وان الفضل بيد الله يؤتيه من يشاء والله ذو الفضل العظيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 1, 542, 1, 55, 28, 'قَدْ سَمِعَ اللَّهُ قَوْلَ الَّتِي تُجَادِلُكَ فِي زَوْجِهَا وَتَشْتَكِي إِلَى اللَّهِ وَاللَّهُ يَسْمَعُ تَحَاوُرَكُمَا إِنَّ اللَّهَ سَمِيعٌ بَصِيرٌ', 'قد سمع الله قول التي تجادلك في زوجها وتشتكي الى الله والله يسمع تحاوركما ان الله سميع بصير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 2, 542, 1, 55, 28, 'الَّذِينَ يُظَاهِرُونَ مِنْكُمْ مِنْ نِسَائِهِمْ مَا هُنَّ أُمَّهَاتِهِمْ إِنْ أُمَّهَاتُهُمْ إِلَّا اللَّائِي وَلَدْنَهُمْ وَإِنَّهُمْ لَيَقُولُونَ مُنْكَرًا مِنَ الْقَوْلِ وَزُورًا وَإِنَّ اللَّهَ لَعَفُوٌّ غَفُورٌ', 'الذين يظاهرون منكم من نسائهم ما هن امهاتهم ان امهاتهم الا اللائي ولدنهم وانهم ليقولون منكرا من القول وزورا وان الله لعفو غفور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 3, 542, 1, 55, 28, 'وَالَّذِينَ يُظَاهِرُونَ مِنْ نِسَائِهِمْ ثُمَّ يَعُودُونَ لِمَا قَالُوا فَتَحْرِيرُ رَقَبَةٍ مِنْ قَبْلِ أَنْ يَتَمَاسَّا ذَلِكُمْ تُوعَظُونَ بِهِ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ', 'والذين يظاهرون من نسائهم ثم يعودون لما قالوا فتحرير رقبة من قبل ان يتماسا ذلكم توعظون به والله بما تعملون خبير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 4, 542, 1, 55, 28, 'فَمَنْ لَمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِ مِنْ قَبْلِ أَنْ يَتَمَاسَّا فَمَنْ لَمْ يَسْتَطِعْ فَإِطْعَامُ سِتِّينَ مِسْكِينًا ذَلِكَ لِتُؤْمِنُوا بِاللَّهِ وَرَسُولِهِ وَتِلْكَ حُدُودُ اللَّهِ وَلِلْكَافِرِينَ عَذَابٌ أَلِيمٌ', 'فمن لم يجد فصيام شهرين متتابعين من قبل ان يتماسا فمن لم يستطع فاطعام ستين مسكينا ذلك لتؤمنوا بالله ورسوله وتلك حدود الله وللكافرين عذاب اليم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 5, 542, 1, 55, 28, 'إِنَّ الَّذِينَ يُحَادُّونَ اللَّهَ وَرَسُولَهُ كُبِتُوا كَمَا كُبِتَ الَّذِينَ مِنْ قَبْلِهِمْ وَقَدْ أَنْزَلْنَا آيَاتٍ بَيِّنَاتٍ وَلِلْكَافِرِينَ عَذَابٌ مُهِينٌ', 'ان الذين يحادون الله ورسوله كبتوا كما كبت الذين من قبلهم وقد انزلنا ايات بينات وللكافرين عذاب مهين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 6, 542, 1, 55, 28, 'يَوْمَ يَبْعَثُهُمُ اللَّهُ جَمِيعًا فَيُنَبِّئُهُمْ بِمَا عَمِلُوا أَحْصَاهُ اللَّهُ وَنَسُوهُ وَاللَّهُ عَلَى كُلِّ شَيْءٍ شَهِيدٌ', 'يوم يبعثهم الله جميعا فينبئهم بما عملوا احصاه الله ونسوه والله على كل شيء شهيد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 7, 543, 1, 55, 28, 'أَلَمْ تَرَ أَنَّ اللَّهَ يَعْلَمُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ مَا يَكُونُ مِنْ نَجْوَى ثَلَاثَةٍ إِلَّا هُوَ رَابِعُهُمْ وَلَا خَمْسَةٍ إِلَّا هُوَ سَادِسُهُمْ وَلَا أَدْنَى مِنْ ذَلِكَ وَلَا أَكْثَرَ إِلَّا هُوَ مَعَهُمْ أَيْنَ مَا كَانُوا ثُمَّ يُنَبِّئُهُمْ بِمَا عَمِلُوا يَوْمَ الْقِيَامَةِ إِنَّ اللَّهَ بِكُلِّ شَيْءٍ عَلِيمٌ', 'الم تر ان الله يعلم ما في السماوات وما في الارض ما يكون من نجوى ثلاثة الا هو رابعهم ولا خمسة الا هو سادسهم ولا ادنى من ذلك ولا اكثر الا هو معهم اين ما كانوا ثم ينبئهم بما عملوا يوم القيامة ان الله بكل شيء عليم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 8, 543, 1, 55, 28, 'أَلَمْ تَرَ إِلَى الَّذِينَ نُهُوا عَنِ النَّجْوَى ثُمَّ يَعُودُونَ لِمَا نُهُوا عَنْهُ وَيَتَنَاجَوْنَ بِالْإِثْمِ وَالْعُدْوَانِ وَمَعْصِيَتِ الرَّسُولِ وَإِذَا جَاءُوكَ حَيَّوْكَ بِمَا لَمْ يُحَيِّكَ بِهِ اللَّهُ وَيَقُولُونَ فِي أَنْفُسِهِمْ لَوْلَا يُعَذِّبُنَا اللَّهُ بِمَا نَقُولُ حَسْبُهُمْ جَهَنَّمُ يَصْلَوْنَهَا فَبِئْسَ الْمَصِيرُ', 'الم تر الى الذين نهوا عن النجوى ثم يعودون لما نهوا عنه ويتناجون بالاثم والعدوان ومعصيت الرسول واذا جاءوك حيوك بما لم يحيك به الله ويقولون في انفسهم لولا يعذبنا الله بما نقول حسبهم جهنم يصلونها فبئس المصير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 9, 543, 1, 55, 28, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا تَنَاجَيْتُمْ فَلَا تَتَنَاجَوْا بِالْإِثْمِ وَالْعُدْوَانِ وَمَعْصِيَتِ الرَّسُولِ وَتَنَاجَوْا بِالْبِرِّ وَالتَّقْوَى وَاتَّقُوا اللَّهَ الَّذِي إِلَيْهِ تُحْشَرُونَ', 'يا ايها الذين امنوا اذا تناجيتم فلا تتناجوا بالاثم والعدوان ومعصيت الرسول وتناجوا بالبر والتقوى واتقوا الله الذي اليه تحشرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 10, 543, 1, 55, 28, 'إِنَّمَا النَّجْوَى مِنَ الشَّيْطَانِ لِيَحْزُنَ الَّذِينَ آمَنُوا وَلَيْسَ بِضَارِّهِمْ شَيْئًا إِلَّا بِإِذْنِ اللَّهِ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ', 'انما النجوى من الشيطان ليحزن الذين امنوا وليس بضارهم شيئا الا باذن الله وعلى الله فليتوكل المؤمنون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 11, 543, 1, 55, 28, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا قِيلَ لَكُمْ تَفَسَّحُوا فِي الْمَجَالِسِ فَافْسَحُوا يَفْسَحِ اللَّهُ لَكُمْ وَإِذَا قِيلَ انْشُزُوا فَانْشُزُوا يَرْفَعِ اللَّهُ الَّذِينَ آمَنُوا مِنْكُمْ وَالَّذِينَ أُوتُوا الْعِلْمَ دَرَجَاتٍ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ', 'يا ايها الذين امنوا اذا قيل لكم تفسحوا في المجالس فافسحوا يفسح الله لكم واذا قيل انشزوا فانشزوا يرفع الله الذين امنوا منكم والذين اوتوا العلم درجات والله بما تعملون خبير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 12, 544, 1, 55, 28, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا نَاجَيْتُمُ الرَّسُولَ فَقَدِّمُوا بَيْنَ يَدَيْ نَجْوَاكُمْ صَدَقَةً ذَلِكَ خَيْرٌ لَكُمْ وَأَطْهَرُ فَإِنْ لَمْ تَجِدُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 'يا ايها الذين امنوا اذا ناجيتم الرسول فقدموا بين يدي نجواكم صدقة ذلك خير لكم واطهر فان لم تجدوا فان الله غفور رحيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 13, 544, 1, 55, 28, 'أَأَشْفَقْتُمْ أَنْ تُقَدِّمُوا بَيْنَ يَدَيْ نَجْوَاكُمْ صَدَقَاتٍ فَإِذْ لَمْ تَفْعَلُوا وَتَابَ اللَّهُ عَلَيْكُمْ فَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ وَأَطِيعُوا اللَّهَ وَرَسُولَهُ وَاللَّهُ خَبِيرٌ بِمَا تَعْمَلُونَ', 'ااشفقتم ان تقدموا بين يدي نجواكم صدقات فاذ لم تفعلوا وتاب الله عليكم فاقيموا الصلاة واتوا الزكاة واطيعوا الله ورسوله والله خبير بما تعملون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 14, 544, 2, 55, 28, 'أَلَمْ تَرَ إِلَى الَّذِينَ تَوَلَّوْا قَوْمًا غَضِبَ اللَّهُ عَلَيْهِمْ مَا هُمْ مِنْكُمْ وَلَا مِنْهُمْ وَيَحْلِفُونَ عَلَى الْكَذِبِ وَهُمْ يَعْلَمُونَ', 'الم تر الى الذين تولوا قوما غضب الله عليهم ما هم منكم ولا منهم ويحلفون على الكذب وهم يعلمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 15, 544, 2, 55, 28, 'أَعَدَّ اللَّهُ لَهُمْ عَذَابًا شَدِيدًا إِنَّهُمْ سَاءَ مَا كَانُوا يَعْمَلُونَ', 'اعد الله لهم عذابا شديدا انهم ساء ما كانوا يعملون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 16, 544, 2, 55, 28, 'اتَّخَذُوا أَيْمَانَهُمْ جُنَّةً فَصَدُّوا عَنْ سَبِيلِ اللَّهِ فَلَهُمْ عَذَابٌ مُهِينٌ', 'اتخذوا ايمانهم جنة فصدوا عن سبيل الله فلهم عذاب مهين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 17, 544, 2, 55, 28, 'لَنْ تُغْنِيَ عَنْهُمْ أَمْوَالُهُمْ وَلَا أَوْلَادُهُمْ مِنَ اللَّهِ شَيْئًا أُولَئِكَ أَصْحَابُ النَّارِ هُمْ فِيهَا خَالِدُونَ', 'لن تغني عنهم اموالهم ولا اولادهم من الله شيئا اولئك اصحاب النار هم فيها خالدون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 18, 544, 2, 55, 28, 'يَوْمَ يَبْعَثُهُمُ اللَّهُ جَمِيعًا فَيَحْلِفُونَ لَهُ كَمَا يَحْلِفُونَ لَكُمْ وَيَحْسَبُونَ أَنَّهُمْ عَلَى شَيْءٍ أَلَا إِنَّهُمْ هُمُ الْكَاذِبُونَ', 'يوم يبعثهم الله جميعا فيحلفون له كما يحلفون لكم ويحسبون انهم على شيء الا انهم هم الكاذبون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 19, 544, 2, 55, 28, 'اسْتَحْوَذَ عَلَيْهِمُ الشَّيْطَانُ فَأَنْسَاهُمْ ذِكْرَ اللَّهِ أُولَئِكَ حِزْبُ الشَّيْطَانِ أَلَا إِنَّ حِزْبَ الشَّيْطَانِ هُمُ الْخَاسِرُونَ', 'استحوذ عليهم الشيطان فانساهم ذكر الله اولئك حزب الشيطان الا ان حزب الشيطان هم الخاسرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 20, 544, 2, 55, 28, 'إِنَّ الَّذِينَ يُحَادُّونَ اللَّهَ وَرَسُولَهُ أُولَئِكَ فِي الْأَذَلِّينَ', 'ان الذين يحادون الله ورسوله اولئك في الاذلين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 21, 544, 2, 55, 28, 'كَتَبَ اللَّهُ لَأَغْلِبَنَّ أَنَا وَرُسُلِي إِنَّ اللَّهَ قَوِيٌّ عَزِيزٌ', 'كتب الله لاغلبن انا ورسلي ان الله قوي عزيز');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 58, 22, 545, 2, 55, 28, 'لَا تَجِدُ قَوْمًا يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ يُوَادُّونَ مَنْ حَادَّ اللَّهَ وَرَسُولَهُ وَلَوْ كَانُوا آبَاءَهُمْ أَوْ أَبْنَاءَهُمْ أَوْ إِخْوَانَهُمْ أَوْ عَشِيرَتَهُمْ أُولَئِكَ كَتَبَ فِي قُلُوبِهِمُ الْإِيمَانَ وَأَيَّدَهُمْ بِرُوحٍ مِنْهُ وَيُدْخِلُهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا رَضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ أُولَئِكَ حِزْبُ اللَّهِ أَلَا إِنَّ حِزْبَ اللَّهِ هُمُ الْمُفْلِحُونَ', 'لا تجد قوما يؤمنون بالله واليوم الاخر يوادون من حاد الله ورسوله ولو كانوا اباءهم او ابناءهم او اخوانهم او عشيرتهم اولئك كتب في قلوبهم الايمان وايدهم بروح منه ويدخلهم جنات تجري من تحتها الانهار خالدين فيها رضي الله عنهم ورضوا عنه اولئك حزب الله الا ان حزب الله هم المفلحون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 1, 545, 2, 55, 28, 'سَبَّحَ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ وَهُوَ الْعَزِيزُ الْحَكِيمُ', 'سبح لله ما في السماوات وما في الارض وهو العزيز الحكيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 2, 545, 2, 55, 28, 'هُوَ الَّذِي أَخْرَجَ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ مِنْ دِيَارِهِمْ لِأَوَّلِ الْحَشْرِ مَا ظَنَنْتُمْ أَنْ يَخْرُجُوا وَظَنُّوا أَنَّهُمْ مَانِعَتُهُمْ حُصُونُهُمْ مِنَ اللَّهِ فَأَتَاهُمُ اللَّهُ مِنْ حَيْثُ لَمْ يَحْتَسِبُوا وَقَذَفَ فِي قُلُوبِهِمُ الرُّعْبَ يُخْرِبُونَ بُيُوتَهُمْ بِأَيْدِيهِمْ وَأَيْدِي الْمُؤْمِنِينَ فَاعْتَبِرُوا يَا أُولِي الْأَبْصَارِ', 'هو الذي اخرج الذين كفروا من اهل الكتاب من ديارهم لاول الحشر ما ظننتم ان يخرجوا وظنوا انهم مانعتهم حصونهم من الله فاتاهم الله من حيث لم يحتسبوا وقذف في قلوبهم الرعب يخربون بيوتهم بايديهم وايدي المؤمنين فاعتبروا يا اولي الابصار');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 3, 545, 2, 55, 28, 'وَلَوْلَا أَنْ كَتَبَ اللَّهُ عَلَيْهِمُ الْجَلَاءَ لَعَذَّبَهُمْ فِي الدُّنْيَا وَلَهُمْ فِي الْآخِرَةِ عَذَابُ النَّارِ', 'ولولا ان كتب الله عليهم الجلاء لعذبهم في الدنيا ولهم في الاخرة عذاب النار');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 4, 546, 2, 55, 28, 'ذَلِكَ بِأَنَّهُمْ شَاقُّوا اللَّهَ وَرَسُولَهُ وَمَنْ يُشَاقِّ اللَّهَ فَإِنَّ اللَّهَ شَدِيدُ الْعِقَابِ', 'ذلك بانهم شاقوا الله ورسوله ومن يشاق الله فان الله شديد العقاب');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 5, 546, 2, 55, 28, 'مَا قَطَعْتُمْ مِنْ لِينَةٍ أَوْ تَرَكْتُمُوهَا قَائِمَةً عَلَى أُصُولِهَا فَبِإِذْنِ اللَّهِ وَلِيُخْزِيَ الْفَاسِقِينَ', 'ما قطعتم من لينة او تركتموها قائمة على اصولها فباذن الله وليخزي الفاسقين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 6, 546, 2, 55, 28, 'وَمَا أَفَاءَ اللَّهُ عَلَى رَسُولِهِ مِنْهُمْ فَمَا أَوْجَفْتُمْ عَلَيْهِ مِنْ خَيْلٍ وَلَا رِكَابٍ وَلَكِنَّ اللَّهَ يُسَلِّطُ رُسُلَهُ عَلَى مَنْ يَشَاءُ وَاللَّهُ عَلَى كُلِّ شَيْءٍ قَدِيرٌ', 'وما افاء الله على رسوله منهم فما اوجفتم عليه من خيل ولا ركاب ولكن الله يسلط رسله على من يشاء والله على كل شيء قدير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 7, 546, 2, 55, 28, 'مَا أَفَاءَ اللَّهُ عَلَى رَسُولِهِ مِنْ أَهْلِ الْقُرَى فَلِلَّهِ وَلِلرَّسُولِ وَلِذِي الْقُرْبَى وَالْيَتَامَى وَالْمَسَاكِينِ وَابْنِ السَّبِيلِ كَيْ لَا يَكُونَ دُولَةً بَيْنَ الْأَغْنِيَاءِ مِنْكُمْ وَمَا آتَاكُمُ الرَّسُولُ فَخُذُوهُ وَمَا نَهَاكُمْ عَنْهُ فَانْتَهُوا وَاتَّقُوا اللَّهَ إِنَّ اللَّهَ شَدِيدُ الْعِقَابِ', 'ما افاء الله على رسوله من اهل القرى فلله وللرسول ولذي القربى واليتامى والمساكين وابن السبيل كي لا يكون دولة بين الاغنياء منكم وما اتاكم الرسول فخذوه وما نهاكم عنه فانتهوا واتقوا الله ان الله شديد العقاب');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 8, 546, 2, 55, 28, 'لِلْفُقَرَاءِ الْمُهَاجِرِينَ الَّذِينَ أُخْرِجُوا مِنْ دِيَارِهِمْ وَأَمْوَالِهِمْ يَبْتَغُونَ فَضْلًا مِنَ اللَّهِ وَرِضْوَانًا وَيَنْصُرُونَ اللَّهَ وَرَسُولَهُ أُولَئِكَ هُمُ الصَّادِقُونَ', 'للفقراء المهاجرين الذين اخرجوا من ديارهم واموالهم يبتغون فضلا من الله ورضوانا وينصرون الله ورسوله اولئك هم الصادقون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 9, 546, 2, 55, 28, 'وَالَّذِينَ تَبَوَّءُوا الدَّارَ وَالْإِيمَانَ مِنْ قَبْلِهِمْ يُحِبُّونَ مَنْ هَاجَرَ إِلَيْهِمْ وَلَا يَجِدُونَ فِي صُدُورِهِمْ حَاجَةً مِمَّا أُوتُوا وَيُؤْثِرُونَ عَلَى أَنْفُسِهِمْ وَلَوْ كَانَ بِهِمْ خَصَاصَةٌ وَمَنْ يُوقَ شُحَّ نَفْسِهِ فَأُولَئِكَ هُمُ الْمُفْلِحُونَ', 'والذين تبوءوا الدار والايمان من قبلهم يحبون من هاجر اليهم ولا يجدون في صدورهم حاجة مما اوتوا ويؤثرون على انفسهم ولو كان بهم خصاصة ومن يوق شح نفسه فاولئك هم المفلحون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 10, 547, 2, 55, 28, 'وَالَّذِينَ جَاءُوا مِنْ بَعْدِهِمْ يَقُولُونَ رَبَّنَا اغْفِرْ لَنَا وَلِإِخْوَانِنَا الَّذِينَ سَبَقُونَا بِالْإِيمَانِ وَلَا تَجْعَلْ فِي قُلُوبِنَا غِلًّا لِلَّذِينَ آمَنُوا رَبَّنَا إِنَّكَ رَءُوفٌ رَحِيمٌ', 'والذين جاءوا من بعدهم يقولون ربنا اغفر لنا ولاخواننا الذين سبقونا بالايمان ولا تجعل في قلوبنا غلا للذين امنوا ربنا انك رءوف رحيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 11, 547, 3, 55, 28, 'أَلَمْ تَرَ إِلَى الَّذِينَ نَافَقُوا يَقُولُونَ لِإِخْوَانِهِمُ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ لَئِنْ أُخْرِجْتُمْ لَنَخْرُجَنَّ مَعَكُمْ وَلَا نُطِيعُ فِيكُمْ أَحَدًا أَبَدًا وَإِنْ قُوتِلْتُمْ لَنَنْصُرَنَّكُمْ وَاللَّهُ يَشْهَدُ إِنَّهُمْ لَكَاذِبُونَ', 'الم تر الى الذين نافقوا يقولون لاخوانهم الذين كفروا من اهل الكتاب لئن اخرجتم لنخرجن معكم ولا نطيع فيكم احدا ابدا وان قوتلتم لننصرنكم والله يشهد انهم لكاذبون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 12, 547, 3, 55, 28, 'لَئِنْ أُخْرِجُوا لَا يَخْرُجُونَ مَعَهُمْ وَلَئِنْ قُوتِلُوا لَا يَنْصُرُونَهُمْ وَلَئِنْ نَصَرُوهُمْ لَيُوَلُّنَّ الْأَدْبَارَ ثُمَّ لَا يُنْصَرُونَ', 'لئن اخرجوا لا يخرجون معهم ولئن قوتلوا لا ينصرونهم ولئن نصروهم ليولن الادبار ثم لا ينصرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 13, 547, 3, 55, 28, 'لَأَنْتُمْ أَشَدُّ رَهْبَةً فِي صُدُورِهِمْ مِنَ اللَّهِ ذَلِكَ بِأَنَّهُمْ قَوْمٌ لَا يَفْقَهُونَ', 'لانتم اشد رهبة في صدورهم من الله ذلك بانهم قوم لا يفقهون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 14, 547, 3, 55, 28, 'لَا يُقَاتِلُونَكُمْ جَمِيعًا إِلَّا فِي قُرًى مُحَصَّنَةٍ أَوْ مِنْ وَرَاءِ جُدُرٍ بَأْسُهُمْ بَيْنَهُمْ شَدِيدٌ تَحْسَبُهُمْ جَمِيعًا وَقُلُوبُهُمْ شَتَّى ذَلِكَ بِأَنَّهُمْ قَوْمٌ لَا يَعْقِلُونَ', 'لا يقاتلونكم جميعا الا في قرى محصنة او من وراء جدر باسهم بينهم شديد تحسبهم جميعا وقلوبهم شتى ذلك بانهم قوم لا يعقلون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 15, 547, 3, 55, 28, 'كَمَثَلِ الَّذِينَ مِنْ قَبْلِهِمْ قَرِيبًا ذَاقُوا وَبَالَ أَمْرِهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ', 'كمثل الذين من قبلهم قريبا ذاقوا وبال امرهم ولهم عذاب اليم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 16, 547, 3, 55, 28, 'كَمَثَلِ الشَّيْطَانِ إِذْ قَالَ لِلْإِنْسَانِ اكْفُرْ فَلَمَّا كَفَرَ قَالَ إِنِّي بَرِيءٌ مِنْكَ إِنِّي أَخَافُ اللَّهَ رَبَّ الْعَالَمِينَ', 'كمثل الشيطان اذ قال للانسان اكفر فلما كفر قال اني بريء منك اني اخاف الله رب العالمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 17, 548, 3, 55, 28, 'فَكَانَ عَاقِبَتَهُمَا أَنَّهُمَا فِي النَّارِ خَالِدَيْنِ فِيهَا وَذَلِكَ جَزَاءُ الظَّالِمِينَ', 'فكان عاقبتهما انهما في النار خالدين فيها وذلك جزاء الظالمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 18, 548, 3, 55, 28, 'يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَلْتَنْظُرْ نَفْسٌ مَا قَدَّمَتْ لِغَدٍ وَاتَّقُوا اللَّهَ إِنَّ اللَّهَ خَبِيرٌ بِمَا تَعْمَلُونَ', 'يا ايها الذين امنوا اتقوا الله ولتنظر نفس ما قدمت لغد واتقوا الله ان الله خبير بما تعملون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 19, 548, 3, 55, 28, 'وَلَا تَكُونُوا كَالَّذِينَ نَسُوا اللَّهَ فَأَنْسَاهُمْ أَنْفُسَهُمْ أُولَئِكَ هُمُ الْفَاسِقُونَ', 'ولا تكونوا كالذين نسوا الله فانساهم انفسهم اولئك هم الفاسقون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 20, 548, 3, 55, 28, 'لَا يَسْتَوِي أَصْحَابُ النَّارِ وَأَصْحَابُ الْجَنَّةِ أَصْحَابُ الْجَنَّةِ هُمُ الْفَائِزُونَ', 'لا يستوي اصحاب النار واصحاب الجنة اصحاب الجنة هم الفائزون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 21, 548, 3, 55, 28, 'لَوْ أَنْزَلْنَا هَذَا الْقُرْآنَ عَلَى جَبَلٍ لَرَأَيْتَهُ خَاشِعًا مُتَصَدِّعًا مِنْ خَشْيَةِ اللَّهِ وَتِلْكَ الْأَمْثَالُ نَضْرِبُهَا لِلنَّاسِ لَعَلَّهُمْ يَتَفَكَّرُونَ', 'لو انزلنا هذا القران على جبل لرايته خاشعا متصدعا من خشية الله وتلك الامثال نضربها للناس لعلهم يتفكرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 22, 548, 3, 55, 28, 'هُوَ اللَّهُ الَّذِي لَا إِلَهَ إِلَّا هُوَ عَالِمُ الْغَيْبِ وَالشَّهَادَةِ هُوَ الرَّحْمَنُ الرَّحِيمُ', 'هو الله الذي لا اله الا هو عالم الغيب والشهادة هو الرحمن الرحيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 23, 548, 3, 55, 28, 'هُوَ اللَّهُ الَّذِي لَا إِلَهَ إِلَّا هُوَ الْمَلِكُ الْقُدُّوسُ السَّلَامُ الْمُؤْمِنُ الْمُهَيْمِنُ الْعَزِيزُ الْجَبَّارُ الْمُتَكَبِّرُ سُبْحَانَ اللَّهِ عَمَّا يُشْرِكُونَ', 'هو الله الذي لا اله الا هو الملك القدوس السلام المؤمن المهيمن العزيز الجبار المتكبر سبحان الله عما يشركون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 59, 24, 548, 3, 55, 28, 'هُوَ اللَّهُ الْخَالِقُ الْبَارِئُ الْمُصَوِّرُ لَهُ الْأَسْمَاءُ الْحُسْنَى يُسَبِّحُ لَهُ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ وَهُوَ الْعَزِيزُ الْحَكِيمُ', 'هو الله الخالق البارئ المصور له الاسماء الحسنى يسبح له ما في السماوات والارض وهو العزيز الحكيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 60, 1, 549, 3, 55, 28, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَّخِذُوا عَدُوِّي وَعَدُوَّكُمْ أَوْلِيَاءَ تُلْقُونَ إِلَيْهِمْ بِالْمَوَدَّةِ وَقَدْ كَفَرُوا بِمَا جَاءَكُمْ مِنَ الْحَقِّ يُخْرِجُونَ الرَّسُولَ وَإِيَّاكُمْ أَنْ تُؤْمِنُوا بِاللَّهِ رَبِّكُمْ إِنْ كُنْتُمْ خَرَجْتُمْ جِهَادًا فِي سَبِيلِي وَابْتِغَاءَ مَرْضَاتِي تُسِرُّونَ إِلَيْهِمْ بِالْمَوَدَّةِ وَأَنَا أَعْلَمُ بِمَا أَخْفَيْتُمْ وَمَا أَعْلَنْتُمْ وَمَنْ يَفْعَلْهُ مِنْكُمْ فَقَدْ ضَلَّ سَوَاءَ السَّبِيلِ', 'يا ايها الذين امنوا لا تتخذوا عدوي وعدوكم اولياء تلقون اليهم بالمودة وقد كفروا بما جاءكم من الحق يخرجون الرسول واياكم ان تؤمنوا بالله ربكم ان كنتم خرجتم جهادا في سبيلي وابتغاء مرضاتي تسرون اليهم بالمودة وانا اعلم بما اخفيتم وما اعلنتم ومن يفعله منكم فقد ضل سواء السبيل');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 60, 2, 549, 3, 55, 28, 'إِنْ يَثْقَفُوكُمْ يَكُونُوا لَكُمْ أَعْدَاءً وَيَبْسُطُوا إِلَيْكُمْ أَيْدِيَهُمْ وَأَلْسِنَتَهُمْ بِالسُّوءِ وَوَدُّوا لَوْ تَكْفُرُونَ', 'ان يثقفوكم يكونوا لكم اعداء ويبسطوا اليكم ايديهم والسنتهم بالسوء وودوا لو تكفرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 60, 3, 549, 3, 55, 28, 'لَنْ تَنْفَعَكُمْ أَرْحَامُكُمْ وَلَا أَوْلَادُكُمْ يَوْمَ الْقِيَامَةِ يَفْصِلُ بَيْنَكُمْ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ', 'لن تنفعكم ارحامكم ولا اولادكم يوم القيامة يفصل بينكم والله بما تعملون بصير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 60, 4, 549, 3, 55, 28, 'قَدْ كَانَتْ لَكُمْ أُسْوَةٌ حَسَنَةٌ فِي إِبْرَاهِيمَ وَالَّذِينَ مَعَهُ إِذْ قَالُوا لِقَوْمِهِمْ إِنَّا بُرَآءُ مِنْكُمْ وَمِمَّا تَعْبُدُونَ مِنْ دُونِ اللَّهِ كَفَرْنَا بِكُمْ وَبَدَا بَيْنَنَا وَبَيْنَكُمُ الْعَدَاوَةُ وَالْبَغْضَاءُ أَبَدًا حَتَّى تُؤْمِنُوا بِاللَّهِ وَحْدَهُ إِلَّا قَوْلَ إِبْرَاهِيمَ لِأَبِيهِ لَأَسْتَغْفِرَنَّ لَكَ وَمَا أَمْلِكُ لَكَ مِنَ اللَّهِ مِنْ شَيْءٍ رَبَّنَا عَلَيْكَ تَوَكَّلْنَا وَإِلَيْكَ أَنَبْنَا وَإِلَيْكَ الْمَصِيرُ', 'قد كانت لكم اسوة حسنة في ابراهيم والذين معه اذ قالوا لقومهم انا براء منكم ومما تعبدون من دون الله كفرنا بكم وبدا بيننا وبينكم العداوة والبغضاء ابدا حتى تؤمنوا بالله وحده الا قول ابراهيم لابيه لاستغفرن لك وما املك لك من الله من شيء ربنا عليك توكلنا واليك انبنا واليك المصير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 60, 5, 549, 3, 55, 28, 'رَبَّنَا لَا تَجْعَلْنَا فِتْنَةً لِلَّذِينَ كَفَرُوا وَاغْفِرْ لَنَا رَبَّنَا إِنَّكَ أَنْتَ الْعَزِيزُ الْحَكِيمُ', 'ربنا لا تجعلنا فتنة للذين كفروا واغفر لنا ربنا انك انت العزيز الحكيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 60, 6, 550, 3, 55, 28, 'لَقَدْ كَانَ لَكُمْ فِيهِمْ أُسْوَةٌ حَسَنَةٌ لِمَنْ كَانَ يَرْجُو اللَّهَ وَالْيَوْمَ الْآخِرَ وَمَنْ يَتَوَلَّ فَإِنَّ اللَّهَ هُوَ الْغَنِيُّ الْحَمِيدُ', 'لقد كان لكم فيهم اسوة حسنة لمن كان يرجو الله واليوم الاخر ومن يتول فان الله هو الغني الحميد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 60, 7, 550, 4, 55, 28, 'عَسَى اللَّهُ أَنْ يَجْعَلَ بَيْنَكُمْ وَبَيْنَ الَّذِينَ عَادَيْتُمْ مِنْهُمْ مَوَدَّةً وَاللَّهُ قَدِيرٌ وَاللَّهُ غَفُورٌ رَحِيمٌ', 'عسى الله ان يجعل بينكم وبين الذين عاديتم منهم مودة والله قدير والله غفور رحيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 60, 8, 550, 4, 55, 28, 'لَا يَنْهَاكُمُ اللَّهُ عَنِ الَّذِينَ لَمْ يُقَاتِلُوكُمْ فِي الدِّينِ وَلَمْ يُخْرِجُوكُمْ مِنْ دِيَارِكُمْ أَنْ تَبَرُّوهُمْ وَتُقْسِطُوا إِلَيْهِمْ إِنَّ اللَّهَ يُحِبُّ الْمُقْسِطِينَ', 'لا ينهاكم الله عن الذين لم يقاتلوكم في الدين ولم يخرجوكم من دياركم ان تبروهم وتقسطوا اليهم ان الله يحب المقسطين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 60, 9, 550, 4, 55, 28, 'إِنَّمَا يَنْهَاكُمُ اللَّهُ عَنِ الَّذِينَ قَاتَلُوكُمْ فِي الدِّينِ وَأَخْرَجُوكُمْ مِنْ دِيَارِكُمْ وَظَاهَرُوا عَلَى إِخْرَاجِكُمْ أَنْ تَوَلَّوْهُمْ وَمَنْ يَتَوَلَّهُمْ فَأُولَئِكَ هُمُ الظَّالِمُونَ', 'انما ينهاكم الله عن الذين قاتلوكم في الدين واخرجوكم من دياركم وظاهروا على اخراجكم ان تولوهم ومن يتولهم فاولئك هم الظالمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 60, 10, 550, 4, 55, 28, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا جَاءَكُمُ الْمُؤْمِنَاتُ مُهَاجِرَاتٍ فَامْتَحِنُوهُنَّ اللَّهُ أَعْلَمُ بِإِيمَانِهِنَّ فَإِنْ عَلِمْتُمُوهُنَّ مُؤْمِنَاتٍ فَلَا تَرْجِعُوهُنَّ إِلَى الْكُفَّارِ لَا هُنَّ حِلٌّ لَهُمْ وَلَا هُمْ يَحِلُّونَ لَهُنَّ وَآتُوهُمْ مَا أَنْفَقُوا وَلَا جُنَاحَ عَلَيْكُمْ أَنْ تَنْكِحُوهُنَّ إِذَا آتَيْتُمُوهُنَّ أُجُورَهُنَّ وَلَا تُمْسِكُوا بِعِصَمِ الْكَوَافِرِ وَاسْأَلُوا مَا أَنْفَقْتُمْ وَلْيَسْأَلُوا مَا أَنْفَقُوا ذَلِكُمْ حُكْمُ اللَّهِ يَحْكُمُ بَيْنَكُمْ وَاللَّهُ عَلِيمٌ حَكِيمٌ', 'يا ايها الذين امنوا اذا جاءكم المؤمنات مهاجرات فامتحنوهن الله اعلم بايمانهن فان علمتموهن مؤمنات فلا ترجعوهن الى الكفار لا هن حل لهم ولا هم يحلون لهن واتوهم ما انفقوا ولا جناح عليكم ان تنكحوهن اذا اتيتموهن اجورهن ولا تمسكوا بعصم الكوافر واسالوا ما انفقتم وليسالوا ما انفقوا ذلكم حكم الله يحكم بينكم والله عليم حكيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 60, 11, 550, 4, 55, 28, 'وَإِنْ فَاتَكُمْ شَيْءٌ مِنْ أَزْوَاجِكُمْ إِلَى الْكُفَّارِ فَعَاقَبْتُمْ فَآتُوا الَّذِينَ ذَهَبَتْ أَزْوَاجُهُمْ مِثْلَ مَا أَنْفَقُوا وَاتَّقُوا اللَّهَ الَّذِي أَنْتُمْ بِهِ مُؤْمِنُونَ', 'وان فاتكم شيء من ازواجكم الى الكفار فعاقبتم فاتوا الذين ذهبت ازواجهم مثل ما انفقوا واتقوا الله الذي انتم به مؤمنون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 60, 12, 551, 4, 55, 28, 'يَا أَيُّهَا النَّبِيُّ إِذَا جَاءَكَ الْمُؤْمِنَاتُ يُبَايِعْنَكَ عَلَى أَنْ لَا يُشْرِكْنَ بِاللَّهِ شَيْئًا وَلَا يَسْرِقْنَ وَلَا يَزْنِينَ وَلَا يَقْتُلْنَ أَوْلَادَهُنَّ وَلَا يَأْتِينَ بِبُهْتَانٍ يَفْتَرِينَهُ بَيْنَ أَيْدِيهِنَّ وَأَرْجُلِهِنَّ وَلَا يَعْصِينَكَ فِي مَعْرُوفٍ فَبَايِعْهُنَّ وَاسْتَغْفِرْ لَهُنَّ اللَّهَ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 'يا ايها النبي اذا جاءك المؤمنات يبايعنك على ان لا يشركن بالله شيئا ولا يسرقن ولا يزنين ولا يقتلن اولادهن ولا ياتين ببهتان يفترينه بين ايديهن وارجلهن ولا يعصينك في معروف فبايعهن واستغفر لهن الله ان الله غفور رحيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 60, 13, 551, 4, 55, 28, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَوَلَّوْا قَوْمًا غَضِبَ اللَّهُ عَلَيْهِمْ قَدْ يَئِسُوا مِنَ الْآخِرَةِ كَمَا يَئِسَ الْكُفَّارُ مِنْ أَصْحَابِ الْقُبُورِ', 'يا ايها الذين امنوا لا تتولوا قوما غضب الله عليهم قد يئسوا من الاخرة كما يئس الكفار من اصحاب القبور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 61, 1, 551, 4, 55, 28, 'سَبَّحَ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ وَهُوَ الْعَزِيزُ الْحَكِيمُ', 'سبح لله ما في السماوات وما في الارض وهو العزيز الحكيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 61, 2, 551, 4, 55, 28, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لِمَ تَقُولُونَ مَا لَا تَفْعَلُونَ', 'يا ايها الذين امنوا لم تقولون ما لا تفعلون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 61, 3, 551, 4, 55, 28, 'كَبُرَ مَقْتًا عِنْدَ اللَّهِ أَنْ تَقُولُوا مَا لَا تَفْعَلُونَ', 'كبر مقتا عند الله ان تقولوا ما لا تفعلون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 61, 4, 551, 4, 55, 28, 'إِنَّ اللَّهَ يُحِبُّ الَّذِينَ يُقَاتِلُونَ فِي سَبِيلِهِ صَفًّا كَأَنَّهُمْ بُنْيَانٌ مَرْصُوصٌ', 'ان الله يحب الذين يقاتلون في سبيله صفا كانهم بنيان مرصوص');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 61, 5, 551, 4, 55, 28, 'وَإِذْ قَالَ مُوسَى لِقَوْمِهِ يَا قَوْمِ لِمَ تُؤْذُونَنِي وَقَدْ تَعْلَمُونَ أَنِّي رَسُولُ اللَّهِ إِلَيْكُمْ فَلَمَّا زَاغُوا أَزَاغَ اللَّهُ قُلُوبَهُمْ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الْفَاسِقِينَ', 'واذ قال موسى لقومه يا قوم لم تؤذونني وقد تعلمون اني رسول الله اليكم فلما زاغوا ازاغ الله قلوبهم والله لا يهدي القوم الفاسقين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 61, 6, 552, 4, 55, 28, 'وَإِذْ قَالَ عِيسَى ابْنُ مَرْيَمَ يَا بَنِي إِسْرَائِيلَ إِنِّي رَسُولُ اللَّهِ إِلَيْكُمْ مُصَدِّقًا لِمَا بَيْنَ يَدَيَّ مِنَ التَّوْرَاةِ وَمُبَشِّرًا بِرَسُولٍ يَأْتِي مِنْ بَعْدِي اسْمُهُ أَحْمَدُ فَلَمَّا جَاءَهُمْ بِالْبَيِّنَاتِ قَالُوا هَذَا سِحْرٌ مُبِينٌ', 'واذ قال عيسى ابن مريم يا بني اسرائيل اني رسول الله اليكم مصدقا لما بين يدي من التوراة ومبشرا برسول ياتي من بعدي اسمه احمد فلما جاءهم بالبينات قالوا هذا سحر مبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 61, 7, 552, 4, 55, 28, 'وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَى عَلَى اللَّهِ الْكَذِبَ وَهُوَ يُدْعَى إِلَى الْإِسْلَامِ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ', 'ومن اظلم ممن افترى على الله الكذب وهو يدعى الى الاسلام والله لا يهدي القوم الظالمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 61, 8, 552, 4, 55, 28, 'يُرِيدُونَ لِيُطْفِئُوا نُورَ اللَّهِ بِأَفْوَاهِهِمْ وَاللَّهُ مُتِمُّ نُورِهِ وَلَوْ كَرِهَ الْكَافِرُونَ', 'يريدون ليطفئوا نور الله بافواههم والله متم نوره ولو كره الكافرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 61, 9, 552, 4, 55, 28, 'هُوَ الَّذِي أَرْسَلَ رَسُولَهُ بِالْهُدَى وَدِينِ الْحَقِّ لِيُظْهِرَهُ عَلَى الدِّينِ كُلِّهِ وَلَوْ كَرِهَ الْمُشْرِكُونَ', 'هو الذي ارسل رسوله بالهدى ودين الحق ليظهره على الدين كله ولو كره المشركون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 61, 10, 552, 4, 55, 28, 'يَا أَيُّهَا الَّذِينَ آمَنُوا هَلْ أَدُلُّكُمْ عَلَى تِجَارَةٍ تُنْجِيكُمْ مِنْ عَذَابٍ أَلِيمٍ', 'يا ايها الذين امنوا هل ادلكم على تجارة تنجيكم من عذاب اليم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 61, 11, 552, 4, 55, 28, 'تُؤْمِنُونَ بِاللَّهِ وَرَسُولِهِ وَتُجَاهِدُونَ فِي سَبِيلِ اللَّهِ بِأَمْوَالِكُمْ وَأَنْفُسِكُمْ ذَلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ', 'تؤمنون بالله ورسوله وتجاهدون في سبيل الله باموالكم وانفسكم ذلكم خير لكم ان كنتم تعلمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 61, 12, 552, 4, 55, 28, 'يَغْفِرْ لَكُمْ ذُنُوبَكُمْ وَيُدْخِلْكُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ وَمَسَاكِنَ طَيِّبَةً فِي جَنَّاتِ عَدْنٍ ذَلِكَ الْفَوْزُ الْعَظِيمُ', 'يغفر لكم ذنوبكم ويدخلكم جنات تجري من تحتها الانهار ومساكن طيبة في جنات عدن ذلك الفوز العظيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 61, 13, 552, 4, 55, 28, 'وَأُخْرَى تُحِبُّونَهَا نَصْرٌ مِنَ اللَّهِ وَفَتْحٌ قَرِيبٌ وَبَشِّرِ الْمُؤْمِنِينَ', 'واخرى تحبونها نصر من الله وفتح قريب وبشر المؤمنين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 61, 14, 552, 4, 55, 28, 'يَا أَيُّهَا الَّذِينَ آمَنُوا كُونُوا أَنْصَارَ اللَّهِ كَمَا قَالَ عِيسَى ابْنُ مَرْيَمَ لِلْحَوَارِيِّينَ مَنْ أَنْصَارِي إِلَى اللَّهِ قَالَ الْحَوَارِيُّونَ نَحْنُ أَنْصَارُ اللَّهِ فَآمَنَتْ طَائِفَةٌ مِنْ بَنِي إِسْرَائِيلَ وَكَفَرَتْ طَائِفَةٌ فَأَيَّدْنَا الَّذِينَ آمَنُوا عَلَى عَدُوِّهِمْ فَأَصْبَحُوا ظَاهِرِينَ', 'يا ايها الذين امنوا كونوا انصار الله كما قال عيسى ابن مريم للحواريين من انصاري الى الله قال الحواريون نحن انصار الله فامنت طائفة من بني اسرائيل وكفرت طائفة فايدنا الذين امنوا على عدوهم فاصبحوا ظاهرين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 62, 1, 553, 1, 56, 28, 'يُسَبِّحُ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ الْمَلِكِ الْقُدُّوسِ الْعَزِيزِ الْحَكِيمِ', 'يسبح لله ما في السماوات وما في الارض الملك القدوس العزيز الحكيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 62, 2, 553, 1, 56, 28, 'هُوَ الَّذِي بَعَثَ فِي الْأُمِّيِّينَ رَسُولًا مِنْهُمْ يَتْلُو عَلَيْهِمْ آيَاتِهِ وَيُزَكِّيهِمْ وَيُعَلِّمُهُمُ الْكِتَابَ وَالْحِكْمَةَ وَإِنْ كَانُوا مِنْ قَبْلُ لَفِي ضَلَالٍ مُبِينٍ', 'هو الذي بعث في الاميين رسولا منهم يتلو عليهم اياته ويزكيهم ويعلمهم الكتاب والحكمة وان كانوا من قبل لفي ضلال مبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 62, 3, 553, 1, 56, 28, 'وَآخَرِينَ مِنْهُمْ لَمَّا يَلْحَقُوا بِهِمْ وَهُوَ الْعَزِيزُ الْحَكِيمُ', 'واخرين منهم لما يلحقوا بهم وهو العزيز الحكيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 62, 4, 553, 1, 56, 28, 'ذَلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ', 'ذلك فضل الله يؤتيه من يشاء والله ذو الفضل العظيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 62, 5, 553, 1, 56, 28, 'مَثَلُ الَّذِينَ حُمِّلُوا التَّوْرَاةَ ثُمَّ لَمْ يَحْمِلُوهَا كَمَثَلِ الْحِمَارِ يَحْمِلُ أَسْفَارًا بِئْسَ مَثَلُ الْقَوْمِ الَّذِينَ كَذَّبُوا بِآيَاتِ اللَّهِ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ', 'مثل الذين حملوا التوراة ثم لم يحملوها كمثل الحمار يحمل اسفارا بئس مثل القوم الذين كذبوا بايات الله والله لا يهدي القوم الظالمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 62, 6, 553, 1, 56, 28, 'قُلْ يَا أَيُّهَا الَّذِينَ هَادُوا إِنْ زَعَمْتُمْ أَنَّكُمْ أَوْلِيَاءُ لِلَّهِ مِنْ دُونِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ إِنْ كُنْتُمْ صَادِقِينَ', 'قل يا ايها الذين هادوا ان زعمتم انكم اولياء لله من دون الناس فتمنوا الموت ان كنتم صادقين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 62, 7, 553, 1, 56, 28, 'وَلَا يَتَمَنَّوْنَهُ أَبَدًا بِمَا قَدَّمَتْ أَيْدِيهِمْ وَاللَّهُ عَلِيمٌ بِالظَّالِمِينَ', 'ولا يتمنونه ابدا بما قدمت ايديهم والله عليم بالظالمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 62, 8, 553, 1, 56, 28, 'قُلْ إِنَّ الْمَوْتَ الَّذِي تَفِرُّونَ مِنْهُ فَإِنَّهُ مُلَاقِيكُمْ ثُمَّ تُرَدُّونَ إِلَى عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ', 'قل ان الموت الذي تفرون منه فانه ملاقيكم ثم تردون الى عالم الغيب والشهادة فينبئكم بما كنتم تعملون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 62, 9, 554, 1, 56, 28, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا نُودِيَ لِلصَّلَاةِ مِنْ يَوْمِ الْجُمُعَةِ فَاسْعَوْا إِلَى ذِكْرِ اللَّهِ وَذَرُوا الْبَيْعَ ذَلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ', 'يا ايها الذين امنوا اذا نودي للصلاة من يوم الجمعة فاسعوا الى ذكر الله وذروا البيع ذلكم خير لكم ان كنتم تعلمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 62, 10, 554, 1, 56, 28, 'فَإِذَا قُضِيَتِ الصَّلَاةُ فَانْتَشِرُوا فِي الْأَرْضِ وَابْتَغُوا مِنْ فَضْلِ اللَّهِ وَاذْكُرُوا اللَّهَ كَثِيرًا لَعَلَّكُمْ تُفْلِحُونَ', 'فاذا قضيت الصلاة فانتشروا في الارض وابتغوا من فضل الله واذكروا الله كثيرا لعلكم تفلحون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 62, 11, 554, 1, 56, 28, 'وَإِذَا رَأَوْا تِجَارَةً أَوْ لَهْوًا انْفَضُّوا إِلَيْهَا وَتَرَكُوكَ قَائِمًا قُلْ مَا عِنْدَ اللَّهِ خَيْرٌ مِنَ اللَّهْوِ وَمِنَ التِّجَارَةِ وَاللَّهُ خَيْرُ الرَّازِقِينَ', 'واذا راوا تجارة او لهوا انفضوا اليها وتركوك قائما قل ما عند الله خير من اللهو ومن التجارة والله خير الرازقين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 63, 1, 554, 1, 56, 28, 'إِذَا جَاءَكَ الْمُنَافِقُونَ قَالُوا نَشْهَدُ إِنَّكَ لَرَسُولُ اللَّهِ وَاللَّهُ يَعْلَمُ إِنَّكَ لَرَسُولُهُ وَاللَّهُ يَشْهَدُ إِنَّ الْمُنَافِقِينَ لَكَاذِبُونَ', 'اذا جاءك المنافقون قالوا نشهد انك لرسول الله والله يعلم انك لرسوله والله يشهد ان المنافقين لكاذبون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 63, 2, 554, 1, 56, 28, 'اتَّخَذُوا أَيْمَانَهُمْ جُنَّةً فَصَدُّوا عَنْ سَبِيلِ اللَّهِ إِنَّهُمْ سَاءَ مَا كَانُوا يَعْمَلُونَ', 'اتخذوا ايمانهم جنة فصدوا عن سبيل الله انهم ساء ما كانوا يعملون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 63, 3, 554, 1, 56, 28, 'ذَلِكَ بِأَنَّهُمْ آمَنُوا ثُمَّ كَفَرُوا فَطُبِعَ عَلَى قُلُوبِهِمْ فَهُمْ لَا يَفْقَهُونَ', 'ذلك بانهم امنوا ثم كفروا فطبع على قلوبهم فهم لا يفقهون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 63, 4, 554, 2, 56, 28, 'وَإِذَا رَأَيْتَهُمْ تُعْجِبُكَ أَجْسَامُهُمْ وَإِنْ يَقُولُوا تَسْمَعْ لِقَوْلِهِمْ كَأَنَّهُمْ خُشُبٌ مُسَنَّدَةٌ يَحْسَبُونَ كُلَّ صَيْحَةٍ عَلَيْهِمْ هُمُ الْعَدُوُّ فَاحْذَرْهُمْ قَاتَلَهُمُ اللَّهُ أَنَّى يُؤْفَكُونَ', 'واذا رايتهم تعجبك اجسامهم وان يقولوا تسمع لقولهم كانهم خشب مسندة يحسبون كل صيحة عليهم هم العدو فاحذرهم قاتلهم الله انى يؤفكون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 63, 5, 555, 2, 56, 28, 'وَإِذَا قِيلَ لَهُمْ تَعَالَوْا يَسْتَغْفِرْ لَكُمْ رَسُولُ اللَّهِ لَوَّوْا رُءُوسَهُمْ وَرَأَيْتَهُمْ يَصُدُّونَ وَهُمْ مُسْتَكْبِرُونَ', 'واذا قيل لهم تعالوا يستغفر لكم رسول الله لووا رءوسهم ورايتهم يصدون وهم مستكبرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 63, 6, 555, 2, 56, 28, 'سَوَاءٌ عَلَيْهِمْ أَسْتَغْفَرْتَ لَهُمْ أَمْ لَمْ تَسْتَغْفِرْ لَهُمْ لَنْ يَغْفِرَ اللَّهُ لَهُمْ إِنَّ اللَّهَ لَا يَهْدِي الْقَوْمَ الْفَاسِقِينَ', 'سواء عليهم استغفرت لهم ام لم تستغفر لهم لن يغفر الله لهم ان الله لا يهدي القوم الفاسقين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 63, 7, 555, 2, 56, 28, 'هُمُ الَّذِينَ يَقُولُونَ لَا تُنْفِقُوا عَلَى مَنْ عِنْدَ رَسُولِ اللَّهِ حَتَّى يَنْفَضُّوا وَلِلَّهِ خَزَائِنُ السَّمَاوَاتِ وَالْأَرْضِ وَلَكِنَّ الْمُنَافِقِينَ لَا يَفْقَهُونَ', 'هم الذين يقولون لا تنفقوا على من عند رسول الله حتى ينفضوا ولله خزائن السماوات والارض ولكن المنافقين لا يفقهون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 63, 8, 555, 2, 56, 28, 'يَقُولُونَ لَئِنْ رَجَعْنَا إِلَى الْمَدِينَةِ لَيُخْرِجَنَّ الْأَعَزُّ مِنْهَا الْأَذَلَّ وَلِلَّهِ الْعِزَّةُ وَلِرَسُولِهِ وَلِلْمُؤْمِنِينَ وَلَكِنَّ الْمُنَافِقِينَ لَا يَعْلَمُونَ', 'يقولون لئن رجعنا الى المدينة ليخرجن الاعز منها الاذل ولله العزة ولرسوله وللمؤمنين ولكن المنافقين لا يعلمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 63, 9, 555, 2, 56, 28, 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تُلْهِكُمْ أَمْوَالُكُمْ وَلَا أَوْلَادُكُمْ عَنْ ذِكْرِ اللَّهِ وَمَنْ يَفْعَلْ ذَلِكَ فَأُولَئِكَ هُمُ الْخَاسِرُونَ', 'يا ايها الذين امنوا لا تلهكم اموالكم ولا اولادكم عن ذكر الله ومن يفعل ذلك فاولئك هم الخاسرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 63, 10, 555, 2, 56, 28, 'وَأَنْفِقُوا مِنْ مَا رَزَقْنَاكُمْ مِنْ قَبْلِ أَنْ يَأْتِيَ أَحَدَكُمُ الْمَوْتُ فَيَقُولَ رَبِّ لَوْلَا أَخَّرْتَنِي إِلَى أَجَلٍ قَرِيبٍ فَأَصَّدَّقَ وَأَكُنْ مِنَ الصَّالِحِينَ', 'وانفقوا من ما رزقناكم من قبل ان ياتي احدكم الموت فيقول رب لولا اخرتني الى اجل قريب فاصدق واكن من الصالحين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 63, 11, 555, 2, 56, 28, 'وَلَنْ يُؤَخِّرَ اللَّهُ نَفْسًا إِذَا جَاءَ أَجَلُهَا وَاللَّهُ خَبِيرٌ بِمَا تَعْمَلُونَ', 'ولن يؤخر الله نفسا اذا جاء اجلها والله خبير بما تعملون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 64, 1, 556, 2, 56, 28, 'يُسَبِّحُ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ لَهُ الْمُلْكُ وَلَهُ الْحَمْدُ وَهُوَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ', 'يسبح لله ما في السماوات وما في الارض له الملك وله الحمد وهو على كل شيء قدير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 64, 2, 556, 2, 56, 28, 'هُوَ الَّذِي خَلَقَكُمْ فَمِنْكُمْ كَافِرٌ وَمِنْكُمْ مُؤْمِنٌ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ', 'هو الذي خلقكم فمنكم كافر ومنكم مؤمن والله بما تعملون بصير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 64, 3, 556, 2, 56, 28, 'خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ بِالْحَقِّ وَصَوَّرَكُمْ فَأَحْسَنَ صُوَرَكُمْ وَإِلَيْهِ الْمَصِيرُ', 'خلق السماوات والارض بالحق وصوركم فاحسن صوركم واليه المصير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 64, 4, 556, 2, 56, 28, 'يَعْلَمُ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ وَيَعْلَمُ مَا تُسِرُّونَ وَمَا تُعْلِنُونَ وَاللَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ', 'يعلم ما في السماوات والارض ويعلم ما تسرون وما تعلنون والله عليم بذات الصدور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 64, 5, 556, 2, 56, 28, 'أَلَمْ يَأْتِكُمْ نَبَأُ الَّذِينَ كَفَرُوا مِنْ قَبْلُ فَذَاقُوا وَبَالَ أَمْرِهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ', 'الم ياتكم نبا الذين كفروا من قبل فذاقوا وبال امرهم ولهم عذاب اليم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 64, 6, 556, 2, 56, 28, 'ذَلِكَ بِأَنَّهُ كَانَتْ تَأْتِيهِمْ رُسُلُهُمْ بِالْبَيِّنَاتِ فَقَالُوا أَبَشَرٌ يَهْدُونَنَا فَكَفَرُوا وَتَوَلَّوْا وَاسْتَغْنَى اللَّهُ وَاللَّهُ غَنِيٌّ حَمِيدٌ', 'ذلك بانه كانت تاتيهم رسلهم بالبينات فقالوا ابشر يهدوننا فكفروا وتولوا واستغنى الله والله غني حميد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 64, 7, 556, 2, 56, 28, 'زَعَمَ الَّذِينَ كَفَرُوا أَنْ لَنْ يُبْعَثُوا قُلْ بَلَى وَرَبِّي لَتُبْعَثُنَّ ثُمَّ لَتُنَبَّؤُنَّ بِمَا عَمِلْتُمْ وَذَلِكَ عَلَى اللَّهِ يَسِيرٌ', 'زعم الذين كفروا ان لن يبعثوا قل بلى وربي لتبعثن ثم لتنبؤن بما عملتم وذلك على الله يسير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 64, 8, 556, 2, 56, 28, 'فَآمِنُوا بِاللَّهِ وَرَسُولِهِ وَالنُّورِ الَّذِي أَنْزَلْنَا وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ', 'فامنوا بالله ورسوله والنور الذي انزلنا والله بما تعملون خبير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 64, 9, 556, 2, 56, 28, 'يَوْمَ يَجْمَعُكُمْ لِيَوْمِ الْجَمْعِ ذَلِكَ يَوْمُ التَّغَابُنِ وَمَنْ يُؤْمِنْ بِاللَّهِ وَيَعْمَلْ صَالِحًا يُكَفِّرْ عَنْهُ سَيِّئَاتِهِ وَيُدْخِلْهُ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا ذَلِكَ الْفَوْزُ الْعَظِيمُ', 'يوم يجمعكم ليوم الجمع ذلك يوم التغابن ومن يؤمن بالله ويعمل صالحا يكفر عنه سيئاته ويدخله جنات تجري من تحتها الانهار خالدين فيها ابدا ذلك الفوز العظيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 64, 10, 557, 2, 56, 28, 'وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَئِكَ أَصْحَابُ النَّارِ خَالِدِينَ فِيهَا وَبِئْسَ الْمَصِيرُ', 'والذين كفروا وكذبوا باياتنا اولئك اصحاب النار خالدين فيها وبئس المصير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 64, 11, 557, 2, 56, 28, 'مَا أَصَابَ مِنْ مُصِيبَةٍ إِلَّا بِإِذْنِ اللَّهِ وَمَنْ يُؤْمِنْ بِاللَّهِ يَهْدِ قَلْبَهُ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ', 'ما اصاب من مصيبة الا باذن الله ومن يؤمن بالله يهد قلبه والله بكل شيء عليم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 64, 12, 557, 2, 56, 28, 'وَأَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ فَإِنْ تَوَلَّيْتُمْ فَإِنَّمَا عَلَى رَسُولِنَا الْبَلَاغُ الْمُبِينُ', 'واطيعوا الله واطيعوا الرسول فان توليتم فانما على رسولنا البلاغ المبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 64, 13, 557, 2, 56, 28, 'اللَّهُ لَا إِلَهَ إِلَّا هُوَ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ', 'الله لا اله الا هو وعلى الله فليتوكل المؤمنون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 64, 14, 557, 2, 56, 28, 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِنَّ مِنْ أَزْوَاجِكُمْ وَأَوْلَادِكُمْ عَدُوًّا لَكُمْ فَاحْذَرُوهُمْ وَإِنْ تَعْفُوا وَتَصْفَحُوا وَتَغْفِرُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 'يا ايها الذين امنوا ان من ازواجكم واولادكم عدوا لكم فاحذروهم وان تعفوا وتصفحوا وتغفروا فان الله غفور رحيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 64, 15, 557, 2, 56, 28, 'إِنَّمَا أَمْوَالُكُمْ وَأَوْلَادُكُمْ فِتْنَةٌ وَاللَّهُ عِنْدَهُ أَجْرٌ عَظِيمٌ', 'انما اموالكم واولادكم فتنة والله عنده اجر عظيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 64, 16, 557, 2, 56, 28, 'فَاتَّقُوا اللَّهَ مَا اسْتَطَعْتُمْ وَاسْمَعُوا وَأَطِيعُوا وَأَنْفِقُوا خَيْرًا لِأَنْفُسِكُمْ وَمَنْ يُوقَ شُحَّ نَفْسِهِ فَأُولَئِكَ هُمُ الْمُفْلِحُونَ', 'فاتقوا الله ما استطعتم واسمعوا واطيعوا وانفقوا خيرا لانفسكم ومن يوق شح نفسه فاولئك هم المفلحون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 64, 17, 557, 2, 56, 28, 'إِنْ تُقْرِضُوا اللَّهَ قَرْضًا حَسَنًا يُضَاعِفْهُ لَكُمْ وَيَغْفِرْ لَكُمْ وَاللَّهُ شَكُورٌ حَلِيمٌ', 'ان تقرضوا الله قرضا حسنا يضاعفه لكم ويغفر لكم والله شكور حليم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 64, 18, 557, 2, 56, 28, 'عَالِمُ الْغَيْبِ وَالشَّهَادَةِ الْعَزِيزُ الْحَكِيمُ', 'عالم الغيب والشهادة العزيز الحكيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 65, 1, 558, 3, 56, 28, 'يَا أَيُّهَا النَّبِيُّ إِذَا طَلَّقْتُمُ النِّسَاءَ فَطَلِّقُوهُنَّ لِعِدَّتِهِنَّ وَأَحْصُوا الْعِدَّةَ وَاتَّقُوا اللَّهَ رَبَّكُمْ لَا تُخْرِجُوهُنَّ مِنْ بُيُوتِهِنَّ وَلَا يَخْرُجْنَ إِلَّا أَنْ يَأْتِينَ بِفَاحِشَةٍ مُبَيِّنَةٍ وَتِلْكَ حُدُودُ اللَّهِ وَمَنْ يَتَعَدَّ حُدُودَ اللَّهِ فَقَدْ ظَلَمَ نَفْسَهُ لَا تَدْرِي لَعَلَّ اللَّهَ يُحْدِثُ بَعْدَ ذَلِكَ أَمْرًا', 'يا ايها النبي اذا طلقتم النساء فطلقوهن لعدتهن واحصوا العدة واتقوا الله ربكم لا تخرجوهن من بيوتهن ولا يخرجن الا ان ياتين بفاحشة مبينة وتلك حدود الله ومن يتعد حدود الله فقد ظلم نفسه لا تدري لعل الله يحدث بعد ذلك امرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 65, 2, 558, 3, 56, 28, 'فَإِذَا بَلَغْنَ أَجَلَهُنَّ فَأَمْسِكُوهُنَّ بِمَعْرُوفٍ أَوْ فَارِقُوهُنَّ بِمَعْرُوفٍ وَأَشْهِدُوا ذَوَيْ عَدْلٍ مِنْكُمْ وَأَقِيمُوا الشَّهَادَةَ لِلَّهِ ذَلِكُمْ يُوعَظُ بِهِ مَنْ كَانَ يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَمَنْ يَتَّقِ اللَّهَ يَجْعَلْ لَهُ مَخْرَجًا', 'فاذا بلغن اجلهن فامسكوهن بمعروف او فارقوهن بمعروف واشهدوا ذوي عدل منكم واقيموا الشهادة لله ذلكم يوعظ به من كان يؤمن بالله واليوم الاخر ومن يتق الله يجعل له مخرجا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 65, 3, 558, 3, 56, 28, 'وَيَرْزُقْهُ مِنْ حَيْثُ لَا يَحْتَسِبُ وَمَنْ يَتَوَكَّلْ عَلَى اللَّهِ فَهُوَ حَسْبُهُ إِنَّ اللَّهَ بَالِغُ أَمْرِهِ قَدْ جَعَلَ اللَّهُ لِكُلِّ شَيْءٍ قَدْرًا', 'ويرزقه من حيث لا يحتسب ومن يتوكل على الله فهو حسبه ان الله بالغ امره قد جعل الله لكل شيء قدرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 65, 4, 558, 3, 56, 28, 'وَاللَّائِي يَئِسْنَ مِنَ الْمَحِيضِ مِنْ نِسَائِكُمْ إِنِ ارْتَبْتُمْ فَعِدَّتُهُنَّ ثَلَاثَةُ أَشْهُرٍ وَاللَّائِي لَمْ يَحِضْنَ وَأُولَاتُ الْأَحْمَالِ أَجَلُهُنَّ أَنْ يَضَعْنَ حَمْلَهُنَّ وَمَنْ يَتَّقِ اللَّهَ يَجْعَلْ لَهُ مِنْ أَمْرِهِ يُسْرًا', 'واللائي يئسن من المحيض من نسائكم ان ارتبتم فعدتهن ثلاثة اشهر واللائي لم يحضن واولات الاحمال اجلهن ان يضعن حملهن ومن يتق الله يجعل له من امره يسرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 65, 5, 558, 3, 56, 28, 'ذَلِكَ أَمْرُ اللَّهِ أَنْزَلَهُ إِلَيْكُمْ وَمَنْ يَتَّقِ اللَّهَ يُكَفِّرْ عَنْهُ سَيِّئَاتِهِ وَيُعْظِمْ لَهُ أَجْرًا', 'ذلك امر الله انزله اليكم ومن يتق الله يكفر عنه سيئاته ويعظم له اجرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 65, 6, 559, 3, 56, 28, 'أَسْكِنُوهُنَّ مِنْ حَيْثُ سَكَنْتُمْ مِنْ وُجْدِكُمْ وَلَا تُضَارُّوهُنَّ لِتُضَيِّقُوا عَلَيْهِنَّ وَإِنْ كُنَّ أُولَاتِ حَمْلٍ فَأَنْفِقُوا عَلَيْهِنَّ حَتَّى يَضَعْنَ حَمْلَهُنَّ فَإِنْ أَرْضَعْنَ لَكُمْ فَآتُوهُنَّ أُجُورَهُنَّ وَأْتَمِرُوا بَيْنَكُمْ بِمَعْرُوفٍ وَإِنْ تَعَاسَرْتُمْ فَسَتُرْضِعُ لَهُ أُخْرَى', 'اسكنوهن من حيث سكنتم من وجدكم ولا تضاروهن لتضيقوا عليهن وان كن اولات حمل فانفقوا عليهن حتى يضعن حملهن فان ارضعن لكم فاتوهن اجورهن واتمروا بينكم بمعروف وان تعاسرتم فسترضع له اخرى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 65, 7, 559, 3, 56, 28, 'لِيُنْفِقْ ذُو سَعَةٍ مِنْ سَعَتِهِ وَمَنْ قُدِرَ عَلَيْهِ رِزْقُهُ فَلْيُنْفِقْ مِمَّا آتَاهُ اللَّهُ لَا يُكَلِّفُ اللَّهُ نَفْسًا إِلَّا مَا آتَاهَا سَيَجْعَلُ اللَّهُ بَعْدَ عُسْرٍ يُسْرًا', 'لينفق ذو سعة من سعته ومن قدر عليه رزقه فلينفق مما اتاه الله لا يكلف الله نفسا الا ما اتاها سيجعل الله بعد عسر يسرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 65, 8, 559, 3, 56, 28, 'وَكَأَيِّنْ مِنْ قَرْيَةٍ عَتَتْ عَنْ أَمْرِ رَبِّهَا وَرُسُلِهِ فَحَاسَبْنَاهَا حِسَابًا شَدِيدًا وَعَذَّبْنَاهَا عَذَابًا نُكْرًا', 'وكاين من قرية عتت عن امر ربها ورسله فحاسبناها حسابا شديدا وعذبناها عذابا نكرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 65, 9, 559, 3, 56, 28, 'فَذَاقَتْ وَبَالَ أَمْرِهَا وَكَانَ عَاقِبَةُ أَمْرِهَا خُسْرًا', 'فذاقت وبال امرها وكان عاقبة امرها خسرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 65, 10, 559, 3, 56, 28, 'أَعَدَّ اللَّهُ لَهُمْ عَذَابًا شَدِيدًا فَاتَّقُوا اللَّهَ يَا أُولِي الْأَلْبَابِ الَّذِينَ آمَنُوا قَدْ أَنْزَلَ اللَّهُ إِلَيْكُمْ ذِكْرًا', 'اعد الله لهم عذابا شديدا فاتقوا الله يا اولي الالباب الذين امنوا قد انزل الله اليكم ذكرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 65, 11, 559, 3, 56, 28, 'رَسُولًا يَتْلُو عَلَيْكُمْ آيَاتِ اللَّهِ مُبَيِّنَاتٍ لِيُخْرِجَ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ مِنَ الظُّلُمَاتِ إِلَى النُّورِ وَمَنْ يُؤْمِنْ بِاللَّهِ وَيَعْمَلْ صَالِحًا يُدْخِلْهُ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا قَدْ أَحْسَنَ اللَّهُ لَهُ رِزْقًا', 'رسولا يتلو عليكم ايات الله مبينات ليخرج الذين امنوا وعملوا الصالحات من الظلمات الى النور ومن يؤمن بالله ويعمل صالحا يدخله جنات تجري من تحتها الانهار خالدين فيها ابدا قد احسن الله له رزقا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 65, 12, 559, 3, 56, 28, 'اللَّهُ الَّذِي خَلَقَ سَبْعَ سَمَاوَاتٍ وَمِنَ الْأَرْضِ مِثْلَهُنَّ يَتَنَزَّلُ الْأَمْرُ بَيْنَهُنَّ لِتَعْلَمُوا أَنَّ اللَّهَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ وَأَنَّ اللَّهَ قَدْ أَحَاطَ بِكُلِّ شَيْءٍ عِلْمًا', 'الله الذي خلق سبع سماوات ومن الارض مثلهن يتنزل الامر بينهن لتعلموا ان الله على كل شيء قدير وان الله قد احاط بكل شيء علما');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 66, 1, 560, 4, 56, 28, 'يَا أَيُّهَا النَّبِيُّ لِمَ تُحَرِّمُ مَا أَحَلَّ اللَّهُ لَكَ تَبْتَغِي مَرْضَاتَ أَزْوَاجِكَ وَاللَّهُ غَفُورٌ رَحِيمٌ', 'يا ايها النبي لم تحرم ما احل الله لك تبتغي مرضات ازواجك والله غفور رحيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 66, 2, 560, 4, 56, 28, 'قَدْ فَرَضَ اللَّهُ لَكُمْ تَحِلَّةَ أَيْمَانِكُمْ وَاللَّهُ مَوْلَاكُمْ وَهُوَ الْعَلِيمُ الْحَكِيمُ', 'قد فرض الله لكم تحلة ايمانكم والله مولاكم وهو العليم الحكيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 66, 3, 560, 4, 56, 28, 'وَإِذْ أَسَرَّ النَّبِيُّ إِلَى بَعْضِ أَزْوَاجِهِ حَدِيثًا فَلَمَّا نَبَّأَتْ بِهِ وَأَظْهَرَهُ اللَّهُ عَلَيْهِ عَرَّفَ بَعْضَهُ وَأَعْرَضَ عَنْ بَعْضٍ فَلَمَّا نَبَّأَهَا بِهِ قَالَتْ مَنْ أَنْبَأَكَ هَذَا قَالَ نَبَّأَنِيَ الْعَلِيمُ الْخَبِيرُ', 'واذ اسر النبي الى بعض ازواجه حديثا فلما نبات به واظهره الله عليه عرف بعضه واعرض عن بعض فلما نباها به قالت من انباك هذا قال نباني العليم الخبير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 66, 4, 560, 4, 56, 28, 'إِنْ تَتُوبَا إِلَى اللَّهِ فَقَدْ صَغَتْ قُلُوبُكُمَا وَإِنْ تَظَاهَرَا عَلَيْهِ فَإِنَّ اللَّهَ هُوَ مَوْلَاهُ وَجِبْرِيلُ وَصَالِحُ الْمُؤْمِنِينَ وَالْمَلَائِكَةُ بَعْدَ ذَلِكَ ظَهِيرٌ', 'ان تتوبا الى الله فقد صغت قلوبكما وان تظاهرا عليه فان الله هو مولاه وجبريل وصالح المؤمنين والملائكة بعد ذلك ظهير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 66, 5, 560, 4, 56, 28, 'عَسَى رَبُّهُ إِنْ طَلَّقَكُنَّ أَنْ يُبْدِلَهُ أَزْوَاجًا خَيْرًا مِنْكُنَّ مُسْلِمَاتٍ مُؤْمِنَاتٍ قَانِتَاتٍ تَائِبَاتٍ عَابِدَاتٍ سَائِحَاتٍ ثَيِّبَاتٍ وَأَبْكَارًا', 'عسى ربه ان طلقكن ان يبدله ازواجا خيرا منكن مسلمات مؤمنات قانتات تائبات عابدات سائحات ثيبات وابكارا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 66, 6, 560, 4, 56, 28, 'يَا أَيُّهَا الَّذِينَ آمَنُوا قُوا أَنْفُسَكُمْ وَأَهْلِيكُمْ نَارًا وَقُودُهَا النَّاسُ وَالْحِجَارَةُ عَلَيْهَا مَلَائِكَةٌ غِلَاظٌ شِدَادٌ لَا يَعْصُونَ اللَّهَ مَا أَمَرَهُمْ وَيَفْعَلُونَ مَا يُؤْمَرُونَ', 'يا ايها الذين امنوا قوا انفسكم واهليكم نارا وقودها الناس والحجارة عليها ملائكة غلاظ شداد لا يعصون الله ما امرهم ويفعلون ما يؤمرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 66, 7, 560, 4, 56, 28, 'يَا أَيُّهَا الَّذِينَ كَفَرُوا لَا تَعْتَذِرُوا الْيَوْمَ إِنَّمَا تُجْزَوْنَ مَا كُنْتُمْ تَعْمَلُونَ', 'يا ايها الذين كفروا لا تعتذروا اليوم انما تجزون ما كنتم تعملون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 66, 8, 561, 4, 56, 28, 'يَا أَيُّهَا الَّذِينَ آمَنُوا تُوبُوا إِلَى اللَّهِ تَوْبَةً نَصُوحًا عَسَى رَبُّكُمْ أَنْ يُكَفِّرَ عَنْكُمْ سَيِّئَاتِكُمْ وَيُدْخِلَكُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ يَوْمَ لَا يُخْزِي اللَّهُ النَّبِيَّ وَالَّذِينَ آمَنُوا مَعَهُ نُورُهُمْ يَسْعَى بَيْنَ أَيْدِيهِمْ وَبِأَيْمَانِهِمْ يَقُولُونَ رَبَّنَا أَتْمِمْ لَنَا نُورَنَا وَاغْفِرْ لَنَا إِنَّكَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ', 'يا ايها الذين امنوا توبوا الى الله توبة نصوحا عسى ربكم ان يكفر عنكم سيئاتكم ويدخلكم جنات تجري من تحتها الانهار يوم لا يخزي الله النبي والذين امنوا معه نورهم يسعى بين ايديهم وبايمانهم يقولون ربنا اتمم لنا نورنا واغفر لنا انك على كل شيء قدير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 66, 9, 561, 4, 56, 28, 'يَا أَيُّهَا النَّبِيُّ جَاهِدِ الْكُفَّارَ وَالْمُنَافِقِينَ وَاغْلُظْ عَلَيْهِمْ وَمَأْوَاهُمْ جَهَنَّمُ وَبِئْسَ الْمَصِيرُ', 'يا ايها النبي جاهد الكفار والمنافقين واغلظ عليهم وماواهم جهنم وبئس المصير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 66, 10, 561, 4, 56, 28, 'ضَرَبَ اللَّهُ مَثَلًا لِلَّذِينَ كَفَرُوا امْرَأَتَ نُوحٍ وَامْرَأَتَ لُوطٍ كَانَتَا تَحْتَ عَبْدَيْنِ مِنْ عِبَادِنَا صَالِحَيْنِ فَخَانَتَاهُمَا فَلَمْ يُغْنِيَا عَنْهُمَا مِنَ اللَّهِ شَيْئًا وَقِيلَ ادْخُلَا النَّارَ مَعَ الدَّاخِلِينَ', 'ضرب الله مثلا للذين كفروا امرات نوح وامرات لوط كانتا تحت عبدين من عبادنا صالحين فخانتاهما فلم يغنيا عنهما من الله شيئا وقيل ادخلا النار مع الداخلين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 66, 11, 561, 4, 56, 28, 'وَضَرَبَ اللَّهُ مَثَلًا لِلَّذِينَ آمَنُوا امْرَأَتَ فِرْعَوْنَ إِذْ قَالَتْ رَبِّ ابْنِ لِي عِنْدَكَ بَيْتًا فِي الْجَنَّةِ وَنَجِّنِي مِنْ فِرْعَوْنَ وَعَمَلِهِ وَنَجِّنِي مِنَ الْقَوْمِ الظَّالِمِينَ', 'وضرب الله مثلا للذين امنوا امرات فرعون اذ قالت رب ابن لي عندك بيتا في الجنة ونجني من فرعون وعمله ونجني من القوم الظالمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 66, 12, 561, 4, 56, 28, 'وَمَرْيَمَ ابْنَتَ عِمْرَانَ الَّتِي أَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا فِيهِ مِنْ رُوحِنَا وَصَدَّقَتْ بِكَلِمَاتِ رَبِّهَا وَكُتُبِهِ وَكَانَتْ مِنَ الْقَانِتِينَ', 'ومريم ابنت عمران التي احصنت فرجها فنفخنا فيه من روحنا وصدقت بكلمات ربها وكتبه وكانت من القانتين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 1, 562, 1, 57, 29, 'تَبَارَكَ الَّذِي بِيَدِهِ الْمُلْكُ وَهُوَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ', 'تبارك الذي بيده الملك وهو على كل شيء قدير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 2, 562, 1, 57, 29, 'الَّذِي خَلَقَ الْمَوْتَ وَالْحَيَاةَ لِيَبْلُوَكُمْ أَيُّكُمْ أَحْسَنُ عَمَلًا وَهُوَ الْعَزِيزُ الْغَفُورُ', 'الذي خلق الموت والحياة ليبلوكم ايكم احسن عملا وهو العزيز الغفور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 3, 562, 1, 57, 29, 'الَّذِي خَلَقَ سَبْعَ سَمَاوَاتٍ طِبَاقًا مَا تَرَى فِي خَلْقِ الرَّحْمَنِ مِنْ تَفَاوُتٍ فَارْجِعِ الْبَصَرَ هَلْ تَرَى مِنْ فُطُورٍ', 'الذي خلق سبع سماوات طباقا ما ترى في خلق الرحمن من تفاوت فارجع البصر هل ترى من فطور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 4, 562, 1, 57, 29, 'ثُمَّ ارْجِعِ الْبَصَرَ كَرَّتَيْنِ يَنْقَلِبْ إِلَيْكَ الْبَصَرُ خَاسِئًا وَهُوَ حَسِيرٌ', 'ثم ارجع البصر كرتين ينقلب اليك البصر خاسئا وهو حسير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 5, 562, 1, 57, 29, 'وَلَقَدْ زَيَّنَّا السَّمَاءَ الدُّنْيَا بِمَصَابِيحَ وَجَعَلْنَاهَا رُجُومًا لِلشَّيَاطِينِ وَأَعْتَدْنَا لَهُمْ عَذَابَ السَّعِيرِ', 'ولقد زينا السماء الدنيا بمصابيح وجعلناها رجوما للشياطين واعتدنا لهم عذاب السعير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 6, 562, 1, 57, 29, 'وَلِلَّذِينَ كَفَرُوا بِرَبِّهِمْ عَذَابُ جَهَنَّمَ وَبِئْسَ الْمَصِيرُ', 'وللذين كفروا بربهم عذاب جهنم وبئس المصير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 7, 562, 1, 57, 29, 'إِذَا أُلْقُوا فِيهَا سَمِعُوا لَهَا شَهِيقًا وَهِيَ تَفُورُ', 'اذا القوا فيها سمعوا لها شهيقا وهي تفور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 8, 562, 1, 57, 29, 'تَكَادُ تَمَيَّزُ مِنَ الْغَيْظِ كُلَّمَا أُلْقِيَ فِيهَا فَوْجٌ سَأَلَهُمْ خَزَنَتُهَا أَلَمْ يَأْتِكُمْ نَذِيرٌ', 'تكاد تميز من الغيظ كلما القي فيها فوج سالهم خزنتها الم ياتكم نذير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 9, 562, 1, 57, 29, 'قَالُوا بَلَى قَدْ جَاءَنَا نَذِيرٌ فَكَذَّبْنَا وَقُلْنَا مَا نَزَّلَ اللَّهُ مِنْ شَيْءٍ إِنْ أَنْتُمْ إِلَّا فِي ضَلَالٍ كَبِيرٍ', 'قالوا بلى قد جاءنا نذير فكذبنا وقلنا ما نزل الله من شيء ان انتم الا في ضلال كبير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 10, 562, 1, 57, 29, 'وَقَالُوا لَوْ كُنَّا نَسْمَعُ أَوْ نَعْقِلُ مَا كُنَّا فِي أَصْحَابِ السَّعِيرِ', 'وقالوا لو كنا نسمع او نعقل ما كنا في اصحاب السعير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 11, 562, 1, 57, 29, 'فَاعْتَرَفُوا بِذَنْبِهِمْ فَسُحْقًا لِأَصْحَابِ السَّعِيرِ', 'فاعترفوا بذنبهم فسحقا لاصحاب السعير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 12, 562, 1, 57, 29, 'إِنَّ الَّذِينَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ لَهُمْ مَغْفِرَةٌ وَأَجْرٌ كَبِيرٌ', 'ان الذين يخشون ربهم بالغيب لهم مغفرة واجر كبير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 13, 563, 1, 57, 29, 'وَأَسِرُّوا قَوْلَكُمْ أَوِ اجْهَرُوا بِهِ إِنَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ', 'واسروا قولكم او اجهروا به انه عليم بذات الصدور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 14, 563, 1, 57, 29, 'أَلَا يَعْلَمُ مَنْ خَلَقَ وَهُوَ اللَّطِيفُ الْخَبِيرُ', 'الا يعلم من خلق وهو اللطيف الخبير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 15, 563, 1, 57, 29, 'هُوَ الَّذِي جَعَلَ لَكُمُ الْأَرْضَ ذَلُولًا فَامْشُوا فِي مَنَاكِبِهَا وَكُلُوا مِنْ رِزْقِهِ وَإِلَيْهِ النُّشُورُ', 'هو الذي جعل لكم الارض ذلولا فامشوا في مناكبها وكلوا من رزقه واليه النشور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 16, 563, 1, 57, 29, 'أَأَمِنْتُمْ مَنْ فِي السَّمَاءِ أَنْ يَخْسِفَ بِكُمُ الْأَرْضَ فَإِذَا هِيَ تَمُورُ', 'اامنتم من في السماء ان يخسف بكم الارض فاذا هي تمور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 17, 563, 1, 57, 29, 'أَمْ أَمِنْتُمْ مَنْ فِي السَّمَاءِ أَنْ يُرْسِلَ عَلَيْكُمْ حَاصِبًا فَسَتَعْلَمُونَ كَيْفَ نَذِيرِ', 'ام امنتم من في السماء ان يرسل عليكم حاصبا فستعلمون كيف نذير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 18, 563, 1, 57, 29, 'وَلَقَدْ كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ فَكَيْفَ كَانَ نَكِيرِ', 'ولقد كذب الذين من قبلهم فكيف كان نكير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 19, 563, 1, 57, 29, 'أَوَلَمْ يَرَوْا إِلَى الطَّيْرِ فَوْقَهُمْ صَافَّاتٍ وَيَقْبِضْنَ مَا يُمْسِكُهُنَّ إِلَّا الرَّحْمَنُ إِنَّهُ بِكُلِّ شَيْءٍ بَصِيرٌ', 'اولم يروا الى الطير فوقهم صافات ويقبضن ما يمسكهن الا الرحمن انه بكل شيء بصير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 20, 563, 1, 57, 29, 'أَمَّنْ هَذَا الَّذِي هُوَ جُنْدٌ لَكُمْ يَنْصُرُكُمْ مِنْ دُونِ الرَّحْمَنِ إِنِ الْكَافِرُونَ إِلَّا فِي غُرُورٍ', 'امن هذا الذي هو جند لكم ينصركم من دون الرحمن ان الكافرون الا في غرور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 21, 563, 1, 57, 29, 'أَمَّنْ هَذَا الَّذِي يَرْزُقُكُمْ إِنْ أَمْسَكَ رِزْقَهُ بَلْ لَجُّوا فِي عُتُوٍّ وَنُفُورٍ', 'امن هذا الذي يرزقكم ان امسك رزقه بل لجوا في عتو ونفور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 22, 563, 1, 57, 29, 'أَفَمَنْ يَمْشِي مُكِبًّا عَلَى وَجْهِهِ أَهْدَى أَمَّنْ يَمْشِي سَوِيًّا عَلَى صِرَاطٍ مُسْتَقِيمٍ', 'افمن يمشي مكبا على وجهه اهدى امن يمشي سويا على صراط مستقيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 23, 563, 1, 57, 29, 'قُلْ هُوَ الَّذِي أَنْشَأَكُمْ وَجَعَلَ لَكُمُ السَّمْعَ وَالْأَبْصَارَ وَالْأَفْئِدَةَ قَلِيلًا مَا تَشْكُرُونَ', 'قل هو الذي انشاكم وجعل لكم السمع والابصار والافئدة قليلا ما تشكرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 24, 563, 1, 57, 29, 'قُلْ هُوَ الَّذِي ذَرَأَكُمْ فِي الْأَرْضِ وَإِلَيْهِ تُحْشَرُونَ', 'قل هو الذي ذراكم في الارض واليه تحشرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 25, 563, 1, 57, 29, 'وَيَقُولُونَ مَتَى هَذَا الْوَعْدُ إِنْ كُنْتُمْ صَادِقِينَ', 'ويقولون متى هذا الوعد ان كنتم صادقين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 26, 563, 1, 57, 29, 'قُلْ إِنَّمَا الْعِلْمُ عِنْدَ اللَّهِ وَإِنَّمَا أَنَا نَذِيرٌ مُبِينٌ', 'قل انما العلم عند الله وانما انا نذير مبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 27, 564, 1, 57, 29, 'فَلَمَّا رَأَوْهُ زُلْفَةً سِيئَتْ وُجُوهُ الَّذِينَ كَفَرُوا وَقِيلَ هَذَا الَّذِي كُنْتُمْ بِهِ تَدَّعُونَ', 'فلما راوه زلفة سيئت وجوه الذين كفروا وقيل هذا الذي كنتم به تدعون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 28, 564, 1, 57, 29, 'قُلْ أَرَأَيْتُمْ إِنْ أَهْلَكَنِيَ اللَّهُ وَمَنْ مَعِيَ أَوْ رَحِمَنَا فَمَنْ يُجِيرُ الْكَافِرِينَ مِنْ عَذَابٍ أَلِيمٍ', 'قل ارايتم ان اهلكني الله ومن معي او رحمنا فمن يجير الكافرين من عذاب اليم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 29, 564, 1, 57, 29, 'قُلْ هُوَ الرَّحْمَنُ آمَنَّا بِهِ وَعَلَيْهِ تَوَكَّلْنَا فَسَتَعْلَمُونَ مَنْ هُوَ فِي ضَلَالٍ مُبِينٍ', 'قل هو الرحمن امنا به وعليه توكلنا فستعلمون من هو في ضلال مبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 67, 30, 564, 1, 57, 29, 'قُلْ أَرَأَيْتُمْ إِنْ أَصْبَحَ مَاؤُكُمْ غَوْرًا فَمَنْ يَأْتِيكُمْ بِمَاءٍ مَعِينٍ', 'قل ارايتم ان اصبح ماؤكم غورا فمن ياتيكم بماء معين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 1, 564, 2, 57, 29, 'ن وَالْقَلَمِ وَمَا يَسْطُرُونَ', 'ن والقلم وما يسطرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 2, 564, 2, 57, 29, 'مَا أَنْتَ بِنِعْمَةِ رَبِّكَ بِمَجْنُونٍ', 'ما انت بنعمة ربك بمجنون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 3, 564, 2, 57, 29, 'وَإِنَّ لَكَ لَأَجْرًا غَيْرَ مَمْنُونٍ', 'وان لك لاجرا غير ممنون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 4, 564, 2, 57, 29, 'وَإِنَّكَ لَعَلَى خُلُقٍ عَظِيمٍ', 'وانك لعلى خلق عظيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 5, 564, 2, 57, 29, 'فَسَتُبْصِرُ وَيُبْصِرُونَ', 'فستبصر ويبصرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 6, 564, 2, 57, 29, 'بِأَيِّكُمُ الْمَفْتُونُ', 'بايكم المفتون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 7, 564, 2, 57, 29, 'إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيلِهِ وَهُوَ أَعْلَمُ بِالْمُهْتَدِينَ', 'ان ربك هو اعلم بمن ضل عن سبيله وهو اعلم بالمهتدين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 8, 564, 2, 57, 29, 'فَلَا تُطِعِ الْمُكَذِّبِينَ', 'فلا تطع المكذبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 9, 564, 2, 57, 29, 'وَدُّوا لَوْ تُدْهِنُ فَيُدْهِنُونَ', 'ودوا لو تدهن فيدهنون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 10, 564, 2, 57, 29, 'وَلَا تُطِعْ كُلَّ حَلَّافٍ مَهِينٍ', 'ولا تطع كل حلاف مهين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 11, 564, 2, 57, 29, 'هَمَّازٍ مَشَّاءٍ بِنَمِيمٍ', 'هماز مشاء بنميم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 12, 564, 2, 57, 29, 'مَنَّاعٍ لِلْخَيْرِ مُعْتَدٍ أَثِيمٍ', 'مناع للخير معتد اثيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 13, 564, 2, 57, 29, 'عُتُلٍّ بَعْدَ ذَلِكَ زَنِيمٍ', 'عتل بعد ذلك زنيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 14, 564, 2, 57, 29, 'أَنْ كَانَ ذَا مَالٍ وَبَنِينَ', 'ان كان ذا مال وبنين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 15, 564, 2, 57, 29, 'إِذَا تُتْلَى عَلَيْهِ آيَاتُنَا قَالَ أَسَاطِيرُ الْأَوَّلِينَ', 'اذا تتلى عليه اياتنا قال اساطير الاولين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 16, 565, 2, 57, 29, 'سَنَسِمُهُ عَلَى الْخُرْطُومِ', 'سنسمه على الخرطوم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 17, 565, 2, 57, 29, 'إِنَّا بَلَوْنَاهُمْ كَمَا بَلَوْنَا أَصْحَابَ الْجَنَّةِ إِذْ أَقْسَمُوا لَيَصْرِمُنَّهَا مُصْبِحِينَ', 'انا بلوناهم كما بلونا اصحاب الجنة اذ اقسموا ليصرمنها مصبحين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 18, 565, 2, 57, 29, 'وَلَا يَسْتَثْنُونَ', 'ولا يستثنون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 19, 565, 2, 57, 29, 'فَطَافَ عَلَيْهَا طَائِفٌ مِنْ رَبِّكَ وَهُمْ نَائِمُونَ', 'فطاف عليها طائف من ربك وهم نائمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 20, 565, 2, 57, 29, 'فَأَصْبَحَتْ كَالصَّرِيمِ', 'فاصبحت كالصريم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 21, 565, 2, 57, 29, 'فَتَنَادَوْا مُصْبِحِينَ', 'فتنادوا مصبحين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 22, 565, 2, 57, 29, 'أَنِ اغْدُوا عَلَى حَرْثِكُمْ إِنْ كُنْتُمْ صَارِمِينَ', 'ان اغدوا على حرثكم ان كنتم صارمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 23, 565, 2, 57, 29, 'فَانْطَلَقُوا وَهُمْ يَتَخَافَتُونَ', 'فانطلقوا وهم يتخافتون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 24, 565, 2, 57, 29, 'أَنْ لَا يَدْخُلَنَّهَا الْيَوْمَ عَلَيْكُمْ مِسْكِينٌ', 'ان لا يدخلنها اليوم عليكم مسكين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 25, 565, 2, 57, 29, 'وَغَدَوْا عَلَى حَرْدٍ قَادِرِينَ', 'وغدوا على حرد قادرين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 26, 565, 2, 57, 29, 'فَلَمَّا رَأَوْهَا قَالُوا إِنَّا لَضَالُّونَ', 'فلما راوها قالوا انا لضالون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 27, 565, 2, 57, 29, 'بَلْ نَحْنُ مَحْرُومُونَ', 'بل نحن محرومون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 28, 565, 2, 57, 29, 'قَالَ أَوْسَطُهُمْ أَلَمْ أَقُلْ لَكُمْ لَوْلَا تُسَبِّحُونَ', 'قال اوسطهم الم اقل لكم لولا تسبحون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 29, 565, 2, 57, 29, 'قَالُوا سُبْحَانَ رَبِّنَا إِنَّا كُنَّا ظَالِمِينَ', 'قالوا سبحان ربنا انا كنا ظالمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 30, 565, 2, 57, 29, 'فَأَقْبَلَ بَعْضُهُمْ عَلَى بَعْضٍ يَتَلَاوَمُونَ', 'فاقبل بعضهم على بعض يتلاومون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 31, 565, 2, 57, 29, 'قَالُوا يَا وَيْلَنَا إِنَّا كُنَّا طَاغِينَ', 'قالوا يا ويلنا انا كنا طاغين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 32, 565, 2, 57, 29, 'عَسَى رَبُّنَا أَنْ يُبْدِلَنَا خَيْرًا مِنْهَا إِنَّا إِلَى رَبِّنَا رَاغِبُونَ', 'عسى ربنا ان يبدلنا خيرا منها انا الى ربنا راغبون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 33, 565, 2, 57, 29, 'كَذَلِكَ الْعَذَابُ وَلَعَذَابُ الْآخِرَةِ أَكْبَرُ لَوْ كَانُوا يَعْلَمُونَ', 'كذلك العذاب ولعذاب الاخرة اكبر لو كانوا يعلمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 34, 565, 2, 57, 29, 'إِنَّ لِلْمُتَّقِينَ عِنْدَ رَبِّهِمْ جَنَّاتِ النَّعِيمِ', 'ان للمتقين عند ربهم جنات النعيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 35, 565, 2, 57, 29, 'أَفَنَجْعَلُ الْمُسْلِمِينَ كَالْمُجْرِمِينَ', 'افنجعل المسلمين كالمجرمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 36, 565, 2, 57, 29, 'مَا لَكُمْ كَيْفَ تَحْكُمُونَ', 'ما لكم كيف تحكمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 37, 565, 2, 57, 29, 'أَمْ لَكُمْ كِتَابٌ فِيهِ تَدْرُسُونَ', 'ام لكم كتاب فيه تدرسون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 38, 565, 2, 57, 29, 'إِنَّ لَكُمْ فِيهِ لَمَا تَخَيَّرُونَ', 'ان لكم فيه لما تخيرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 39, 565, 2, 57, 29, 'أَمْ لَكُمْ أَيْمَانٌ عَلَيْنَا بَالِغَةٌ إِلَى يَوْمِ الْقِيَامَةِ إِنَّ لَكُمْ لَمَا تَحْكُمُونَ', 'ام لكم ايمان علينا بالغة الى يوم القيامة ان لكم لما تحكمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 40, 565, 2, 57, 29, 'سَلْهُمْ أَيُّهُمْ بِذَلِكَ زَعِيمٌ', 'سلهم ايهم بذلك زعيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 41, 565, 2, 57, 29, 'أَمْ لَهُمْ شُرَكَاءُ فَلْيَأْتُوا بِشُرَكَائِهِمْ إِنْ كَانُوا صَادِقِينَ', 'ام لهم شركاء فلياتوا بشركائهم ان كانوا صادقين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 42, 565, 2, 57, 29, 'يَوْمَ يُكْشَفُ عَنْ سَاقٍ وَيُدْعَوْنَ إِلَى السُّجُودِ فَلَا يَسْتَطِيعُونَ', 'يوم يكشف عن ساق ويدعون الى السجود فلا يستطيعون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 43, 566, 2, 57, 29, 'خَاشِعَةً أَبْصَارُهُمْ تَرْهَقُهُمْ ذِلَّةٌ وَقَدْ كَانُوا يُدْعَوْنَ إِلَى السُّجُودِ وَهُمْ سَالِمُونَ', 'خاشعة ابصارهم ترهقهم ذلة وقد كانوا يدعون الى السجود وهم سالمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 44, 566, 2, 57, 29, 'فَذَرْنِي وَمَنْ يُكَذِّبُ بِهَذَا الْحَدِيثِ سَنَسْتَدْرِجُهُمْ مِنْ حَيْثُ لَا يَعْلَمُونَ', 'فذرني ومن يكذب بهذا الحديث سنستدرجهم من حيث لا يعلمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 45, 566, 2, 57, 29, 'وَأُمْلِي لَهُمْ إِنَّ كَيْدِي مَتِينٌ', 'واملي لهم ان كيدي متين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 46, 566, 2, 57, 29, 'أَمْ تَسْأَلُهُمْ أَجْرًا فَهُمْ مِنْ مَغْرَمٍ مُثْقَلُونَ', 'ام تسالهم اجرا فهم من مغرم مثقلون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 47, 566, 2, 57, 29, 'أَمْ عِنْدَهُمُ الْغَيْبُ فَهُمْ يَكْتُبُونَ', 'ام عندهم الغيب فهم يكتبون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 48, 566, 2, 57, 29, 'فَاصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تَكُنْ كَصَاحِبِ الْحُوتِ إِذْ نَادَى وَهُوَ مَكْظُومٌ', 'فاصبر لحكم ربك ولا تكن كصاحب الحوت اذ نادى وهو مكظوم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 49, 566, 2, 57, 29, 'لَوْلَا أَنْ تَدَارَكَهُ نِعْمَةٌ مِنْ رَبِّهِ لَنُبِذَ بِالْعَرَاءِ وَهُوَ مَذْمُومٌ', 'لولا ان تداركه نعمة من ربه لنبذ بالعراء وهو مذموم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 50, 566, 2, 57, 29, 'فَاجْتَبَاهُ رَبُّهُ فَجَعَلَهُ مِنَ الصَّالِحِينَ', 'فاجتباه ربه فجعله من الصالحين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 51, 566, 2, 57, 29, 'وَإِنْ يَكَادُ الَّذِينَ كَفَرُوا لَيُزْلِقُونَكَ بِأَبْصَارِهِمْ لَمَّا سَمِعُوا الذِّكْرَ وَيَقُولُونَ إِنَّهُ لَمَجْنُونٌ', 'وان يكاد الذين كفروا ليزلقونك بابصارهم لما سمعوا الذكر ويقولون انه لمجنون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 68, 52, 566, 2, 57, 29, 'وَمَا هُوَ إِلَّا ذِكْرٌ لِلْعَالَمِينَ', 'وما هو الا ذكر للعالمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 1, 566, 3, 57, 29, 'الْحَاقَّةُ', 'الحاقة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 2, 566, 3, 57, 29, 'مَا الْحَاقَّةُ', 'ما الحاقة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 3, 566, 3, 57, 29, 'وَمَا أَدْرَاكَ مَا الْحَاقَّةُ', 'وما ادراك ما الحاقة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 4, 566, 3, 57, 29, 'كَذَّبَتْ ثَمُودُ وَعَادٌ بِالْقَارِعَةِ', 'كذبت ثمود وعاد بالقارعة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 5, 566, 3, 57, 29, 'فَأَمَّا ثَمُودُ فَأُهْلِكُوا بِالطَّاغِيَةِ', 'فاما ثمود فاهلكوا بالطاغية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 6, 566, 3, 57, 29, 'وَأَمَّا عَادٌ فَأُهْلِكُوا بِرِيحٍ صَرْصَرٍ عَاتِيَةٍ', 'واما عاد فاهلكوا بريح صرصر عاتية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 7, 566, 3, 57, 29, 'سَخَّرَهَا عَلَيْهِمْ سَبْعَ لَيَالٍ وَثَمَانِيَةَ أَيَّامٍ حُسُومًا فَتَرَى الْقَوْمَ فِيهَا صَرْعَى كَأَنَّهُمْ أَعْجَازُ نَخْلٍ خَاوِيَةٍ', 'سخرها عليهم سبع ليال وثمانية ايام حسوما فترى القوم فيها صرعى كانهم اعجاز نخل خاوية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 8, 566, 3, 57, 29, 'فَهَلْ تَرَى لَهُمْ مِنْ بَاقِيَةٍ', 'فهل ترى لهم من باقية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 9, 567, 3, 57, 29, 'وَجَاءَ فِرْعَوْنُ وَمَنْ قَبْلَهُ وَالْمُؤْتَفِكَاتُ بِالْخَاطِئَةِ', 'وجاء فرعون ومن قبله والمؤتفكات بالخاطئة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 10, 567, 3, 57, 29, 'فَعَصَوْا رَسُولَ رَبِّهِمْ فَأَخَذَهُمْ أَخْذَةً رَابِيَةً', 'فعصوا رسول ربهم فاخذهم اخذة رابية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 11, 567, 3, 57, 29, 'إِنَّا لَمَّا طَغَى الْمَاءُ حَمَلْنَاكُمْ فِي الْجَارِيَةِ', 'انا لما طغى الماء حملناكم في الجارية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 12, 567, 3, 57, 29, 'لِنَجْعَلَهَا لَكُمْ تَذْكِرَةً وَتَعِيَهَا أُذُنٌ وَاعِيَةٌ', 'لنجعلها لكم تذكرة وتعيها اذن واعية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 13, 567, 3, 57, 29, 'فَإِذَا نُفِخَ فِي الصُّورِ نَفْخَةٌ وَاحِدَةٌ', 'فاذا نفخ في الصور نفخة واحدة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 14, 567, 3, 57, 29, 'وَحُمِلَتِ الْأَرْضُ وَالْجِبَالُ فَدُكَّتَا دَكَّةً وَاحِدَةً', 'وحملت الارض والجبال فدكتا دكة واحدة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 15, 567, 3, 57, 29, 'فَيَوْمَئِذٍ وَقَعَتِ الْوَاقِعَةُ', 'فيومئذ وقعت الواقعة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 16, 567, 3, 57, 29, 'وَانْشَقَّتِ السَّمَاءُ فَهِيَ يَوْمَئِذٍ وَاهِيَةٌ', 'وانشقت السماء فهي يومئذ واهية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 17, 567, 3, 57, 29, 'وَالْمَلَكُ عَلَى أَرْجَائِهَا وَيَحْمِلُ عَرْشَ رَبِّكَ فَوْقَهُمْ يَوْمَئِذٍ ثَمَانِيَةٌ', 'والملك على ارجائها ويحمل عرش ربك فوقهم يومئذ ثمانية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 18, 567, 3, 57, 29, 'يَوْمَئِذٍ تُعْرَضُونَ لَا تَخْفَى مِنْكُمْ خَافِيَةٌ', 'يومئذ تعرضون لا تخفى منكم خافية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 19, 567, 3, 57, 29, 'فَأَمَّا مَنْ أُوتِيَ كِتَابَهُ بِيَمِينِهِ فَيَقُولُ هَاؤُمُ اقْرَءُوا كِتَابِيَهْ', 'فاما من اوتي كتابه بيمينه فيقول هاؤم اقرءوا كتابيه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 20, 567, 3, 57, 29, 'إِنِّي ظَنَنْتُ أَنِّي مُلَاقٍ حِسَابِيَهْ', 'اني ظننت اني ملاق حسابيه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 21, 567, 3, 57, 29, 'فَهُوَ فِي عِيشَةٍ رَاضِيَةٍ', 'فهو في عيشة راضية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 22, 567, 3, 57, 29, 'فِي جَنَّةٍ عَالِيَةٍ', 'في جنة عالية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 23, 567, 3, 57, 29, 'قُطُوفُهَا دَانِيَةٌ', 'قطوفها دانية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 24, 567, 3, 57, 29, 'كُلُوا وَاشْرَبُوا هَنِيئًا بِمَا أَسْلَفْتُمْ فِي الْأَيَّامِ الْخَالِيَةِ', 'كلوا واشربوا هنيئا بما اسلفتم في الايام الخالية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 25, 567, 3, 57, 29, 'وَأَمَّا مَنْ أُوتِيَ كِتَابَهُ بِشِمَالِهِ فَيَقُولُ يَا لَيْتَنِي لَمْ أُوتَ كِتَابِيَهْ', 'واما من اوتي كتابه بشماله فيقول يا ليتني لم اوت كتابيه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 26, 567, 3, 57, 29, 'وَلَمْ أَدْرِ مَا حِسَابِيَهْ', 'ولم ادر ما حسابيه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 27, 567, 3, 57, 29, 'يَا لَيْتَهَا كَانَتِ الْقَاضِيَةَ', 'يا ليتها كانت القاضية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 28, 567, 3, 57, 29, 'مَا أَغْنَى عَنِّي مَالِيَهْ', 'ما اغنى عني ماليه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 29, 567, 3, 57, 29, 'هَلَكَ عَنِّي سُلْطَانِيَهْ', 'هلك عني سلطانيه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 30, 567, 3, 57, 29, 'خُذُوهُ فَغُلُّوهُ', 'خذوه فغلوه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 31, 567, 3, 57, 29, 'ثُمَّ الْجَحِيمَ صَلُّوهُ', 'ثم الجحيم صلوه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 32, 567, 3, 57, 29, 'ثُمَّ فِي سِلْسِلَةٍ ذَرْعُهَا سَبْعُونَ ذِرَاعًا فَاسْلُكُوهُ', 'ثم في سلسلة ذرعها سبعون ذراعا فاسلكوه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 33, 567, 3, 57, 29, 'إِنَّهُ كَانَ لَا يُؤْمِنُ بِاللَّهِ الْعَظِيمِ', 'انه كان لا يؤمن بالله العظيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 34, 567, 3, 57, 29, 'وَلَا يَحُضُّ عَلَى طَعَامِ الْمِسْكِينِ', 'ولا يحض على طعام المسكين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 35, 568, 3, 57, 29, 'فَلَيْسَ لَهُ الْيَوْمَ هَاهُنَا حَمِيمٌ', 'فليس له اليوم هاهنا حميم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 36, 568, 3, 57, 29, 'وَلَا طَعَامٌ إِلَّا مِنْ غِسْلِينٍ', 'ولا طعام الا من غسلين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 37, 568, 3, 57, 29, 'لَا يَأْكُلُهُ إِلَّا الْخَاطِئُونَ', 'لا ياكله الا الخاطئون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 38, 568, 3, 57, 29, 'فَلَا أُقْسِمُ بِمَا تُبْصِرُونَ', 'فلا اقسم بما تبصرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 39, 568, 3, 57, 29, 'وَمَا لَا تُبْصِرُونَ', 'وما لا تبصرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 40, 568, 3, 57, 29, 'إِنَّهُ لَقَوْلُ رَسُولٍ كَرِيمٍ', 'انه لقول رسول كريم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 41, 568, 3, 57, 29, 'وَمَا هُوَ بِقَوْلِ شَاعِرٍ قَلِيلًا مَا تُؤْمِنُونَ', 'وما هو بقول شاعر قليلا ما تؤمنون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 42, 568, 3, 57, 29, 'وَلَا بِقَوْلِ كَاهِنٍ قَلِيلًا مَا تَذَكَّرُونَ', 'ولا بقول كاهن قليلا ما تذكرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 43, 568, 3, 57, 29, 'تَنْزِيلٌ مِنْ رَبِّ الْعَالَمِينَ', 'تنزيل من رب العالمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 44, 568, 3, 57, 29, 'وَلَوْ تَقَوَّلَ عَلَيْنَا بَعْضَ الْأَقَاوِيلِ', 'ولو تقول علينا بعض الاقاويل');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 45, 568, 3, 57, 29, 'لَأَخَذْنَا مِنْهُ بِالْيَمِينِ', 'لاخذنا منه باليمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 46, 568, 3, 57, 29, 'ثُمَّ لَقَطَعْنَا مِنْهُ الْوَتِينَ', 'ثم لقطعنا منه الوتين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 47, 568, 3, 57, 29, 'فَمَا مِنْكُمْ مِنْ أَحَدٍ عَنْهُ حَاجِزِينَ', 'فما منكم من احد عنه حاجزين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 48, 568, 3, 57, 29, 'وَإِنَّهُ لَتَذْكِرَةٌ لِلْمُتَّقِينَ', 'وانه لتذكرة للمتقين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 49, 568, 3, 57, 29, 'وَإِنَّا لَنَعْلَمُ أَنَّ مِنْكُمْ مُكَذِّبِينَ', 'وانا لنعلم ان منكم مكذبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 50, 568, 3, 57, 29, 'وَإِنَّهُ لَحَسْرَةٌ عَلَى الْكَافِرِينَ', 'وانه لحسرة على الكافرين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 51, 568, 3, 57, 29, 'وَإِنَّهُ لَحَقُّ الْيَقِينِ', 'وانه لحق اليقين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 69, 52, 568, 3, 57, 29, 'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيمِ', 'فسبح باسم ربك العظيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 1, 568, 3, 57, 29, 'سَأَلَ سَائِلٌ بِعَذَابٍ وَاقِعٍ', 'سال سائل بعذاب واقع');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 2, 568, 3, 57, 29, 'لِلْكَافِرِينَ لَيْسَ لَهُ دَافِعٌ', 'للكافرين ليس له دافع');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 3, 568, 3, 57, 29, 'مِنَ اللَّهِ ذِي الْمَعَارِجِ', 'من الله ذي المعارج');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 4, 568, 3, 57, 29, 'تَعْرُجُ الْمَلَائِكَةُ وَالرُّوحُ إِلَيْهِ فِي يَوْمٍ كَانَ مِقْدَارُهُ خَمْسِينَ أَلْفَ سَنَةٍ', 'تعرج الملائكة والروح اليه في يوم كان مقداره خمسين الف سنة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 5, 568, 3, 57, 29, 'فَاصْبِرْ صَبْرًا جَمِيلًا', 'فاصبر صبرا جميلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 6, 568, 3, 57, 29, 'إِنَّهُمْ يَرَوْنَهُ بَعِيدًا', 'انهم يرونه بعيدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 7, 568, 3, 57, 29, 'وَنَرَاهُ قَرِيبًا', 'ونراه قريبا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 8, 568, 3, 57, 29, 'يَوْمَ تَكُونُ السَّمَاءُ كَالْمُهْلِ', 'يوم تكون السماء كالمهل');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 9, 568, 3, 57, 29, 'وَتَكُونُ الْجِبَالُ كَالْعِهْنِ', 'وتكون الجبال كالعهن');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 10, 568, 3, 57, 29, 'وَلَا يَسْأَلُ حَمِيمٌ حَمِيمًا', 'ولا يسال حميم حميما');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 11, 569, 3, 57, 29, 'يُبَصَّرُونَهُمْ يَوَدُّ الْمُجْرِمُ لَوْ يَفْتَدِي مِنْ عَذَابِ يَوْمِئِذٍ بِبَنِيهِ', 'يبصرونهم يود المجرم لو يفتدي من عذاب يومئذ ببنيه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 12, 569, 3, 57, 29, 'وَصَاحِبَتِهِ وَأَخِيهِ', 'وصاحبته واخيه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 13, 569, 3, 57, 29, 'وَفَصِيلَتِهِ الَّتِي تُؤْوِيهِ', 'وفصيلته التي تؤويه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 14, 569, 3, 57, 29, 'وَمَنْ فِي الْأَرْضِ جَمِيعًا ثُمَّ يُنْجِيهِ', 'ومن في الارض جميعا ثم ينجيه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 15, 569, 3, 57, 29, 'كَلَّا إِنَّهَا لَظَى', 'كلا انها لظى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 16, 569, 3, 57, 29, 'نَزَّاعَةً لِلشَّوَى', 'نزاعة للشوى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 17, 569, 3, 57, 29, 'تَدْعُو مَنْ أَدْبَرَ وَتَوَلَّى', 'تدعو من ادبر وتولى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 18, 569, 3, 57, 29, 'وَجَمَعَ فَأَوْعَى', 'وجمع فاوعى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 19, 569, 4, 57, 29, 'إِنَّ الْإِنْسَانَ خُلِقَ هَلُوعًا', 'ان الانسان خلق هلوعا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 20, 569, 4, 57, 29, 'إِذَا مَسَّهُ الشَّرُّ جَزُوعًا', 'اذا مسه الشر جزوعا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 21, 569, 4, 57, 29, 'وَإِذَا مَسَّهُ الْخَيْرُ مَنُوعًا', 'واذا مسه الخير منوعا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 22, 569, 4, 57, 29, 'إِلَّا الْمُصَلِّينَ', 'الا المصلين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 23, 569, 4, 57, 29, 'الَّذِينَ هُمْ عَلَى صَلَاتِهِمْ دَائِمُونَ', 'الذين هم على صلاتهم دائمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 24, 569, 4, 57, 29, 'وَالَّذِينَ فِي أَمْوَالِهِمْ حَقٌّ مَعْلُومٌ', 'والذين في اموالهم حق معلوم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 25, 569, 4, 57, 29, 'لِلسَّائِلِ وَالْمَحْرُومِ', 'للسائل والمحروم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 26, 569, 4, 57, 29, 'وَالَّذِينَ يُصَدِّقُونَ بِيَوْمِ الدِّينِ', 'والذين يصدقون بيوم الدين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 27, 569, 4, 57, 29, 'وَالَّذِينَ هُمْ مِنْ عَذَابِ رَبِّهِمْ مُشْفِقُونَ', 'والذين هم من عذاب ربهم مشفقون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 28, 569, 4, 57, 29, 'إِنَّ عَذَابَ رَبِّهِمْ غَيْرُ مَأْمُونٍ', 'ان عذاب ربهم غير مامون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 29, 569, 4, 57, 29, 'وَالَّذِينَ هُمْ لِفُرُوجِهِمْ حَافِظُونَ', 'والذين هم لفروجهم حافظون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 30, 569, 4, 57, 29, 'إِلَّا عَلَى أَزْوَاجِهِمْ أَوْ مَا مَلَكَتْ أَيْمَانُهُمْ فَإِنَّهُمْ غَيْرُ مَلُومِينَ', 'الا على ازواجهم او ما ملكت ايمانهم فانهم غير ملومين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 31, 569, 4, 57, 29, 'فَمَنِ ابْتَغَى وَرَاءَ ذَلِكَ فَأُولَئِكَ هُمُ الْعَادُونَ', 'فمن ابتغى وراء ذلك فاولئك هم العادون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 32, 569, 4, 57, 29, 'وَالَّذِينَ هُمْ لِأَمَانَاتِهِمْ وَعَهْدِهِمْ رَاعُونَ', 'والذين هم لاماناتهم وعهدهم راعون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 33, 569, 4, 57, 29, 'وَالَّذِينَ هُمْ بِشَهَادَاتِهِمْ قَائِمُونَ', 'والذين هم بشهاداتهم قائمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 34, 569, 4, 57, 29, 'وَالَّذِينَ هُمْ عَلَى صَلَاتِهِمْ يُحَافِظُونَ', 'والذين هم على صلاتهم يحافظون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 35, 569, 4, 57, 29, 'أُولَئِكَ فِي جَنَّاتٍ مُكْرَمُونَ', 'اولئك في جنات مكرمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 36, 569, 4, 57, 29, 'فَمَالِ الَّذِينَ كَفَرُوا قِبَلَكَ مُهْطِعِينَ', 'فمال الذين كفروا قبلك مهطعين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 37, 569, 4, 57, 29, 'عَنِ الْيَمِينِ وَعَنِ الشِّمَالِ عِزِينَ', 'عن اليمين وعن الشمال عزين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 38, 569, 4, 57, 29, 'أَيَطْمَعُ كُلُّ امْرِئٍ مِنْهُمْ أَنْ يُدْخَلَ جَنَّةَ نَعِيمٍ', 'ايطمع كل امرئ منهم ان يدخل جنة نعيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 39, 569, 4, 57, 29, 'كَلَّا إِنَّا خَلَقْنَاهُمْ مِمَّا يَعْلَمُونَ', 'كلا انا خلقناهم مما يعلمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 40, 570, 4, 57, 29, 'فَلَا أُقْسِمُ بِرَبِّ الْمَشَارِقِ وَالْمَغَارِبِ إِنَّا لَقَادِرُونَ', 'فلا اقسم برب المشارق والمغارب انا لقادرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 41, 570, 4, 57, 29, 'عَلَى أَنْ نُبَدِّلَ خَيْرًا مِنْهُمْ وَمَا نَحْنُ بِمَسْبُوقِينَ', 'على ان نبدل خيرا منهم وما نحن بمسبوقين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 42, 570, 4, 57, 29, 'فَذَرْهُمْ يَخُوضُوا وَيَلْعَبُوا حَتَّى يُلَاقُوا يَوْمَهُمُ الَّذِي يُوعَدُونَ', 'فذرهم يخوضوا ويلعبوا حتى يلاقوا يومهم الذي يوعدون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 43, 570, 4, 57, 29, 'يَوْمَ يَخْرُجُونَ مِنَ الْأَجْدَاثِ سِرَاعًا كَأَنَّهُمْ إِلَى نُصُبٍ يُوفِضُونَ', 'يوم يخرجون من الاجداث سراعا كانهم الى نصب يوفضون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 70, 44, 570, 4, 57, 29, 'خَاشِعَةً أَبْصَارُهُمْ تَرْهَقُهُمْ ذِلَّةٌ ذَلِكَ الْيَوْمُ الَّذِي كَانُوا يُوعَدُونَ', 'خاشعة ابصارهم ترهقهم ذلة ذلك اليوم الذي كانوا يوعدون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 1, 570, 4, 57, 29, 'إِنَّا أَرْسَلْنَا نُوحًا إِلَى قَوْمِهِ أَنْ أَنْذِرْ قَوْمَكَ مِنْ قَبْلِ أَنْ يَأْتِيَهُمْ عَذَابٌ أَلِيمٌ', 'انا ارسلنا نوحا الى قومه ان انذر قومك من قبل ان ياتيهم عذاب اليم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 2, 570, 4, 57, 29, 'قَالَ يَا قَوْمِ إِنِّي لَكُمْ نَذِيرٌ مُبِينٌ', 'قال يا قوم اني لكم نذير مبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 3, 570, 4, 57, 29, 'أَنِ اعْبُدُوا اللَّهَ وَاتَّقُوهُ وَأَطِيعُونِ', 'ان اعبدوا الله واتقوه واطيعون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 4, 570, 4, 57, 29, 'يَغْفِرْ لَكُمْ مِنْ ذُنُوبِكُمْ وَيُؤَخِّرْكُمْ إِلَى أَجَلٍ مُسَمًّى إِنَّ أَجَلَ اللَّهِ إِذَا جَاءَ لَا يُؤَخَّرُ لَوْ كُنْتُمْ تَعْلَمُونَ', 'يغفر لكم من ذنوبكم ويؤخركم الى اجل مسمى ان اجل الله اذا جاء لا يؤخر لو كنتم تعلمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 5, 570, 4, 57, 29, 'قَالَ رَبِّ إِنِّي دَعَوْتُ قَوْمِي لَيْلًا وَنَهَارًا', 'قال رب اني دعوت قومي ليلا ونهارا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 6, 570, 4, 57, 29, 'فَلَمْ يَزِدْهُمْ دُعَائِي إِلَّا فِرَارًا', 'فلم يزدهم دعائي الا فرارا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 7, 570, 4, 57, 29, 'وَإِنِّي كُلَّمَا دَعَوْتُهُمْ لِتَغْفِرَ لَهُمْ جَعَلُوا أَصَابِعَهُمْ فِي آذَانِهِمْ وَاسْتَغْشَوْا ثِيَابَهُمْ وَأَصَرُّوا وَاسْتَكْبَرُوا اسْتِكْبَارًا', 'واني كلما دعوتهم لتغفر لهم جعلوا اصابعهم في اذانهم واستغشوا ثيابهم واصروا واستكبروا استكبارا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 8, 570, 4, 57, 29, 'ثُمَّ إِنِّي دَعَوْتُهُمْ جِهَارًا', 'ثم اني دعوتهم جهارا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 9, 570, 4, 57, 29, 'ثُمَّ إِنِّي أَعْلَنْتُ لَهُمْ وَأَسْرَرْتُ لَهُمْ إِسْرَارًا', 'ثم اني اعلنت لهم واسررت لهم اسرارا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 10, 570, 4, 57, 29, 'فَقُلْتُ اسْتَغْفِرُوا رَبَّكُمْ إِنَّهُ كَانَ غَفَّارًا', 'فقلت استغفروا ربكم انه كان غفارا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 11, 571, 4, 57, 29, 'يُرْسِلِ السَّمَاءَ عَلَيْكُمْ مِدْرَارًا', 'يرسل السماء عليكم مدرارا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 12, 571, 4, 57, 29, 'وَيُمْدِدْكُمْ بِأَمْوَالٍ وَبَنِينَ وَيَجْعَلْ لَكُمْ جَنَّاتٍ وَيَجْعَلْ لَكُمْ أَنْهَارًا', 'ويمددكم باموال وبنين ويجعل لكم جنات ويجعل لكم انهارا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 13, 571, 4, 57, 29, 'مَا لَكُمْ لَا تَرْجُونَ لِلَّهِ وَقَارًا', 'ما لكم لا ترجون لله وقارا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 14, 571, 4, 57, 29, 'وَقَدْ خَلَقَكُمْ أَطْوَارًا', 'وقد خلقكم اطوارا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 15, 571, 4, 57, 29, 'أَلَمْ تَرَوْا كَيْفَ خَلَقَ اللَّهُ سَبْعَ سَمَاوَاتٍ طِبَاقًا', 'الم تروا كيف خلق الله سبع سماوات طباقا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 16, 571, 4, 57, 29, 'وَجَعَلَ الْقَمَرَ فِيهِنَّ نُورًا وَجَعَلَ الشَّمْسَ سِرَاجًا', 'وجعل القمر فيهن نورا وجعل الشمس سراجا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 17, 571, 4, 57, 29, 'وَاللَّهُ أَنْبَتَكُمْ مِنَ الْأَرْضِ نَبَاتًا', 'والله انبتكم من الارض نباتا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 18, 571, 4, 57, 29, 'ثُمَّ يُعِيدُكُمْ فِيهَا وَيُخْرِجُكُمْ إِخْرَاجًا', 'ثم يعيدكم فيها ويخرجكم اخراجا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 19, 571, 4, 57, 29, 'وَاللَّهُ جَعَلَ لَكُمُ الْأَرْضَ بِسَاطًا', 'والله جعل لكم الارض بساطا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 20, 571, 4, 57, 29, 'لِتَسْلُكُوا مِنْهَا سُبُلًا فِجَاجًا', 'لتسلكوا منها سبلا فجاجا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 21, 571, 4, 57, 29, 'قَالَ نُوحٌ رَبِّ إِنَّهُمْ عَصَوْنِي وَاتَّبَعُوا مَنْ لَمْ يَزِدْهُ مَالُهُ وَوَلَدُهُ إِلَّا خَسَارًا', 'قال نوح رب انهم عصوني واتبعوا من لم يزده ماله وولده الا خسارا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 22, 571, 4, 57, 29, 'وَمَكَرُوا مَكْرًا كُبَّارًا', 'ومكروا مكرا كبارا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 23, 571, 4, 57, 29, 'وَقَالُوا لَا تَذَرُنَّ آلِهَتَكُمْ وَلَا تَذَرُنَّ وَدًّا وَلَا سُوَاعًا وَلَا يَغُوثَ وَيَعُوقَ وَنَسْرًا', 'وقالوا لا تذرن الهتكم ولا تذرن ودا ولا سواعا ولا يغوث ويعوق ونسرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 24, 571, 4, 57, 29, 'وَقَدْ أَضَلُّوا كَثِيرًا وَلَا تَزِدِ الظَّالِمِينَ إِلَّا ضَلَالًا', 'وقد اضلوا كثيرا ولا تزد الظالمين الا ضلالا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 25, 571, 4, 57, 29, 'مِمَّا خَطِيئَاتِهِمْ أُغْرِقُوا فَأُدْخِلُوا نَارًا فَلَمْ يَجِدُوا لَهُمْ مِنْ دُونِ اللَّهِ أَنْصَارًا', 'مما خطيئاتهم اغرقوا فادخلوا نارا فلم يجدوا لهم من دون الله انصارا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 26, 571, 4, 57, 29, 'وَقَالَ نُوحٌ رَبِّ لَا تَذَرْ عَلَى الْأَرْضِ مِنَ الْكَافِرِينَ دَيَّارًا', 'وقال نوح رب لا تذر على الارض من الكافرين ديارا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 27, 571, 4, 57, 29, 'إِنَّكَ إِنْ تَذَرْهُمْ يُضِلُّوا عِبَادَكَ وَلَا يَلِدُوا إِلَّا فَاجِرًا كَفَّارًا', 'انك ان تذرهم يضلوا عبادك ولا يلدوا الا فاجرا كفارا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 71, 28, 571, 4, 57, 29, 'رَبِّ اغْفِرْ لِي وَلِوَالِدَيَّ وَلِمَنْ دَخَلَ بَيْتِيَ مُؤْمِنًا وَلِلْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ وَلَا تَزِدِ الظَّالِمِينَ إِلَّا تَبَارًا', 'رب اغفر لي ولوالدي ولمن دخل بيتي مؤمنا وللمؤمنين والمؤمنات ولا تزد الظالمين الا تبارا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 1, 572, 1, 58, 29, 'قُلْ أُوحِيَ إِلَيَّ أَنَّهُ اسْتَمَعَ نَفَرٌ مِنَ الْجِنِّ فَقَالُوا إِنَّا سَمِعْنَا قُرْآنًا عَجَبًا', 'قل اوحي الي انه استمع نفر من الجن فقالوا انا سمعنا قرانا عجبا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 2, 572, 1, 58, 29, 'يَهْدِي إِلَى الرُّشْدِ فَآمَنَّا بِهِ وَلَنْ نُشْرِكَ بِرَبِّنَا أَحَدًا', 'يهدي الى الرشد فامنا به ولن نشرك بربنا احدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 3, 572, 1, 58, 29, 'وَأَنَّهُ تَعَالَى جَدُّ رَبِّنَا مَا اتَّخَذَ صَاحِبَةً وَلَا وَلَدًا', 'وانه تعالى جد ربنا ما اتخذ صاحبة ولا ولدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 4, 572, 1, 58, 29, 'وَأَنَّهُ كَانَ يَقُولُ سَفِيهُنَا عَلَى اللَّهِ شَطَطًا', 'وانه كان يقول سفيهنا على الله شططا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 5, 572, 1, 58, 29, 'وَأَنَّا ظَنَنَّا أَنْ لَنْ تَقُولَ الْإِنْسُ وَالْجِنُّ عَلَى اللَّهِ كَذِبًا', 'وانا ظننا ان لن تقول الانس والجن على الله كذبا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 6, 572, 1, 58, 29, 'وَأَنَّهُ كَانَ رِجَالٌ مِنَ الْإِنْسِ يَعُوذُونَ بِرِجَالٍ مِنَ الْجِنِّ فَزَادُوهُمْ رَهَقًا', 'وانه كان رجال من الانس يعوذون برجال من الجن فزادوهم رهقا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 7, 572, 1, 58, 29, 'وَأَنَّهُمْ ظَنُّوا كَمَا ظَنَنْتُمْ أَنْ لَنْ يَبْعَثَ اللَّهُ أَحَدًا', 'وانهم ظنوا كما ظننتم ان لن يبعث الله احدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 8, 572, 1, 58, 29, 'وَأَنَّا لَمَسْنَا السَّمَاءَ فَوَجَدْنَاهَا مُلِئَتْ حَرَسًا شَدِيدًا وَشُهُبًا', 'وانا لمسنا السماء فوجدناها ملئت حرسا شديدا وشهبا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 9, 572, 1, 58, 29, 'وَأَنَّا كُنَّا نَقْعُدُ مِنْهَا مَقَاعِدَ لِلسَّمْعِ فَمَنْ يَسْتَمِعِ الْآنَ يَجِدْ لَهُ شِهَابًا رَصَدًا', 'وانا كنا نقعد منها مقاعد للسمع فمن يستمع الان يجد له شهابا رصدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 10, 572, 1, 58, 29, 'وَأَنَّا لَا نَدْرِي أَشَرٌّ أُرِيدَ بِمَنْ فِي الْأَرْضِ أَمْ أَرَادَ بِهِمْ رَبُّهُمْ رَشَدًا', 'وانا لا ندري اشر اريد بمن في الارض ام اراد بهم ربهم رشدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 11, 572, 1, 58, 29, 'وَأَنَّا مِنَّا الصَّالِحُونَ وَمِنَّا دُونَ ذَلِكَ كُنَّا طَرَائِقَ قِدَدًا', 'وانا منا الصالحون ومنا دون ذلك كنا طرائق قددا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 12, 572, 1, 58, 29, 'وَأَنَّا ظَنَنَّا أَنْ لَنْ نُعْجِزَ اللَّهَ فِي الْأَرْضِ وَلَنْ نُعْجِزَهُ هَرَبًا', 'وانا ظننا ان لن نعجز الله في الارض ولن نعجزه هربا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 13, 572, 1, 58, 29, 'وَأَنَّا لَمَّا سَمِعْنَا الْهُدَى آمَنَّا بِهِ فَمَنْ يُؤْمِنْ بِرَبِّهِ فَلَا يَخَافُ بَخْسًا وَلَا رَهَقًا', 'وانا لما سمعنا الهدى امنا به فمن يؤمن بربه فلا يخاف بخسا ولا رهقا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 14, 573, 1, 58, 29, 'وَأَنَّا مِنَّا الْمُسْلِمُونَ وَمِنَّا الْقَاسِطُونَ فَمَنْ أَسْلَمَ فَأُولَئِكَ تَحَرَّوْا رَشَدًا', 'وانا منا المسلمون ومنا القاسطون فمن اسلم فاولئك تحروا رشدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 15, 573, 1, 58, 29, 'وَأَمَّا الْقَاسِطُونَ فَكَانُوا لِجَهَنَّمَ حَطَبًا', 'واما القاسطون فكانوا لجهنم حطبا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 16, 573, 1, 58, 29, 'وَأَلَّوِ اسْتَقَامُوا عَلَى الطَّرِيقَةِ لَأَسْقَيْنَاهُمْ مَاءً غَدَقًا', 'والو استقاموا على الطريقة لاسقيناهم ماء غدقا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 17, 573, 1, 58, 29, 'لِنَفْتِنَهُمْ فِيهِ وَمَنْ يُعْرِضْ عَنْ ذِكْرِ رَبِّهِ يَسْلُكْهُ عَذَابًا صَعَدًا', 'لنفتنهم فيه ومن يعرض عن ذكر ربه يسلكه عذابا صعدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 18, 573, 1, 58, 29, 'وَأَنَّ الْمَسَاجِدَ لِلَّهِ فَلَا تَدْعُوا مَعَ اللَّهِ أَحَدًا', 'وان المساجد لله فلا تدعوا مع الله احدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 19, 573, 1, 58, 29, 'وَأَنَّهُ لَمَّا قَامَ عَبْدُ اللَّهِ يَدْعُوهُ كَادُوا يَكُونُونَ عَلَيْهِ لِبَدًا', 'وانه لما قام عبد الله يدعوه كادوا يكونون عليه لبدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 20, 573, 1, 58, 29, 'قُلْ إِنَّمَا أَدْعُو رَبِّي وَلَا أُشْرِكُ بِهِ أَحَدًا', 'قل انما ادعو ربي ولا اشرك به احدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 21, 573, 1, 58, 29, 'قُلْ إِنِّي لَا أَمْلِكُ لَكُمْ ضَرًّا وَلَا رَشَدًا', 'قل اني لا املك لكم ضرا ولا رشدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 22, 573, 1, 58, 29, 'قُلْ إِنِّي لَنْ يُجِيرَنِي مِنَ اللَّهِ أَحَدٌ وَلَنْ أَجِدَ مِنْ دُونِهِ مُلْتَحَدًا', 'قل اني لن يجيرني من الله احد ولن اجد من دونه ملتحدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 23, 573, 1, 58, 29, 'إِلَّا بَلَاغًا مِنَ اللَّهِ وَرِسَالَاتِهِ وَمَنْ يَعْصِ اللَّهَ وَرَسُولَهُ فَإِنَّ لَهُ نَارَ جَهَنَّمَ خَالِدِينَ فِيهَا أَبَدًا', 'الا بلاغا من الله ورسالاته ومن يعص الله ورسوله فان له نار جهنم خالدين فيها ابدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 24, 573, 1, 58, 29, 'حَتَّى إِذَا رَأَوْا مَا يُوعَدُونَ فَسَيَعْلَمُونَ مَنْ أَضْعَفُ نَاصِرًا وَأَقَلُّ عَدَدًا', 'حتى اذا راوا ما يوعدون فسيعلمون من اضعف ناصرا واقل عددا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 25, 573, 1, 58, 29, 'قُلْ إِنْ أَدْرِي أَقَرِيبٌ مَا تُوعَدُونَ أَمْ يَجْعَلُ لَهُ رَبِّي أَمَدًا', 'قل ان ادري اقريب ما توعدون ام يجعل له ربي امدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 26, 573, 1, 58, 29, 'عَالِمُ الْغَيْبِ فَلَا يُظْهِرُ عَلَى غَيْبِهِ أَحَدًا', 'عالم الغيب فلا يظهر على غيبه احدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 27, 573, 1, 58, 29, 'إِلَّا مَنِ ارْتَضَى مِنْ رَسُولٍ فَإِنَّهُ يَسْلُكُ مِنْ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهِ رَصَدًا', 'الا من ارتضى من رسول فانه يسلك من بين يديه ومن خلفه رصدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 72, 28, 573, 1, 58, 29, 'لِيَعْلَمَ أَنْ قَدْ أَبْلَغُوا رِسَالَاتِ رَبِّهِمْ وَأَحَاطَ بِمَا لَدَيْهِمْ وَأَحْصَى كُلَّ شَيْءٍ عَدَدًا', 'ليعلم ان قد ابلغوا رسالات ربهم واحاط بما لديهم واحصى كل شيء عددا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 1, 574, 1, 58, 29, 'يَا أَيُّهَا الْمُزَّمِّلُ', 'يا ايها المزمل');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 2, 574, 1, 58, 29, 'قُمِ اللَّيْلَ إِلَّا قَلِيلًا', 'قم الليل الا قليلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 3, 574, 1, 58, 29, 'نِصْفَهُ أَوِ انْقُصْ مِنْهُ قَلِيلًا', 'نصفه او انقص منه قليلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 4, 574, 1, 58, 29, 'أَوْ زِدْ عَلَيْهِ وَرَتِّلِ الْقُرْآنَ تَرْتِيلًا', 'او زد عليه ورتل القران ترتيلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 5, 574, 1, 58, 29, 'إِنَّا سَنُلْقِي عَلَيْكَ قَوْلًا ثَقِيلًا', 'انا سنلقي عليك قولا ثقيلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 6, 574, 1, 58, 29, 'إِنَّ نَاشِئَةَ اللَّيْلِ هِيَ أَشَدُّ وَطْئًا وَأَقْوَمُ قِيلًا', 'ان ناشئة الليل هي اشد وطئا واقوم قيلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 7, 574, 1, 58, 29, 'إِنَّ لَكَ فِي النَّهَارِ سَبْحًا طَوِيلًا', 'ان لك في النهار سبحا طويلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 8, 574, 1, 58, 29, 'وَاذْكُرِ اسْمَ رَبِّكَ وَتَبَتَّلْ إِلَيْهِ تَبْتِيلًا', 'واذكر اسم ربك وتبتل اليه تبتيلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 9, 574, 1, 58, 29, 'رَبُّ الْمَشْرِقِ وَالْمَغْرِبِ لَا إِلَهَ إِلَّا هُوَ فَاتَّخِذْهُ وَكِيلًا', 'رب المشرق والمغرب لا اله الا هو فاتخذه وكيلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 10, 574, 1, 58, 29, 'وَاصْبِرْ عَلَى مَا يَقُولُونَ وَاهْجُرْهُمْ هَجْرًا جَمِيلًا', 'واصبر على ما يقولون واهجرهم هجرا جميلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 11, 574, 1, 58, 29, 'وَذَرْنِي وَالْمُكَذِّبِينَ أُولِي النَّعْمَةِ وَمَهِّلْهُمْ قَلِيلًا', 'وذرني والمكذبين اولي النعمة ومهلهم قليلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 12, 574, 1, 58, 29, 'إِنَّ لَدَيْنَا أَنْكَالًا وَجَحِيمًا', 'ان لدينا انكالا وجحيما');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 13, 574, 1, 58, 29, 'وَطَعَامًا ذَا غُصَّةٍ وَعَذَابًا أَلِيمًا', 'وطعاما ذا غصة وعذابا اليما');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 14, 574, 1, 58, 29, 'يَوْمَ تَرْجُفُ الْأَرْضُ وَالْجِبَالُ وَكَانَتِ الْجِبَالُ كَثِيبًا مَهِيلًا', 'يوم ترجف الارض والجبال وكانت الجبال كثيبا مهيلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 15, 574, 1, 58, 29, 'إِنَّا أَرْسَلْنَا إِلَيْكُمْ رَسُولًا شَاهِدًا عَلَيْكُمْ كَمَا أَرْسَلْنَا إِلَى فِرْعَوْنَ رَسُولًا', 'انا ارسلنا اليكم رسولا شاهدا عليكم كما ارسلنا الى فرعون رسولا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 16, 574, 1, 58, 29, 'فَعَصَى فِرْعَوْنُ الرَّسُولَ فَأَخَذْنَاهُ أَخْذًا وَبِيلًا', 'فعصى فرعون الرسول فاخذناه اخذا وبيلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 17, 574, 1, 58, 29, 'فَكَيْفَ تَتَّقُونَ إِنْ كَفَرْتُمْ يَوْمًا يَجْعَلُ الْوِلْدَانَ شِيبًا', 'فكيف تتقون ان كفرتم يوما يجعل الولدان شيبا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 18, 574, 1, 58, 29, 'السَّمَاءُ مُنْفَطِرٌ بِهِ كَانَ وَعْدُهُ مَفْعُولًا', 'السماء منفطر به كان وعده مفعولا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 19, 574, 1, 58, 29, 'إِنَّ هَذِهِ تَذْكِرَةٌ فَمَنْ شَاءَ اتَّخَذَ إِلَى رَبِّهِ سَبِيلًا', 'ان هذه تذكرة فمن شاء اتخذ الى ربه سبيلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 73, 20, 575, 2, 58, 29, 'إِنَّ رَبَّكَ يَعْلَمُ أَنَّكَ تَقُومُ أَدْنَى مِنْ ثُلُثَيِ اللَّيْلِ وَنِصْفَهُ وَثُلُثَهُ وَطَائِفَةٌ مِنَ الَّذِينَ مَعَكَ وَاللَّهُ يُقَدِّرُ اللَّيْلَ وَالنَّهَارَ عَلِمَ أَنْ لَنْ تُحْصُوهُ فَتَابَ عَلَيْكُمْ فَاقْرَءُوا مَا تَيَسَّرَ مِنَ الْقُرْآنِ عَلِمَ أَنْ سَيَكُونُ مِنْكُمْ مَرْضَى وَآخَرُونَ يَضْرِبُونَ فِي الْأَرْضِ يَبْتَغُونَ مِنْ فَضْلِ اللَّهِ وَآخَرُونَ يُقَاتِلُونَ فِي سَبِيلِ اللَّهِ فَاقْرَءُوا مَا تَيَسَّرَ مِنْهُ وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ وَأَقْرِضُوا اللَّهَ قَرْضًا حَسَنًا وَمَا تُقَدِّمُوا لِأَنْفُسِكُمْ مِنْ خَيْرٍ تَجِدُوهُ عِنْدَ اللَّهِ هُوَ خَيْرًا وَأَعْظَمَ أَجْرًا وَاسْتَغْفِرُوا اللَّهَ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', 'ان ربك يعلم انك تقوم ادنى من ثلثي الليل ونصفه وثلثه وطائفة من الذين معك والله يقدر الليل والنهار علم ان لن تحصوه فتاب عليكم فاقرءوا ما تيسر من القران علم ان سيكون منكم مرضى واخرون يضربون في الارض يبتغون من فضل الله واخرون يقاتلون في سبيل الله فاقرءوا ما تيسر منه واقيموا الصلاة واتوا الزكاة واقرضوا الله قرضا حسنا وما تقدموا لانفسكم من خير تجدوه عند الله هو خيرا واعظم اجرا واستغفروا الله ان الله غفور رحيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 1, 575, 2, 58, 29, 'يَا أَيُّهَا الْمُدَّثِّرُ', 'يا ايها المدثر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 2, 575, 2, 58, 29, 'قُمْ فَأَنْذِرْ', 'قم فانذر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 3, 575, 2, 58, 29, 'وَرَبَّكَ فَكَبِّرْ', 'وربك فكبر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 4, 575, 2, 58, 29, 'وَثِيَابَكَ فَطَهِّرْ', 'وثيابك فطهر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 5, 575, 2, 58, 29, 'وَالرُّجْزَ فَاهْجُرْ', 'والرجز فاهجر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 6, 575, 2, 58, 29, 'وَلَا تَمْنُنْ تَسْتَكْثِرُ', 'ولا تمنن تستكثر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 7, 575, 2, 58, 29, 'وَلِرَبِّكَ فَاصْبِرْ', 'ولربك فاصبر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 8, 575, 2, 58, 29, 'فَإِذَا نُقِرَ فِي النَّاقُورِ', 'فاذا نقر في الناقور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 9, 575, 2, 58, 29, 'فَذَلِكَ يَوْمَئِذٍ يَوْمٌ عَسِيرٌ', 'فذلك يومئذ يوم عسير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 10, 575, 2, 58, 29, 'عَلَى الْكَافِرِينَ غَيْرُ يَسِيرٍ', 'على الكافرين غير يسير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 11, 575, 2, 58, 29, 'ذَرْنِي وَمَنْ خَلَقْتُ وَحِيدًا', 'ذرني ومن خلقت وحيدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 12, 575, 2, 58, 29, 'وَجَعَلْتُ لَهُ مَالًا مَمْدُودًا', 'وجعلت له مالا ممدودا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 13, 575, 2, 58, 29, 'وَبَنِينَ شُهُودًا', 'وبنين شهودا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 14, 575, 2, 58, 29, 'وَمَهَّدْتُ لَهُ تَمْهِيدًا', 'ومهدت له تمهيدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 15, 575, 2, 58, 29, 'ثُمَّ يَطْمَعُ أَنْ أَزِيدَ', 'ثم يطمع ان ازيد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 16, 575, 2, 58, 29, 'كَلَّا إِنَّهُ كَانَ لِآيَاتِنَا عَنِيدًا', 'كلا انه كان لاياتنا عنيدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 17, 575, 2, 58, 29, 'سَأُرْهِقُهُ صَعُودًا', 'سارهقه صعودا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 18, 576, 2, 58, 29, 'إِنَّهُ فَكَّرَ وَقَدَّرَ', 'انه فكر وقدر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 19, 576, 2, 58, 29, 'فَقُتِلَ كَيْفَ قَدَّرَ', 'فقتل كيف قدر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 20, 576, 2, 58, 29, 'ثُمَّ قُتِلَ كَيْفَ قَدَّرَ', 'ثم قتل كيف قدر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 21, 576, 2, 58, 29, 'ثُمَّ نَظَرَ', 'ثم نظر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 22, 576, 2, 58, 29, 'ثُمَّ عَبَسَ وَبَسَرَ', 'ثم عبس وبسر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 23, 576, 2, 58, 29, 'ثُمَّ أَدْبَرَ وَاسْتَكْبَرَ', 'ثم ادبر واستكبر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 24, 576, 2, 58, 29, 'فَقَالَ إِنْ هَذَا إِلَّا سِحْرٌ يُؤْثَرُ', 'فقال ان هذا الا سحر يؤثر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 25, 576, 2, 58, 29, 'إِنْ هَذَا إِلَّا قَوْلُ الْبَشَرِ', 'ان هذا الا قول البشر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 26, 576, 2, 58, 29, 'سَأُصْلِيهِ سَقَرَ', 'ساصليه سقر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 27, 576, 2, 58, 29, 'وَمَا أَدْرَاكَ مَا سَقَرُ', 'وما ادراك ما سقر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 28, 576, 2, 58, 29, 'لَا تُبْقِي وَلَا تَذَرُ', 'لا تبقي ولا تذر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 29, 576, 2, 58, 29, 'لَوَّاحَةٌ لِلْبَشَرِ', 'لواحة للبشر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 30, 576, 2, 58, 29, 'عَلَيْهَا تِسْعَةَ عَشَرَ', 'عليها تسعة عشر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 31, 576, 2, 58, 29, 'وَمَا جَعَلْنَا أَصْحَابَ النَّارِ إِلَّا مَلَائِكَةً وَمَا جَعَلْنَا عِدَّتَهُمْ إِلَّا فِتْنَةً لِلَّذِينَ كَفَرُوا لِيَسْتَيْقِنَ الَّذِينَ أُوتُوا الْكِتَابَ وَيَزْدَادَ الَّذِينَ آمَنُوا إِيمَانًا وَلَا يَرْتَابَ الَّذِينَ أُوتُوا الْكِتَابَ وَالْمُؤْمِنُونَ وَلِيَقُولَ الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ وَالْكَافِرُونَ مَاذَا أَرَادَ اللَّهُ بِهَذَا مَثَلًا كَذَلِكَ يُضِلُّ اللَّهُ مَنْ يَشَاءُ وَيَهْدِي مَنْ يَشَاءُ وَمَا يَعْلَمُ جُنُودَ رَبِّكَ إِلَّا هُوَ وَمَا هِيَ إِلَّا ذِكْرَى لِلْبَشَرِ', 'وما جعلنا اصحاب النار الا ملائكة وما جعلنا عدتهم الا فتنة للذين كفروا ليستيقن الذين اوتوا الكتاب ويزداد الذين امنوا ايمانا ولا يرتاب الذين اوتوا الكتاب والمؤمنون وليقول الذين في قلوبهم مرض والكافرون ماذا اراد الله بهذا مثلا كذلك يضل الله من يشاء ويهدي من يشاء وما يعلم جنود ربك الا هو وما هي الا ذكرى للبشر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 32, 576, 2, 58, 29, 'كَلَّا وَالْقَمَرِ', 'كلا والقمر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 33, 576, 2, 58, 29, 'وَاللَّيْلِ إِذْ أَدْبَرَ', 'والليل اذ ادبر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 34, 576, 2, 58, 29, 'وَالصُّبْحِ إِذَا أَسْفَرَ', 'والصبح اذا اسفر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 35, 576, 2, 58, 29, 'إِنَّهَا لَإِحْدَى الْكُبَرِ', 'انها لاحدى الكبر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 36, 576, 2, 58, 29, 'نَذِيرًا لِلْبَشَرِ', 'نذيرا للبشر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 37, 576, 2, 58, 29, 'لِمَنْ شَاءَ مِنْكُمْ أَنْ يَتَقَدَّمَ أَوْ يَتَأَخَّرَ', 'لمن شاء منكم ان يتقدم او يتاخر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 38, 576, 2, 58, 29, 'كُلُّ نَفْسٍ بِمَا كَسَبَتْ رَهِينَةٌ', 'كل نفس بما كسبت رهينة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 39, 576, 2, 58, 29, 'إِلَّا أَصْحَابَ الْيَمِينِ', 'الا اصحاب اليمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 40, 576, 2, 58, 29, 'فِي جَنَّاتٍ يَتَسَاءَلُونَ', 'في جنات يتساءلون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 41, 576, 2, 58, 29, 'عَنِ الْمُجْرِمِينَ', 'عن المجرمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 42, 576, 2, 58, 29, 'مَا سَلَكَكُمْ فِي سَقَرَ', 'ما سلككم في سقر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 43, 576, 2, 58, 29, 'قَالُوا لَمْ نَكُ مِنَ الْمُصَلِّينَ', 'قالوا لم نك من المصلين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 44, 576, 2, 58, 29, 'وَلَمْ نَكُ نُطْعِمُ الْمِسْكِينَ', 'ولم نك نطعم المسكين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 45, 576, 2, 58, 29, 'وَكُنَّا نَخُوضُ مَعَ الْخَائِضِينَ', 'وكنا نخوض مع الخائضين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 46, 576, 2, 58, 29, 'وَكُنَّا نُكَذِّبُ بِيَوْمِ الدِّينِ', 'وكنا نكذب بيوم الدين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 47, 576, 2, 58, 29, 'حَتَّى أَتَانَا الْيَقِينُ', 'حتى اتانا اليقين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 48, 577, 2, 58, 29, 'فَمَا تَنْفَعُهُمْ شَفَاعَةُ الشَّافِعِينَ', 'فما تنفعهم شفاعة الشافعين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 49, 577, 2, 58, 29, 'فَمَا لَهُمْ عَنِ التَّذْكِرَةِ مُعْرِضِينَ', 'فما لهم عن التذكرة معرضين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 50, 577, 2, 58, 29, 'كَأَنَّهُمْ حُمُرٌ مُسْتَنْفِرَةٌ', 'كانهم حمر مستنفرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 51, 577, 2, 58, 29, 'فَرَّتْ مِنْ قَسْوَرَةٍ', 'فرت من قسورة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 52, 577, 2, 58, 29, 'بَلْ يُرِيدُ كُلُّ امْرِئٍ مِنْهُمْ أَنْ يُؤْتَى صُحُفًا مُنَشَّرَةً', 'بل يريد كل امرئ منهم ان يؤتى صحفا منشرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 53, 577, 2, 58, 29, 'كَلَّا بَلْ لَا يَخَافُونَ الْآخِرَةَ', 'كلا بل لا يخافون الاخرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 54, 577, 2, 58, 29, 'كَلَّا إِنَّهُ تَذْكِرَةٌ', 'كلا انه تذكرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 55, 577, 2, 58, 29, 'فَمَنْ شَاءَ ذَكَرَهُ', 'فمن شاء ذكره');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 74, 56, 577, 2, 58, 29, 'وَمَا يَذْكُرُونَ إِلَّا أَنْ يَشَاءَ اللَّهُ هُوَ أَهْلُ التَّقْوَى وَأَهْلُ الْمَغْفِرَةِ', 'وما يذكرون الا ان يشاء الله هو اهل التقوى واهل المغفرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 1, 577, 3, 58, 29, 'لَا أُقْسِمُ بِيَوْمِ الْقِيَامَةِ', 'لا اقسم بيوم القيامة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 2, 577, 3, 58, 29, 'وَلَا أُقْسِمُ بِالنَّفْسِ اللَّوَّامَةِ', 'ولا اقسم بالنفس اللوامة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 3, 577, 3, 58, 29, 'أَيَحْسَبُ الْإِنْسَانُ أَلَّنْ نَجْمَعَ عِظَامَهُ', 'ايحسب الانسان الن نجمع عظامه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 4, 577, 3, 58, 29, 'بَلَى قَادِرِينَ عَلَى أَنْ نُسَوِّيَ بَنَانَهُ', 'بلى قادرين على ان نسوي بنانه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 5, 577, 3, 58, 29, 'بَلْ يُرِيدُ الْإِنْسَانُ لِيَفْجُرَ أَمَامَهُ', 'بل يريد الانسان ليفجر امامه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 6, 577, 3, 58, 29, 'يَسْأَلُ أَيَّانَ يَوْمُ الْقِيَامَةِ', 'يسال ايان يوم القيامة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 7, 577, 3, 58, 29, 'فَإِذَا بَرِقَ الْبَصَرُ', 'فاذا برق البصر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 8, 577, 3, 58, 29, 'وَخَسَفَ الْقَمَرُ', 'وخسف القمر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 9, 577, 3, 58, 29, 'وَجُمِعَ الشَّمْسُ وَالْقَمَرُ', 'وجمع الشمس والقمر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 10, 577, 3, 58, 29, 'يَقُولُ الْإِنْسَانُ يَوْمَئِذٍ أَيْنَ الْمَفَرُّ', 'يقول الانسان يومئذ اين المفر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 11, 577, 3, 58, 29, 'كَلَّا لَا وَزَرَ', 'كلا لا وزر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 12, 577, 3, 58, 29, 'إِلَى رَبِّكَ يَوْمَئِذٍ الْمُسْتَقَرُّ', 'الى ربك يومئذ المستقر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 13, 577, 3, 58, 29, 'يُنَبَّأُ الْإِنْسَانُ يَوْمَئِذٍ بِمَا قَدَّمَ وَأَخَّرَ', 'ينبا الانسان يومئذ بما قدم واخر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 14, 577, 3, 58, 29, 'بَلِ الْإِنْسَانُ عَلَى نَفْسِهِ بَصِيرَةٌ', 'بل الانسان على نفسه بصيرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 15, 577, 3, 58, 29, 'وَلَوْ أَلْقَى مَعَاذِيرَهُ', 'ولو القى معاذيره');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 16, 577, 3, 58, 29, 'لَا تُحَرِّكْ بِهِ لِسَانَكَ لِتَعْجَلَ بِهِ', 'لا تحرك به لسانك لتعجل به');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 17, 577, 3, 58, 29, 'إِنَّ عَلَيْنَا جَمْعَهُ وَقُرْآنَهُ', 'ان علينا جمعه وقرانه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 18, 577, 3, 58, 29, 'فَإِذَا قَرَأْنَاهُ فَاتَّبِعْ قُرْآنَهُ', 'فاذا قراناه فاتبع قرانه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 19, 577, 3, 58, 29, 'ثُمَّ إِنَّ عَلَيْنَا بَيَانَهُ', 'ثم ان علينا بيانه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 20, 578, 3, 58, 29, 'كَلَّا بَلْ تُحِبُّونَ الْعَاجِلَةَ', 'كلا بل تحبون العاجلة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 21, 578, 3, 58, 29, 'وَتَذَرُونَ الْآخِرَةَ', 'وتذرون الاخرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 22, 578, 3, 58, 29, 'وُجُوهٌ يَوْمَئِذٍ نَاضِرَةٌ', 'وجوه يومئذ ناضرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 23, 578, 3, 58, 29, 'إِلَى رَبِّهَا نَاظِرَةٌ', 'الى ربها ناظرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 24, 578, 3, 58, 29, 'وَوُجُوهٌ يَوْمَئِذٍ بَاسِرَةٌ', 'ووجوه يومئذ باسرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 25, 578, 3, 58, 29, 'تَظُنُّ أَنْ يُفْعَلَ بِهَا فَاقِرَةٌ', 'تظن ان يفعل بها فاقرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 26, 578, 3, 58, 29, 'كَلَّا إِذَا بَلَغَتِ التَّرَاقِيَ', 'كلا اذا بلغت التراقي');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 27, 578, 3, 58, 29, 'وَقِيلَ مَنْ رَاقٍ', 'وقيل من راق');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 28, 578, 3, 58, 29, 'وَظَنَّ أَنَّهُ الْفِرَاقُ', 'وظن انه الفراق');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 29, 578, 3, 58, 29, 'وَالْتَفَّتِ السَّاقُ بِالسَّاقِ', 'والتفت الساق بالساق');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 30, 578, 3, 58, 29, 'إِلَى رَبِّكَ يَوْمَئِذٍ الْمَسَاقُ', 'الى ربك يومئذ المساق');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 31, 578, 3, 58, 29, 'فَلَا صَدَّقَ وَلَا صَلَّى', 'فلا صدق ولا صلى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 32, 578, 3, 58, 29, 'وَلَكِنْ كَذَّبَ وَتَوَلَّى', 'ولكن كذب وتولى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 33, 578, 3, 58, 29, 'ثُمَّ ذَهَبَ إِلَى أَهْلِهِ يَتَمَطَّى', 'ثم ذهب الى اهله يتمطى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 34, 578, 3, 58, 29, 'أَوْلَى لَكَ فَأَوْلَى', 'اولى لك فاولى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 35, 578, 3, 58, 29, 'ثُمَّ أَوْلَى لَكَ فَأَوْلَى', 'ثم اولى لك فاولى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 36, 578, 3, 58, 29, 'أَيَحْسَبُ الْإِنْسَانُ أَنْ يُتْرَكَ سُدًى', 'ايحسب الانسان ان يترك سدى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 37, 578, 3, 58, 29, 'أَلَمْ يَكُ نُطْفَةً مِنْ مَنِيٍّ يُمْنَى', 'الم يك نطفة من مني يمنى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 38, 578, 3, 58, 29, 'ثُمَّ كَانَ عَلَقَةً فَخَلَقَ فَسَوَّى', 'ثم كان علقة فخلق فسوى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 39, 578, 3, 58, 29, 'فَجَعَلَ مِنْهُ الزَّوْجَيْنِ الذَّكَرَ وَالْأُنْثَى', 'فجعل منه الزوجين الذكر والانثى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 75, 40, 578, 3, 58, 29, 'أَلَيْسَ ذَلِكَ بِقَادِرٍ عَلَى أَنْ يُحْيِيَ الْمَوْتَى', 'اليس ذلك بقادر على ان يحيي الموتى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 1, 578, 3, 58, 29, 'هَلْ أَتَى عَلَى الْإِنْسَانِ حِينٌ مِنَ الدَّهْرِ لَمْ يَكُنْ شَيْئًا مَذْكُورًا', 'هل اتى على الانسان حين من الدهر لم يكن شيئا مذكورا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 2, 578, 3, 58, 29, 'إِنَّا خَلَقْنَا الْإِنْسَانَ مِنْ نُطْفَةٍ أَمْشَاجٍ نَبْتَلِيهِ فَجَعَلْنَاهُ سَمِيعًا بَصِيرًا', 'انا خلقنا الانسان من نطفة امشاج نبتليه فجعلناه سميعا بصيرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 3, 578, 3, 58, 29, 'إِنَّا هَدَيْنَاهُ السَّبِيلَ إِمَّا شَاكِرًا وَإِمَّا كَفُورًا', 'انا هديناه السبيل اما شاكرا واما كفورا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 4, 578, 3, 58, 29, 'إِنَّا أَعْتَدْنَا لِلْكَافِرِينَ سَلَاسِلَ وَأَغْلَالًا وَسَعِيرًا', 'انا اعتدنا للكافرين سلاسل واغلالا وسعيرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 5, 578, 3, 58, 29, 'إِنَّ الْأَبْرَارَ يَشْرَبُونَ مِنْ كَأْسٍ كَانَ مِزَاجُهَا كَافُورًا', 'ان الابرار يشربون من كاس كان مزاجها كافورا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 6, 579, 3, 58, 29, 'عَيْنًا يَشْرَبُ بِهَا عِبَادُ اللَّهِ يُفَجِّرُونَهَا تَفْجِيرًا', 'عينا يشرب بها عباد الله يفجرونها تفجيرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 7, 579, 3, 58, 29, 'يُوفُونَ بِالنَّذْرِ وَيَخَافُونَ يَوْمًا كَانَ شَرُّهُ مُسْتَطِيرًا', 'يوفون بالنذر ويخافون يوما كان شره مستطيرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 8, 579, 3, 58, 29, 'وَيُطْعِمُونَ الطَّعَامَ عَلَى حُبِّهِ مِسْكِينًا وَيَتِيمًا وَأَسِيرًا', 'ويطعمون الطعام على حبه مسكينا ويتيما واسيرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 9, 579, 3, 58, 29, 'إِنَّمَا نُطْعِمُكُمْ لِوَجْهِ اللَّهِ لَا نُرِيدُ مِنْكُمْ جَزَاءً وَلَا شُكُورًا', 'انما نطعمكم لوجه الله لا نريد منكم جزاء ولا شكورا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 10, 579, 3, 58, 29, 'إِنَّا نَخَافُ مِنْ رَبِّنَا يَوْمًا عَبُوسًا قَمْطَرِيرًا', 'انا نخاف من ربنا يوما عبوسا قمطريرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 11, 579, 3, 58, 29, 'فَوَقَاهُمُ اللَّهُ شَرَّ ذَلِكَ الْيَوْمِ وَلَقَّاهُمْ نَضْرَةً وَسُرُورًا', 'فوقاهم الله شر ذلك اليوم ولقاهم نضرة وسرورا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 12, 579, 3, 58, 29, 'وَجَزَاهُمْ بِمَا صَبَرُوا جَنَّةً وَحَرِيرًا', 'وجزاهم بما صبروا جنة وحريرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 13, 579, 3, 58, 29, 'مُتَّكِئِينَ فِيهَا عَلَى الْأَرَائِكِ لَا يَرَوْنَ فِيهَا شَمْسًا وَلَا زَمْهَرِيرًا', 'متكئين فيها على الارائك لا يرون فيها شمسا ولا زمهريرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 14, 579, 3, 58, 29, 'وَدَانِيَةً عَلَيْهِمْ ظِلَالُهَا وَذُلِّلَتْ قُطُوفُهَا تَذْلِيلًا', 'ودانية عليهم ظلالها وذللت قطوفها تذليلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 15, 579, 3, 58, 29, 'وَيُطَافُ عَلَيْهِمْ بِآنِيَةٍ مِنْ فِضَّةٍ وَأَكْوَابٍ كَانَتْ قَوَارِيرَا', 'ويطاف عليهم بانية من فضة واكواب كانت قواريرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 16, 579, 3, 58, 29, 'قَوَارِيرَ مِنْ فِضَّةٍ قَدَّرُوهَا تَقْدِيرًا', 'قوارير من فضة قدروها تقديرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 17, 579, 3, 58, 29, 'وَيُسْقَوْنَ فِيهَا كَأْسًا كَانَ مِزَاجُهَا زَنْجَبِيلًا', 'ويسقون فيها كاسا كان مزاجها زنجبيلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 18, 579, 3, 58, 29, 'عَيْنًا فِيهَا تُسَمَّى سَلْسَبِيلًا', 'عينا فيها تسمى سلسبيلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 19, 579, 4, 58, 29, 'وَيَطُوفُ عَلَيْهِمْ وِلْدَانٌ مُخَلَّدُونَ إِذَا رَأَيْتَهُمْ حَسِبْتَهُمْ لُؤْلُؤًا مَنْثُورًا', 'ويطوف عليهم ولدان مخلدون اذا رايتهم حسبتهم لؤلؤا منثورا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 20, 579, 4, 58, 29, 'وَإِذَا رَأَيْتَ ثَمَّ رَأَيْتَ نَعِيمًا وَمُلْكًا كَبِيرًا', 'واذا رايت ثم رايت نعيما وملكا كبيرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 21, 579, 4, 58, 29, 'عَالِيَهُمْ ثِيَابُ سُنْدُسٍ خُضْرٌ وَإِسْتَبْرَقٌ وَحُلُّوا أَسَاوِرَ مِنْ فِضَّةٍ وَسَقَاهُمْ رَبُّهُمْ شَرَابًا طَهُورًا', 'عاليهم ثياب سندس خضر واستبرق وحلوا اساور من فضة وسقاهم ربهم شرابا طهورا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 22, 579, 4, 58, 29, 'إِنَّ هَذَا كَانَ لَكُمْ جَزَاءً وَكَانَ سَعْيُكُمْ مَشْكُورًا', 'ان هذا كان لكم جزاء وكان سعيكم مشكورا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 23, 579, 4, 58, 29, 'إِنَّا نَحْنُ نَزَّلْنَا عَلَيْكَ الْقُرْآنَ تَنْزِيلًا', 'انا نحن نزلنا عليك القران تنزيلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 24, 579, 4, 58, 29, 'فَاصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تُطِعْ مِنْهُمْ آثِمًا أَوْ كَفُورًا', 'فاصبر لحكم ربك ولا تطع منهم اثما او كفورا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 25, 579, 4, 58, 29, 'وَاذْكُرِ اسْمَ رَبِّكَ بُكْرَةً وَأَصِيلًا', 'واذكر اسم ربك بكرة واصيلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 26, 580, 4, 58, 29, 'وَمِنَ اللَّيْلِ فَاسْجُدْ لَهُ وَسَبِّحْهُ لَيْلًا طَوِيلًا', 'ومن الليل فاسجد له وسبحه ليلا طويلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 27, 580, 4, 58, 29, 'إِنَّ هَؤُلَاءِ يُحِبُّونَ الْعَاجِلَةَ وَيَذَرُونَ وَرَاءَهُمْ يَوْمًا ثَقِيلًا', 'ان هؤلاء يحبون العاجلة ويذرون وراءهم يوما ثقيلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 28, 580, 4, 58, 29, 'نَحْنُ خَلَقْنَاهُمْ وَشَدَدْنَا أَسْرَهُمْ وَإِذَا شِئْنَا بَدَّلْنَا أَمْثَالَهُمْ تَبْدِيلًا', 'نحن خلقناهم وشددنا اسرهم واذا شئنا بدلنا امثالهم تبديلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 29, 580, 4, 58, 29, 'إِنَّ هَذِهِ تَذْكِرَةٌ فَمَنْ شَاءَ اتَّخَذَ إِلَى رَبِّهِ سَبِيلًا', 'ان هذه تذكرة فمن شاء اتخذ الى ربه سبيلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 30, 580, 4, 58, 29, 'وَمَا تَشَاءُونَ إِلَّا أَنْ يَشَاءَ اللَّهُ إِنَّ اللَّهَ كَانَ عَلِيمًا حَكِيمًا', 'وما تشاءون الا ان يشاء الله ان الله كان عليما حكيما');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 76, 31, 580, 4, 58, 29, 'يُدْخِلُ مَنْ يَشَاءُ فِي رَحْمَتِهِ وَالظَّالِمِينَ أَعَدَّ لَهُمْ عَذَابًا أَلِيمًا', 'يدخل من يشاء في رحمته والظالمين اعد لهم عذابا اليما');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 1, 580, 4, 58, 29, 'وَالْمُرْسَلَاتِ عُرْفًا', 'والمرسلات عرفا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 2, 580, 4, 58, 29, 'فَالْعَاصِفَاتِ عَصْفًا', 'فالعاصفات عصفا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 3, 580, 4, 58, 29, 'وَالنَّاشِرَاتِ نَشْرًا', 'والناشرات نشرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 4, 580, 4, 58, 29, 'فَالْفَارِقَاتِ فَرْقًا', 'فالفارقات فرقا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 5, 580, 4, 58, 29, 'فَالْمُلْقِيَاتِ ذِكْرًا', 'فالملقيات ذكرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 6, 580, 4, 58, 29, 'عُذْرًا أَوْ نُذْرًا', 'عذرا او نذرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 7, 580, 4, 58, 29, 'إِنَّمَا تُوعَدُونَ لَوَاقِعٌ', 'انما توعدون لواقع');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 8, 580, 4, 58, 29, 'فَإِذَا النُّجُومُ طُمِسَتْ', 'فاذا النجوم طمست');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 9, 580, 4, 58, 29, 'وَإِذَا السَّمَاءُ فُرِجَتْ', 'واذا السماء فرجت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 10, 580, 4, 58, 29, 'وَإِذَا الْجِبَالُ نُسِفَتْ', 'واذا الجبال نسفت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 11, 580, 4, 58, 29, 'وَإِذَا الرُّسُلُ أُقِّتَتْ', 'واذا الرسل اقتت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 12, 580, 4, 58, 29, 'لِأَيِّ يَوْمٍ أُجِّلَتْ', 'لاي يوم اجلت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 13, 580, 4, 58, 29, 'لِيَوْمِ الْفَصْلِ', 'ليوم الفصل');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 14, 580, 4, 58, 29, 'وَمَا أَدْرَاكَ مَا يَوْمُ الْفَصْلِ', 'وما ادراك ما يوم الفصل');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 15, 580, 4, 58, 29, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 'ويل يومئذ للمكذبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 16, 580, 4, 58, 29, 'أَلَمْ نُهْلِكِ الْأَوَّلِينَ', 'الم نهلك الاولين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 17, 580, 4, 58, 29, 'ثُمَّ نُتْبِعُهُمُ الْآخِرِينَ', 'ثم نتبعهم الاخرين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 18, 580, 4, 58, 29, 'كَذَلِكَ نَفْعَلُ بِالْمُجْرِمِينَ', 'كذلك نفعل بالمجرمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 19, 580, 4, 58, 29, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 'ويل يومئذ للمكذبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 20, 581, 4, 58, 29, 'أَلَمْ نَخْلُقْكُمْ مِنْ مَاءٍ مَهِينٍ', 'الم نخلقكم من ماء مهين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 21, 581, 4, 58, 29, 'فَجَعَلْنَاهُ فِي قَرَارٍ مَكِينٍ', 'فجعلناه في قرار مكين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 22, 581, 4, 58, 29, 'إِلَى قَدَرٍ مَعْلُومٍ', 'الى قدر معلوم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 23, 581, 4, 58, 29, 'فَقَدَرْنَا فَنِعْمَ الْقَادِرُونَ', 'فقدرنا فنعم القادرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 24, 581, 4, 58, 29, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 'ويل يومئذ للمكذبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 25, 581, 4, 58, 29, 'أَلَمْ نَجْعَلِ الْأَرْضَ كِفَاتًا', 'الم نجعل الارض كفاتا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 26, 581, 4, 58, 29, 'أَحْيَاءً وَأَمْوَاتًا', 'احياء وامواتا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 27, 581, 4, 58, 29, 'وَجَعَلْنَا فِيهَا رَوَاسِيَ شَامِخَاتٍ وَأَسْقَيْنَاكُمْ مَاءً فُرَاتًا', 'وجعلنا فيها رواسي شامخات واسقيناكم ماء فراتا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 28, 581, 4, 58, 29, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 'ويل يومئذ للمكذبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 29, 581, 4, 58, 29, 'انْطَلِقُوا إِلَى مَا كُنْتُمْ بِهِ تُكَذِّبُونَ', 'انطلقوا الى ما كنتم به تكذبون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 30, 581, 4, 58, 29, 'انْطَلِقُوا إِلَى ظِلٍّ ذِي ثَلَاثِ شُعَبٍ', 'انطلقوا الى ظل ذي ثلاث شعب');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 31, 581, 4, 58, 29, 'لَا ظَلِيلٍ وَلَا يُغْنِي مِنَ اللَّهَبِ', 'لا ظليل ولا يغني من اللهب');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 32, 581, 4, 58, 29, 'إِنَّهَا تَرْمِي بِشَرَرٍ كَالْقَصْرِ', 'انها ترمي بشرر كالقصر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 33, 581, 4, 58, 29, 'كَأَنَّهُ جِمَالَتٌ صُفْرٌ', 'كانه جمالت صفر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 34, 581, 4, 58, 29, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 'ويل يومئذ للمكذبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 35, 581, 4, 58, 29, 'هَذَا يَوْمُ لَا يَنْطِقُونَ', 'هذا يوم لا ينطقون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 36, 581, 4, 58, 29, 'وَلَا يُؤْذَنُ لَهُمْ فَيَعْتَذِرُونَ', 'ولا يؤذن لهم فيعتذرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 37, 581, 4, 58, 29, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 'ويل يومئذ للمكذبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 38, 581, 4, 58, 29, 'هَذَا يَوْمُ الْفَصْلِ جَمَعْنَاكُمْ وَالْأَوَّلِينَ', 'هذا يوم الفصل جمعناكم والاولين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 39, 581, 4, 58, 29, 'فَإِنْ كَانَ لَكُمْ كَيْدٌ فَكِيدُونِ', 'فان كان لكم كيد فكيدون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 40, 581, 4, 58, 29, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 'ويل يومئذ للمكذبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 41, 581, 4, 58, 29, 'إِنَّ الْمُتَّقِينَ فِي ظِلَالٍ وَعُيُونٍ', 'ان المتقين في ظلال وعيون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 42, 581, 4, 58, 29, 'وَفَوَاكِهَ مِمَّا يَشْتَهُونَ', 'وفواكه مما يشتهون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 43, 581, 4, 58, 29, 'كُلُوا وَاشْرَبُوا هَنِيئًا بِمَا كُنْتُمْ تَعْمَلُونَ', 'كلوا واشربوا هنيئا بما كنتم تعملون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 44, 581, 4, 58, 29, 'إِنَّا كَذَلِكَ نَجْزِي الْمُحْسِنِينَ', 'انا كذلك نجزي المحسنين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 45, 581, 4, 58, 29, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 'ويل يومئذ للمكذبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 46, 581, 4, 58, 29, 'كُلُوا وَتَمَتَّعُوا قَلِيلًا إِنَّكُمْ مُجْرِمُونَ', 'كلوا وتمتعوا قليلا انكم مجرمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 47, 581, 4, 58, 29, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 'ويل يومئذ للمكذبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 48, 581, 4, 58, 29, 'وَإِذَا قِيلَ لَهُمُ ارْكَعُوا لَا يَرْكَعُونَ', 'واذا قيل لهم اركعوا لا يركعون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 49, 581, 4, 58, 29, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 'ويل يومئذ للمكذبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 77, 50, 581, 4, 58, 29, 'فَبِأَيِّ حَدِيثٍ بَعْدَهُ يُؤْمِنُونَ', 'فباي حديث بعده يؤمنون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 1, 582, 1, 59, 30, 'عَمَّ يَتَسَاءَلُونَ', 'عم يتساءلون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 2, 582, 1, 59, 30, 'عَنِ النَّبَإِ الْعَظِيمِ', 'عن النبا العظيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 3, 582, 1, 59, 30, 'الَّذِي هُمْ فِيهِ مُخْتَلِفُونَ', 'الذي هم فيه مختلفون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 4, 582, 1, 59, 30, 'كَلَّا سَيَعْلَمُونَ', 'كلا سيعلمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 5, 582, 1, 59, 30, 'ثُمَّ كَلَّا سَيَعْلَمُونَ', 'ثم كلا سيعلمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 6, 582, 1, 59, 30, 'أَلَمْ نَجْعَلِ الْأَرْضَ مِهَادًا', 'الم نجعل الارض مهادا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 7, 582, 1, 59, 30, 'وَالْجِبَالَ أَوْتَادًا', 'والجبال اوتادا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 8, 582, 1, 59, 30, 'وَخَلَقْنَاكُمْ أَزْوَاجًا', 'وخلقناكم ازواجا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 9, 582, 1, 59, 30, 'وَجَعَلْنَا نَوْمَكُمْ سُبَاتًا', 'وجعلنا نومكم سباتا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 10, 582, 1, 59, 30, 'وَجَعَلْنَا اللَّيْلَ لِبَاسًا', 'وجعلنا الليل لباسا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 11, 582, 1, 59, 30, 'وَجَعَلْنَا النَّهَارَ مَعَاشًا', 'وجعلنا النهار معاشا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 12, 582, 1, 59, 30, 'وَبَنَيْنَا فَوْقَكُمْ سَبْعًا شِدَادًا', 'وبنينا فوقكم سبعا شدادا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 13, 582, 1, 59, 30, 'وَجَعَلْنَا سِرَاجًا وَهَّاجًا', 'وجعلنا سراجا وهاجا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 14, 582, 1, 59, 30, 'وَأَنْزَلْنَا مِنَ الْمُعْصِرَاتِ مَاءً ثَجَّاجًا', 'وانزلنا من المعصرات ماء ثجاجا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 15, 582, 1, 59, 30, 'لِنُخْرِجَ بِهِ حَبًّا وَنَبَاتًا', 'لنخرج به حبا ونباتا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 16, 582, 1, 59, 30, 'وَجَنَّاتٍ أَلْفَافًا', 'وجنات الفافا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 17, 582, 1, 59, 30, 'إِنَّ يَوْمَ الْفَصْلِ كَانَ مِيقَاتًا', 'ان يوم الفصل كان ميقاتا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 18, 582, 1, 59, 30, 'يَوْمَ يُنْفَخُ فِي الصُّورِ فَتَأْتُونَ أَفْوَاجًا', 'يوم ينفخ في الصور فتاتون افواجا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 19, 582, 1, 59, 30, 'وَفُتِحَتِ السَّمَاءُ فَكَانَتْ أَبْوَابًا', 'وفتحت السماء فكانت ابوابا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 20, 582, 1, 59, 30, 'وَسُيِّرَتِ الْجِبَالُ فَكَانَتْ سَرَابًا', 'وسيرت الجبال فكانت سرابا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 21, 582, 1, 59, 30, 'إِنَّ جَهَنَّمَ كَانَتْ مِرْصَادًا', 'ان جهنم كانت مرصادا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 22, 582, 1, 59, 30, 'لِلطَّاغِينَ مَآبًا', 'للطاغين مابا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 23, 582, 1, 59, 30, 'لَابِثِينَ فِيهَا أَحْقَابًا', 'لابثين فيها احقابا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 24, 582, 1, 59, 30, 'لَا يَذُوقُونَ فِيهَا بَرْدًا وَلَا شَرَابًا', 'لا يذوقون فيها بردا ولا شرابا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 25, 582, 1, 59, 30, 'إِلَّا حَمِيمًا وَغَسَّاقًا', 'الا حميما وغساقا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 26, 582, 1, 59, 30, 'جَزَاءً وِفَاقًا', 'جزاء وفاقا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 27, 582, 1, 59, 30, 'إِنَّهُمْ كَانُوا لَا يَرْجُونَ حِسَابًا', 'انهم كانوا لا يرجون حسابا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 28, 582, 1, 59, 30, 'وَكَذَّبُوا بِآيَاتِنَا كِذَّابًا', 'وكذبوا باياتنا كذابا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 29, 582, 1, 59, 30, 'وَكُلَّ شَيْءٍ أَحْصَيْنَاهُ كِتَابًا', 'وكل شيء احصيناه كتابا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 30, 582, 1, 59, 30, 'فَذُوقُوا فَلَنْ نَزِيدَكُمْ إِلَّا عَذَابًا', 'فذوقوا فلن نزيدكم الا عذابا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 31, 583, 1, 59, 30, 'إِنَّ لِلْمُتَّقِينَ مَفَازًا', 'ان للمتقين مفازا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 32, 583, 1, 59, 30, 'حَدَائِقَ وَأَعْنَابًا', 'حدائق واعنابا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 33, 583, 1, 59, 30, 'وَكَوَاعِبَ أَتْرَابًا', 'وكواعب اترابا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 34, 583, 1, 59, 30, 'وَكَأْسًا دِهَاقًا', 'وكاسا دهاقا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 35, 583, 1, 59, 30, 'لَا يَسْمَعُونَ فِيهَا لَغْوًا وَلَا كِذَّابًا', 'لا يسمعون فيها لغوا ولا كذابا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 36, 583, 1, 59, 30, 'جَزَاءً مِنْ رَبِّكَ عَطَاءً حِسَابًا', 'جزاء من ربك عطاء حسابا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 37, 583, 1, 59, 30, 'رَبِّ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا الرَّحْمَنِ لَا يَمْلِكُونَ مِنْهُ خِطَابًا', 'رب السماوات والارض وما بينهما الرحمن لا يملكون منه خطابا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 38, 583, 1, 59, 30, 'يَوْمَ يَقُومُ الرُّوحُ وَالْمَلَائِكَةُ صَفًّا لَا يَتَكَلَّمُونَ إِلَّا مَنْ أَذِنَ لَهُ الرَّحْمَنُ وَقَالَ صَوَابًا', 'يوم يقوم الروح والملائكة صفا لا يتكلمون الا من اذن له الرحمن وقال صوابا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 39, 583, 1, 59, 30, 'ذَلِكَ الْيَوْمُ الْحَقُّ فَمَنْ شَاءَ اتَّخَذَ إِلَى رَبِّهِ مَآبًا', 'ذلك اليوم الحق فمن شاء اتخذ الى ربه مابا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 78, 40, 583, 1, 59, 30, 'إِنَّا أَنْذَرْنَاكُمْ عَذَابًا قَرِيبًا يَوْمَ يَنْظُرُ الْمَرْءُ مَا قَدَّمَتْ يَدَاهُ وَيَقُولُ الْكَافِرُ يَا لَيْتَنِي كُنْتُ تُرَابًا', 'انا انذرناكم عذابا قريبا يوم ينظر المرء ما قدمت يداه ويقول الكافر يا ليتني كنت ترابا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 1, 583, 1, 59, 30, 'وَالنَّازِعَاتِ غَرْقًا', 'والنازعات غرقا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 2, 583, 1, 59, 30, 'وَالنَّاشِطَاتِ نَشْطًا', 'والناشطات نشطا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 3, 583, 1, 59, 30, 'وَالسَّابِحَاتِ سَبْحًا', 'والسابحات سبحا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 4, 583, 1, 59, 30, 'فَالسَّابِقَاتِ سَبْقًا', 'فالسابقات سبقا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 5, 583, 1, 59, 30, 'فَالْمُدَبِّرَاتِ أَمْرًا', 'فالمدبرات امرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 6, 583, 1, 59, 30, 'يَوْمَ تَرْجُفُ الرَّاجِفَةُ', 'يوم ترجف الراجفة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 7, 583, 1, 59, 30, 'تَتْبَعُهَا الرَّادِفَةُ', 'تتبعها الرادفة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 8, 583, 1, 59, 30, 'قُلُوبٌ يَوْمَئِذٍ وَاجِفَةٌ', 'قلوب يومئذ واجفة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 9, 583, 1, 59, 30, 'أَبْصَارُهَا خَاشِعَةٌ', 'ابصارها خاشعة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 10, 583, 1, 59, 30, 'يَقُولُونَ أَإِنَّا لَمَرْدُودُونَ فِي الْحَافِرَةِ', 'يقولون اانا لمردودون في الحافرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 11, 583, 1, 59, 30, 'أَإِذَا كُنَّا عِظَامًا نَخِرَةً', 'ااذا كنا عظاما نخرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 12, 583, 1, 59, 30, 'قَالُوا تِلْكَ إِذًا كَرَّةٌ خَاسِرَةٌ', 'قالوا تلك اذا كرة خاسرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 13, 583, 1, 59, 30, 'فَإِنَّمَا هِيَ زَجْرَةٌ وَاحِدَةٌ', 'فانما هي زجرة واحدة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 14, 583, 1, 59, 30, 'فَإِذَا هُمْ بِالسَّاهِرَةِ', 'فاذا هم بالساهرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 15, 583, 1, 59, 30, 'هَلْ أَتَاكَ حَدِيثُ مُوسَى', 'هل اتاك حديث موسى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 16, 584, 1, 59, 30, 'إِذْ نَادَاهُ رَبُّهُ بِالْوَادِ الْمُقَدَّسِ طُوًى', 'اذ ناداه ربه بالواد المقدس طوى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 17, 584, 1, 59, 30, 'اذْهَبْ إِلَى فِرْعَوْنَ إِنَّهُ طَغَى', 'اذهب الى فرعون انه طغى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 18, 584, 1, 59, 30, 'فَقُلْ هَلْ لَكَ إِلَى أَنْ تَزَكَّى', 'فقل هل لك الى ان تزكى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 19, 584, 1, 59, 30, 'وَأَهْدِيَكَ إِلَى رَبِّكَ فَتَخْشَى', 'واهديك الى ربك فتخشى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 20, 584, 1, 59, 30, 'فَأَرَاهُ الْآيَةَ الْكُبْرَى', 'فاراه الاية الكبرى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 21, 584, 1, 59, 30, 'فَكَذَّبَ وَعَصَى', 'فكذب وعصى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 22, 584, 1, 59, 30, 'ثُمَّ أَدْبَرَ يَسْعَى', 'ثم ادبر يسعى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 23, 584, 1, 59, 30, 'فَحَشَرَ فَنَادَى', 'فحشر فنادى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 24, 584, 1, 59, 30, 'فَقَالَ أَنَا رَبُّكُمُ الْأَعْلَى', 'فقال انا ربكم الاعلى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 25, 584, 1, 59, 30, 'فَأَخَذَهُ اللَّهُ نَكَالَ الْآخِرَةِ وَالْأُولَى', 'فاخذه الله نكال الاخرة والاولى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 26, 584, 1, 59, 30, 'إِنَّ فِي ذَلِكَ لَعِبْرَةً لِمَنْ يَخْشَى', 'ان في ذلك لعبرة لمن يخشى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 27, 584, 1, 59, 30, 'أَأَنْتُمْ أَشَدُّ خَلْقًا أَمِ السَّمَاءُ بَنَاهَا', 'اانتم اشد خلقا ام السماء بناها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 28, 584, 1, 59, 30, 'رَفَعَ سَمْكَهَا فَسَوَّاهَا', 'رفع سمكها فسواها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 29, 584, 1, 59, 30, 'وَأَغْطَشَ لَيْلَهَا وَأَخْرَجَ ضُحَاهَا', 'واغطش ليلها واخرج ضحاها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 30, 584, 1, 59, 30, 'وَالْأَرْضَ بَعْدَ ذَلِكَ دَحَاهَا', 'والارض بعد ذلك دحاها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 31, 584, 1, 59, 30, 'أَخْرَجَ مِنْهَا مَاءَهَا وَمَرْعَاهَا', 'اخرج منها ماءها ومرعاها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 32, 584, 1, 59, 30, 'وَالْجِبَالَ أَرْسَاهَا', 'والجبال ارساها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 33, 584, 1, 59, 30, 'مَتَاعًا لَكُمْ وَلِأَنْعَامِكُمْ', 'متاعا لكم ولانعامكم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 34, 584, 1, 59, 30, 'فَإِذَا جَاءَتِ الطَّامَّةُ الْكُبْرَى', 'فاذا جاءت الطامة الكبرى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 35, 584, 1, 59, 30, 'يَوْمَ يَتَذَكَّرُ الْإِنْسَانُ مَا سَعَى', 'يوم يتذكر الانسان ما سعى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 36, 584, 1, 59, 30, 'وَبُرِّزَتِ الْجَحِيمُ لِمَنْ يَرَى', 'وبرزت الجحيم لمن يرى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 37, 584, 1, 59, 30, 'فَأَمَّا مَنْ طَغَى', 'فاما من طغى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 38, 584, 1, 59, 30, 'وَآثَرَ الْحَيَاةَ الدُّنْيَا', 'واثر الحياة الدنيا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 39, 584, 1, 59, 30, 'فَإِنَّ الْجَحِيمَ هِيَ الْمَأْوَى', 'فان الجحيم هي الماوى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 40, 584, 1, 59, 30, 'وَأَمَّا مَنْ خَافَ مَقَامَ رَبِّهِ وَنَهَى النَّفْسَ عَنِ الْهَوَى', 'واما من خاف مقام ربه ونهى النفس عن الهوى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 41, 584, 1, 59, 30, 'فَإِنَّ الْجَنَّةَ هِيَ الْمَأْوَى', 'فان الجنة هي الماوى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 42, 584, 1, 59, 30, 'يَسْأَلُونَكَ عَنِ السَّاعَةِ أَيَّانَ مُرْسَاهَا', 'يسالونك عن الساعة ايان مرساها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 43, 584, 1, 59, 30, 'فِيمَ أَنْتَ مِنْ ذِكْرَاهَا', 'فيم انت من ذكراها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 44, 584, 1, 59, 30, 'إِلَى رَبِّكَ مُنْتَهَاهَا', 'الى ربك منتهاها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 45, 584, 1, 59, 30, 'إِنَّمَا أَنْتَ مُنْذِرُ مَنْ يَخْشَاهَا', 'انما انت منذر من يخشاها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 79, 46, 584, 1, 59, 30, 'كَأَنَّهُمْ يَوْمَ يَرَوْنَهَا لَمْ يَلْبَثُوا إِلَّا عَشِيَّةً أَوْ ضُحَاهَا', 'كانهم يوم يرونها لم يلبثوا الا عشية او ضحاها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 1, 585, 2, 59, 30, 'عَبَسَ وَتَوَلَّى', 'عبس وتولى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 2, 585, 2, 59, 30, 'أَنْ جَاءَهُ الْأَعْمَى', 'ان جاءه الاعمى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 3, 585, 2, 59, 30, 'وَمَا يُدْرِيكَ لَعَلَّهُ يَزَّكَّى', 'وما يدريك لعله يزكى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 4, 585, 2, 59, 30, 'أَوْ يَذَّكَّرُ فَتَنْفَعَهُ الذِّكْرَى', 'او يذكر فتنفعه الذكرى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 5, 585, 2, 59, 30, 'أَمَّا مَنِ اسْتَغْنَى', 'اما من استغنى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 6, 585, 2, 59, 30, 'فَأَنْتَ لَهُ تَصَدَّى', 'فانت له تصدى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 7, 585, 2, 59, 30, 'وَمَا عَلَيْكَ أَلَّا يَزَّكَّى', 'وما عليك الا يزكى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 8, 585, 2, 59, 30, 'وَأَمَّا مَنْ جَاءَكَ يَسْعَى', 'واما من جاءك يسعى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 9, 585, 2, 59, 30, 'وَهُوَ يَخْشَى', 'وهو يخشى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 10, 585, 2, 59, 30, 'فَأَنْتَ عَنْهُ تَلَهَّى', 'فانت عنه تلهى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 11, 585, 2, 59, 30, 'كَلَّا إِنَّهَا تَذْكِرَةٌ', 'كلا انها تذكرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 12, 585, 2, 59, 30, 'فَمَنْ شَاءَ ذَكَرَهُ', 'فمن شاء ذكره');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 13, 585, 2, 59, 30, 'فِي صُحُفٍ مُكَرَّمَةٍ', 'في صحف مكرمة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 14, 585, 2, 59, 30, 'مَرْفُوعَةٍ مُطَهَّرَةٍ', 'مرفوعة مطهرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 15, 585, 2, 59, 30, 'بِأَيْدِي سَفَرَةٍ', 'بايدي سفرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 16, 585, 2, 59, 30, 'كِرَامٍ بَرَرَةٍ', 'كرام بررة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 17, 585, 2, 59, 30, 'قُتِلَ الْإِنْسَانُ مَا أَكْفَرَهُ', 'قتل الانسان ما اكفره');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 18, 585, 2, 59, 30, 'مِنْ أَيِّ شَيْءٍ خَلَقَهُ', 'من اي شيء خلقه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 19, 585, 2, 59, 30, 'مِنْ نُطْفَةٍ خَلَقَهُ فَقَدَّرَهُ', 'من نطفة خلقه فقدره');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 20, 585, 2, 59, 30, 'ثُمَّ السَّبِيلَ يَسَّرَهُ', 'ثم السبيل يسره');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 21, 585, 2, 59, 30, 'ثُمَّ أَمَاتَهُ فَأَقْبَرَهُ', 'ثم اماته فاقبره');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 22, 585, 2, 59, 30, 'ثُمَّ إِذَا شَاءَ أَنْشَرَهُ', 'ثم اذا شاء انشره');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 23, 585, 2, 59, 30, 'كَلَّا لَمَّا يَقْضِ مَا أَمَرَهُ', 'كلا لما يقض ما امره');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 24, 585, 2, 59, 30, 'فَلْيَنْظُرِ الْإِنْسَانُ إِلَى طَعَامِهِ', 'فلينظر الانسان الى طعامه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 25, 585, 2, 59, 30, 'أَنَّا صَبَبْنَا الْمَاءَ صَبًّا', 'انا صببنا الماء صبا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 26, 585, 2, 59, 30, 'ثُمَّ شَقَقْنَا الْأَرْضَ شَقًّا', 'ثم شققنا الارض شقا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 27, 585, 2, 59, 30, 'فَأَنْبَتْنَا فِيهَا حَبًّا', 'فانبتنا فيها حبا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 28, 585, 2, 59, 30, 'وَعِنَبًا وَقَضْبًا', 'وعنبا وقضبا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 29, 585, 2, 59, 30, 'وَزَيْتُونًا وَنَخْلًا', 'وزيتونا ونخلا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 30, 585, 2, 59, 30, 'وَحَدَائِقَ غُلْبًا', 'وحدائق غلبا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 31, 585, 2, 59, 30, 'وَفَاكِهَةً وَأَبًّا', 'وفاكهة وابا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 32, 585, 2, 59, 30, 'مَتَاعًا لَكُمْ وَلِأَنْعَامِكُمْ', 'متاعا لكم ولانعامكم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 33, 585, 2, 59, 30, 'فَإِذَا جَاءَتِ الصَّاخَّةُ', 'فاذا جاءت الصاخة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 34, 585, 2, 59, 30, 'يَوْمَ يَفِرُّ الْمَرْءُ مِنْ أَخِيهِ', 'يوم يفر المرء من اخيه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 35, 585, 2, 59, 30, 'وَأُمِّهِ وَأَبِيهِ', 'وامه وابيه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 36, 585, 2, 59, 30, 'وَصَاحِبَتِهِ وَبَنِيهِ', 'وصاحبته وبنيه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 37, 585, 2, 59, 30, 'لِكُلِّ امْرِئٍ مِنْهُمْ يَوْمَئِذٍ شَأْنٌ يُغْنِيهِ', 'لكل امرئ منهم يومئذ شان يغنيه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 38, 585, 2, 59, 30, 'وُجُوهٌ يَوْمَئِذٍ مُسْفِرَةٌ', 'وجوه يومئذ مسفرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 39, 585, 2, 59, 30, 'ضَاحِكَةٌ مُسْتَبْشِرَةٌ', 'ضاحكة مستبشرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 40, 585, 2, 59, 30, 'وَوُجُوهٌ يَوْمَئِذٍ عَلَيْهَا غَبَرَةٌ', 'ووجوه يومئذ عليها غبرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 41, 585, 2, 59, 30, 'تَرْهَقُهَا قَتَرَةٌ', 'ترهقها قترة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 80, 42, 585, 2, 59, 30, 'أُولَئِكَ هُمُ الْكَفَرَةُ الْفَجَرَةُ', 'اولئك هم الكفرة الفجرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 1, 586, 2, 59, 30, 'إِذَا الشَّمْسُ كُوِّرَتْ', 'اذا الشمس كورت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 2, 586, 2, 59, 30, 'وَإِذَا النُّجُومُ انْكَدَرَتْ', 'واذا النجوم انكدرت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 3, 586, 2, 59, 30, 'وَإِذَا الْجِبَالُ سُيِّرَتْ', 'واذا الجبال سيرت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 4, 586, 2, 59, 30, 'وَإِذَا الْعِشَارُ عُطِّلَتْ', 'واذا العشار عطلت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 5, 586, 2, 59, 30, 'وَإِذَا الْوُحُوشُ حُشِرَتْ', 'واذا الوحوش حشرت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 6, 586, 2, 59, 30, 'وَإِذَا الْبِحَارُ سُجِّرَتْ', 'واذا البحار سجرت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 7, 586, 2, 59, 30, 'وَإِذَا النُّفُوسُ زُوِّجَتْ', 'واذا النفوس زوجت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 8, 586, 2, 59, 30, 'وَإِذَا الْمَوْءُودَةُ سُئِلَتْ', 'واذا الموءودة سئلت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 9, 586, 2, 59, 30, 'بِأَيِّ ذَنْبٍ قُتِلَتْ', 'باي ذنب قتلت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 10, 586, 2, 59, 30, 'وَإِذَا الصُّحُفُ نُشِرَتْ', 'واذا الصحف نشرت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 11, 586, 2, 59, 30, 'وَإِذَا السَّمَاءُ كُشِطَتْ', 'واذا السماء كشطت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 12, 586, 2, 59, 30, 'وَإِذَا الْجَحِيمُ سُعِّرَتْ', 'واذا الجحيم سعرت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 13, 586, 2, 59, 30, 'وَإِذَا الْجَنَّةُ أُزْلِفَتْ', 'واذا الجنة ازلفت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 14, 586, 2, 59, 30, 'عَلِمَتْ نَفْسٌ مَا أَحْضَرَتْ', 'علمت نفس ما احضرت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 15, 586, 2, 59, 30, 'فَلَا أُقْسِمُ بِالْخُنَّسِ', 'فلا اقسم بالخنس');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 16, 586, 2, 59, 30, 'الْجَوَارِ الْكُنَّسِ', 'الجوار الكنس');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 17, 586, 2, 59, 30, 'وَاللَّيْلِ إِذَا عَسْعَسَ', 'والليل اذا عسعس');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 18, 586, 2, 59, 30, 'وَالصُّبْحِ إِذَا تَنَفَّسَ', 'والصبح اذا تنفس');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 19, 586, 2, 59, 30, 'إِنَّهُ لَقَوْلُ رَسُولٍ كَرِيمٍ', 'انه لقول رسول كريم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 20, 586, 2, 59, 30, 'ذِي قُوَّةٍ عِنْدَ ذِي الْعَرْشِ مَكِينٍ', 'ذي قوة عند ذي العرش مكين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 21, 586, 2, 59, 30, 'مُطَاعٍ ثَمَّ أَمِينٍ', 'مطاع ثم امين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 22, 586, 2, 59, 30, 'وَمَا صَاحِبُكُمْ بِمَجْنُونٍ', 'وما صاحبكم بمجنون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 23, 586, 2, 59, 30, 'وَلَقَدْ رَآهُ بِالْأُفُقِ الْمُبِينِ', 'ولقد راه بالافق المبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 24, 586, 2, 59, 30, 'وَمَا هُوَ عَلَى الْغَيْبِ بِضَنِينٍ', 'وما هو على الغيب بضنين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 25, 586, 2, 59, 30, 'وَمَا هُوَ بِقَوْلِ شَيْطَانٍ رَجِيمٍ', 'وما هو بقول شيطان رجيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 26, 586, 2, 59, 30, 'فَأَيْنَ تَذْهَبُونَ', 'فاين تذهبون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 27, 586, 2, 59, 30, 'إِنْ هُوَ إِلَّا ذِكْرٌ لِلْعَالَمِينَ', 'ان هو الا ذكر للعالمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 28, 586, 2, 59, 30, 'لِمَنْ شَاءَ مِنْكُمْ أَنْ يَسْتَقِيمَ', 'لمن شاء منكم ان يستقيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 81, 29, 586, 2, 59, 30, 'وَمَا تَشَاءُونَ إِلَّا أَنْ يَشَاءَ اللَّهُ رَبُّ الْعَالَمِينَ', 'وما تشاءون الا ان يشاء الله رب العالمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 82, 1, 587, 3, 59, 30, 'إِذَا السَّمَاءُ انْفَطَرَتْ', 'اذا السماء انفطرت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 82, 2, 587, 3, 59, 30, 'وَإِذَا الْكَوَاكِبُ انْتَثَرَتْ', 'واذا الكواكب انتثرت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 82, 3, 587, 3, 59, 30, 'وَإِذَا الْبِحَارُ فُجِّرَتْ', 'واذا البحار فجرت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 82, 4, 587, 3, 59, 30, 'وَإِذَا الْقُبُورُ بُعْثِرَتْ', 'واذا القبور بعثرت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 82, 5, 587, 3, 59, 30, 'عَلِمَتْ نَفْسٌ مَا قَدَّمَتْ وَأَخَّرَتْ', 'علمت نفس ما قدمت واخرت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 82, 6, 587, 3, 59, 30, 'يَا أَيُّهَا الْإِنْسَانُ مَا غَرَّكَ بِرَبِّكَ الْكَرِيمِ', 'يا ايها الانسان ما غرك بربك الكريم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 82, 7, 587, 3, 59, 30, 'الَّذِي خَلَقَكَ فَسَوَّاكَ فَعَدَلَكَ', 'الذي خلقك فسواك فعدلك');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 82, 8, 587, 3, 59, 30, 'فِي أَيِّ صُورَةٍ مَا شَاءَ رَكَّبَكَ', 'في اي صورة ما شاء ركبك');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 82, 9, 587, 3, 59, 30, 'كَلَّا بَلْ تُكَذِّبُونَ بِالدِّينِ', 'كلا بل تكذبون بالدين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 82, 10, 587, 3, 59, 30, 'وَإِنَّ عَلَيْكُمْ لَحَافِظِينَ', 'وان عليكم لحافظين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 82, 11, 587, 3, 59, 30, 'كِرَامًا كَاتِبِينَ', 'كراما كاتبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 82, 12, 587, 3, 59, 30, 'يَعْلَمُونَ مَا تَفْعَلُونَ', 'يعلمون ما تفعلون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 82, 13, 587, 3, 59, 30, 'إِنَّ الْأَبْرَارَ لَفِي نَعِيمٍ', 'ان الابرار لفي نعيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 82, 14, 587, 3, 59, 30, 'وَإِنَّ الْفُجَّارَ لَفِي جَحِيمٍ', 'وان الفجار لفي جحيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 82, 15, 587, 3, 59, 30, 'يَصْلَوْنَهَا يَوْمَ الدِّينِ', 'يصلونها يوم الدين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 82, 16, 587, 3, 59, 30, 'وَمَا هُمْ عَنْهَا بِغَائِبِينَ', 'وما هم عنها بغائبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 82, 17, 587, 3, 59, 30, 'وَمَا أَدْرَاكَ مَا يَوْمُ الدِّينِ', 'وما ادراك ما يوم الدين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 82, 18, 587, 3, 59, 30, 'ثُمَّ مَا أَدْرَاكَ مَا يَوْمُ الدِّينِ', 'ثم ما ادراك ما يوم الدين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 82, 19, 587, 3, 59, 30, 'يَوْمَ لَا تَمْلِكُ نَفْسٌ لِنَفْسٍ شَيْئًا وَالْأَمْرُ يَوْمَئِذٍ لِلَّهِ', 'يوم لا تملك نفس لنفس شيئا والامر يومئذ لله');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 1, 587, 3, 59, 30, 'وَيْلٌ لِلْمُطَفِّفِينَ', 'ويل للمطففين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 2, 587, 3, 59, 30, 'الَّذِينَ إِذَا اكْتَالُوا عَلَى النَّاسِ يَسْتَوْفُونَ', 'الذين اذا اكتالوا على الناس يستوفون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 3, 587, 3, 59, 30, 'وَإِذَا كَالُوهُمْ أَوْ وَزَنُوهُمْ يُخْسِرُونَ', 'واذا كالوهم او وزنوهم يخسرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 4, 587, 3, 59, 30, 'أَلَا يَظُنُّ أُولَئِكَ أَنَّهُمْ مَبْعُوثُونَ', 'الا يظن اولئك انهم مبعوثون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 5, 587, 3, 59, 30, 'لِيَوْمٍ عَظِيمٍ', 'ليوم عظيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 6, 587, 3, 59, 30, 'يَوْمَ يَقُومُ النَّاسُ لِرَبِّ الْعَالَمِينَ', 'يوم يقوم الناس لرب العالمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 7, 588, 3, 59, 30, 'كَلَّا إِنَّ كِتَابَ الْفُجَّارِ لَفِي سِجِّينٍ', 'كلا ان كتاب الفجار لفي سجين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 8, 588, 3, 59, 30, 'وَمَا أَدْرَاكَ مَا سِجِّينٌ', 'وما ادراك ما سجين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 9, 588, 3, 59, 30, 'كِتَابٌ مَرْقُومٌ', 'كتاب مرقوم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 10, 588, 3, 59, 30, 'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ', 'ويل يومئذ للمكذبين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 11, 588, 3, 59, 30, 'الَّذِينَ يُكَذِّبُونَ بِيَوْمِ الدِّينِ', 'الذين يكذبون بيوم الدين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 12, 588, 3, 59, 30, 'وَمَا يُكَذِّبُ بِهِ إِلَّا كُلُّ مُعْتَدٍ أَثِيمٍ', 'وما يكذب به الا كل معتد اثيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 13, 588, 3, 59, 30, 'إِذَا تُتْلَى عَلَيْهِ آيَاتُنَا قَالَ أَسَاطِيرُ الْأَوَّلِينَ', 'اذا تتلى عليه اياتنا قال اساطير الاولين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 14, 588, 3, 59, 30, 'كَلَّا بَلْ رَانَ عَلَى قُلُوبِهِمْ مَا كَانُوا يَكْسِبُونَ', 'كلا بل ران على قلوبهم ما كانوا يكسبون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 15, 588, 3, 59, 30, 'كَلَّا إِنَّهُمْ عَنْ رَبِّهِمْ يَوْمَئِذٍ لَمَحْجُوبُونَ', 'كلا انهم عن ربهم يومئذ لمحجوبون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 16, 588, 3, 59, 30, 'ثُمَّ إِنَّهُمْ لَصَالُو الْجَحِيمِ', 'ثم انهم لصالو الجحيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 17, 588, 3, 59, 30, 'ثُمَّ يُقَالُ هَذَا الَّذِي كُنْتُمْ بِهِ تُكَذِّبُونَ', 'ثم يقال هذا الذي كنتم به تكذبون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 18, 588, 3, 59, 30, 'كَلَّا إِنَّ كِتَابَ الْأَبْرَارِ لَفِي عِلِّيِّينَ', 'كلا ان كتاب الابرار لفي عليين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 19, 588, 3, 59, 30, 'وَمَا أَدْرَاكَ مَا عِلِّيُّونَ', 'وما ادراك ما عليون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 20, 588, 3, 59, 30, 'كِتَابٌ مَرْقُومٌ', 'كتاب مرقوم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 21, 588, 3, 59, 30, 'يَشْهَدُهُ الْمُقَرَّبُونَ', 'يشهده المقربون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 22, 588, 3, 59, 30, 'إِنَّ الْأَبْرَارَ لَفِي نَعِيمٍ', 'ان الابرار لفي نعيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 23, 588, 3, 59, 30, 'عَلَى الْأَرَائِكِ يَنْظُرُونَ', 'على الارائك ينظرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 24, 588, 3, 59, 30, 'تَعْرِفُ فِي وُجُوهِهِمْ نَضْرَةَ النَّعِيمِ', 'تعرف في وجوههم نضرة النعيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 25, 588, 3, 59, 30, 'يُسْقَوْنَ مِنْ رَحِيقٍ مَخْتُومٍ', 'يسقون من رحيق مختوم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 26, 588, 3, 59, 30, 'خِتَامُهُ مِسْكٌ وَفِي ذَلِكَ فَلْيَتَنَافَسِ الْمُتَنَافِسُونَ', 'ختامه مسك وفي ذلك فليتنافس المتنافسون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 27, 588, 3, 59, 30, 'وَمِزَاجُهُ مِنْ تَسْنِيمٍ', 'ومزاجه من تسنيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 28, 588, 3, 59, 30, 'عَيْنًا يَشْرَبُ بِهَا الْمُقَرَّبُونَ', 'عينا يشرب بها المقربون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 29, 588, 3, 59, 30, 'إِنَّ الَّذِينَ أَجْرَمُوا كَانُوا مِنَ الَّذِينَ آمَنُوا يَضْحَكُونَ', 'ان الذين اجرموا كانوا من الذين امنوا يضحكون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 30, 588, 3, 59, 30, 'وَإِذَا مَرُّوا بِهِمْ يَتَغَامَزُونَ', 'واذا مروا بهم يتغامزون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 31, 588, 3, 59, 30, 'وَإِذَا انْقَلَبُوا إِلَى أَهْلِهِمُ انْقَلَبُوا فَكِهِينَ', 'واذا انقلبوا الى اهلهم انقلبوا فكهين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 32, 588, 3, 59, 30, 'وَإِذَا رَأَوْهُمْ قَالُوا إِنَّ هَؤُلَاءِ لَضَالُّونَ', 'واذا راوهم قالوا ان هؤلاء لضالون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 33, 588, 3, 59, 30, 'وَمَا أُرْسِلُوا عَلَيْهِمْ حَافِظِينَ', 'وما ارسلوا عليهم حافظين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 34, 588, 3, 59, 30, 'فَالْيَوْمَ الَّذِينَ آمَنُوا مِنَ الْكُفَّارِ يَضْحَكُونَ', 'فاليوم الذين امنوا من الكفار يضحكون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 35, 589, 3, 59, 30, 'عَلَى الْأَرَائِكِ يَنْظُرُونَ', 'على الارائك ينظرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 83, 36, 589, 3, 59, 30, 'هَلْ ثُوِّبَ الْكُفَّارُ مَا كَانُوا يَفْعَلُونَ', 'هل ثوب الكفار ما كانوا يفعلون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 1, 589, 4, 59, 30, 'إِذَا السَّمَاءُ انْشَقَّتْ', 'اذا السماء انشقت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 2, 589, 4, 59, 30, 'وَأَذِنَتْ لِرَبِّهَا وَحُقَّتْ', 'واذنت لربها وحقت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 3, 589, 4, 59, 30, 'وَإِذَا الْأَرْضُ مُدَّتْ', 'واذا الارض مدت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 4, 589, 4, 59, 30, 'وَأَلْقَتْ مَا فِيهَا وَتَخَلَّتْ', 'والقت ما فيها وتخلت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 5, 589, 4, 59, 30, 'وَأَذِنَتْ لِرَبِّهَا وَحُقَّتْ', 'واذنت لربها وحقت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 6, 589, 4, 59, 30, 'يَا أَيُّهَا الْإِنْسَانُ إِنَّكَ كَادِحٌ إِلَى رَبِّكَ كَدْحًا فَمُلَاقِيهِ', 'يا ايها الانسان انك كادح الى ربك كدحا فملاقيه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 7, 589, 4, 59, 30, 'فَأَمَّا مَنْ أُوتِيَ كِتَابَهُ بِيَمِينِهِ', 'فاما من اوتي كتابه بيمينه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 8, 589, 4, 59, 30, 'فَسَوْفَ يُحَاسَبُ حِسَابًا يَسِيرًا', 'فسوف يحاسب حسابا يسيرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 9, 589, 4, 59, 30, 'وَيَنْقَلِبُ إِلَى أَهْلِهِ مَسْرُورًا', 'وينقلب الى اهله مسرورا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 10, 589, 4, 59, 30, 'وَأَمَّا مَنْ أُوتِيَ كِتَابَهُ وَرَاءَ ظَهْرِهِ', 'واما من اوتي كتابه وراء ظهره');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 11, 589, 4, 59, 30, 'فَسَوْفَ يَدْعُو ثُبُورًا', 'فسوف يدعو ثبورا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 12, 589, 4, 59, 30, 'وَيَصْلَى سَعِيرًا', 'ويصلى سعيرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 13, 589, 4, 59, 30, 'إِنَّهُ كَانَ فِي أَهْلِهِ مَسْرُورًا', 'انه كان في اهله مسرورا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 14, 589, 4, 59, 30, 'إِنَّهُ ظَنَّ أَنْ لَنْ يَحُورَ', 'انه ظن ان لن يحور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 15, 589, 4, 59, 30, 'بَلَى إِنَّ رَبَّهُ كَانَ بِهِ بَصِيرًا', 'بلى ان ربه كان به بصيرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 16, 589, 4, 59, 30, 'فَلَا أُقْسِمُ بِالشَّفَقِ', 'فلا اقسم بالشفق');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 17, 589, 4, 59, 30, 'وَاللَّيْلِ وَمَا وَسَقَ', 'والليل وما وسق');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 18, 589, 4, 59, 30, 'وَالْقَمَرِ إِذَا اتَّسَقَ', 'والقمر اذا اتسق');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 19, 589, 4, 59, 30, 'لَتَرْكَبُنَّ طَبَقًا عَنْ طَبَقٍ', 'لتركبن طبقا عن طبق');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 20, 589, 4, 59, 30, 'فَمَا لَهُمْ لَا يُؤْمِنُونَ', 'فما لهم لا يؤمنون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 21, 589, 4, 59, 30, 'وَإِذَا قُرِئَ عَلَيْهِمُ الْقُرْآنُ لَا يَسْجُدُونَ', 'واذا قرئ عليهم القران لا يسجدون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 22, 589, 4, 59, 30, 'بَلِ الَّذِينَ كَفَرُوا يُكَذِّبُونَ', 'بل الذين كفروا يكذبون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 23, 589, 4, 59, 30, 'وَاللَّهُ أَعْلَمُ بِمَا يُوعُونَ', 'والله اعلم بما يوعون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 24, 589, 4, 59, 30, 'فَبَشِّرْهُمْ بِعَذَابٍ أَلِيمٍ', 'فبشرهم بعذاب اليم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 84, 25, 589, 4, 59, 30, 'إِلَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ', 'الا الذين امنوا وعملوا الصالحات لهم اجر غير ممنون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 1, 590, 4, 59, 30, 'وَالسَّمَاءِ ذَاتِ الْبُرُوجِ', 'والسماء ذات البروج');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 2, 590, 4, 59, 30, 'وَالْيَوْمِ الْمَوْعُودِ', 'واليوم الموعود');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 3, 590, 4, 59, 30, 'وَشَاهِدٍ وَمَشْهُودٍ', 'وشاهد ومشهود');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 4, 590, 4, 59, 30, 'قُتِلَ أَصْحَابُ الْأُخْدُودِ', 'قتل اصحاب الاخدود');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 5, 590, 4, 59, 30, 'النَّارِ ذَاتِ الْوَقُودِ', 'النار ذات الوقود');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 6, 590, 4, 59, 30, 'إِذْ هُمْ عَلَيْهَا قُعُودٌ', 'اذ هم عليها قعود');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 7, 590, 4, 59, 30, 'وَهُمْ عَلَى مَا يَفْعَلُونَ بِالْمُؤْمِنِينَ شُهُودٌ', 'وهم على ما يفعلون بالمؤمنين شهود');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 8, 590, 4, 59, 30, 'وَمَا نَقَمُوا مِنْهُمْ إِلَّا أَنْ يُؤْمِنُوا بِاللَّهِ الْعَزِيزِ الْحَمِيدِ', 'وما نقموا منهم الا ان يؤمنوا بالله العزيز الحميد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 9, 590, 4, 59, 30, 'الَّذِي لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ وَاللَّهُ عَلَى كُلِّ شَيْءٍ شَهِيدٌ', 'الذي له ملك السماوات والارض والله على كل شيء شهيد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 10, 590, 4, 59, 30, 'إِنَّ الَّذِينَ فَتَنُوا الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ ثُمَّ لَمْ يَتُوبُوا فَلَهُمْ عَذَابُ جَهَنَّمَ وَلَهُمْ عَذَابُ الْحَرِيقِ', 'ان الذين فتنوا المؤمنين والمؤمنات ثم لم يتوبوا فلهم عذاب جهنم ولهم عذاب الحريق');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 11, 590, 4, 59, 30, 'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ ذَلِكَ الْفَوْزُ الْكَبِيرُ', 'ان الذين امنوا وعملوا الصالحات لهم جنات تجري من تحتها الانهار ذلك الفوز الكبير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 12, 590, 4, 59, 30, 'إِنَّ بَطْشَ رَبِّكَ لَشَدِيدٌ', 'ان بطش ربك لشديد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 13, 590, 4, 59, 30, 'إِنَّهُ هُوَ يُبْدِئُ وَيُعِيدُ', 'انه هو يبدئ ويعيد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 14, 590, 4, 59, 30, 'وَهُوَ الْغَفُورُ الْوَدُودُ', 'وهو الغفور الودود');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 15, 590, 4, 59, 30, 'ذُو الْعَرْشِ الْمَجِيدُ', 'ذو العرش المجيد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 16, 590, 4, 59, 30, 'فَعَّالٌ لِمَا يُرِيدُ', 'فعال لما يريد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 17, 590, 4, 59, 30, 'هَلْ أَتَاكَ حَدِيثُ الْجُنُودِ', 'هل اتاك حديث الجنود');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 18, 590, 4, 59, 30, 'فِرْعَوْنَ وَثَمُودَ', 'فرعون وثمود');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 19, 590, 4, 59, 30, 'بَلِ الَّذِينَ كَفَرُوا فِي تَكْذِيبٍ', 'بل الذين كفروا في تكذيب');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 20, 590, 4, 59, 30, 'وَاللَّهُ مِنْ وَرَائِهِمْ مُحِيطٌ', 'والله من ورائهم محيط');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 21, 590, 4, 59, 30, 'بَلْ هُوَ قُرْآنٌ مَجِيدٌ', 'بل هو قران مجيد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 85, 22, 590, 4, 59, 30, 'فِي لَوْحٍ مَحْفُوظٍ', 'في لوح محفوظ');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 86, 1, 591, 4, 59, 30, 'وَالسَّمَاءِ وَالطَّارِقِ', 'والسماء والطارق');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 86, 2, 591, 4, 59, 30, 'وَمَا أَدْرَاكَ مَا الطَّارِقُ', 'وما ادراك ما الطارق');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 86, 3, 591, 4, 59, 30, 'النَّجْمُ الثَّاقِبُ', 'النجم الثاقب');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 86, 4, 591, 4, 59, 30, 'إِنْ كُلُّ نَفْسٍ لَمَّا عَلَيْهَا حَافِظٌ', 'ان كل نفس لما عليها حافظ');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 86, 5, 591, 4, 59, 30, 'فَلْيَنْظُرِ الْإِنْسَانُ مِمَّ خُلِقَ', 'فلينظر الانسان مم خلق');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 86, 6, 591, 4, 59, 30, 'خُلِقَ مِنْ مَاءٍ دَافِقٍ', 'خلق من ماء دافق');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 86, 7, 591, 4, 59, 30, 'يَخْرُجُ مِنْ بَيْنِ الصُّلْبِ وَالتَّرَائِبِ', 'يخرج من بين الصلب والترائب');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 86, 8, 591, 4, 59, 30, 'إِنَّهُ عَلَى رَجْعِهِ لَقَادِرٌ', 'انه على رجعه لقادر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 86, 9, 591, 4, 59, 30, 'يَوْمَ تُبْلَى السَّرَائِرُ', 'يوم تبلى السرائر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 86, 10, 591, 4, 59, 30, 'فَمَا لَهُ مِنْ قُوَّةٍ وَلَا نَاصِرٍ', 'فما له من قوة ولا ناصر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 86, 11, 591, 4, 59, 30, 'وَالسَّمَاءِ ذَاتِ الرَّجْعِ', 'والسماء ذات الرجع');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 86, 12, 591, 4, 59, 30, 'وَالْأَرْضِ ذَاتِ الصَّدْعِ', 'والارض ذات الصدع');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 86, 13, 591, 4, 59, 30, 'إِنَّهُ لَقَوْلٌ فَصْلٌ', 'انه لقول فصل');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 86, 14, 591, 4, 59, 30, 'وَمَا هُوَ بِالْهَزْلِ', 'وما هو بالهزل');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 86, 15, 591, 4, 59, 30, 'إِنَّهُمْ يَكِيدُونَ كَيْدًا', 'انهم يكيدون كيدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 86, 16, 591, 4, 59, 30, 'وَأَكِيدُ كَيْدًا', 'واكيد كيدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 86, 17, 591, 4, 59, 30, 'فَمَهِّلِ الْكَافِرِينَ أَمْهِلْهُمْ رُوَيْدًا', 'فمهل الكافرين امهلهم رويدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 87, 1, 591, 1, 60, 30, 'سَبِّحِ اسْمَ رَبِّكَ الْأَعْلَى', 'سبح اسم ربك الاعلى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 87, 2, 591, 1, 60, 30, 'الَّذِي خَلَقَ فَسَوَّى', 'الذي خلق فسوى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 87, 3, 591, 1, 60, 30, 'وَالَّذِي قَدَّرَ فَهَدَى', 'والذي قدر فهدى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 87, 4, 591, 1, 60, 30, 'وَالَّذِي أَخْرَجَ الْمَرْعَى', 'والذي اخرج المرعى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 87, 5, 591, 1, 60, 30, 'فَجَعَلَهُ غُثَاءً أَحْوَى', 'فجعله غثاء احوى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 87, 6, 591, 1, 60, 30, 'سَنُقْرِئُكَ فَلَا تَنْسَى', 'سنقرئك فلا تنسى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 87, 7, 591, 1, 60, 30, 'إِلَّا مَا شَاءَ اللَّهُ إِنَّهُ يَعْلَمُ الْجَهْرَ وَمَا يَخْفَى', 'الا ما شاء الله انه يعلم الجهر وما يخفى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 87, 8, 591, 1, 60, 30, 'وَنُيَسِّرُكَ لِلْيُسْرَى', 'ونيسرك لليسرى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 87, 9, 591, 1, 60, 30, 'فَذَكِّرْ إِنْ نَفَعَتِ الذِّكْرَى', 'فذكر ان نفعت الذكرى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 87, 10, 591, 1, 60, 30, 'سَيَذَّكَّرُ مَنْ يَخْشَى', 'سيذكر من يخشى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 87, 11, 591, 1, 60, 30, 'وَيَتَجَنَّبُهَا الْأَشْقَى', 'ويتجنبها الاشقى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 87, 12, 591, 1, 60, 30, 'الَّذِي يَصْلَى النَّارَ الْكُبْرَى', 'الذي يصلى النار الكبرى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 87, 13, 591, 1, 60, 30, 'ثُمَّ لَا يَمُوتُ فِيهَا وَلَا يَحْيَى', 'ثم لا يموت فيها ولا يحيى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 87, 14, 591, 1, 60, 30, 'قَدْ أَفْلَحَ مَنْ تَزَكَّى', 'قد افلح من تزكى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 87, 15, 591, 1, 60, 30, 'وَذَكَرَ اسْمَ رَبِّهِ فَصَلَّى', 'وذكر اسم ربه فصلى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 87, 16, 592, 1, 60, 30, 'بَلْ تُؤْثِرُونَ الْحَيَاةَ الدُّنْيَا', 'بل تؤثرون الحياة الدنيا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 87, 17, 592, 1, 60, 30, 'وَالْآخِرَةُ خَيْرٌ وَأَبْقَى', 'والاخرة خير وابقى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 87, 18, 592, 1, 60, 30, 'إِنَّ هَذَا لَفِي الصُّحُفِ الْأُولَى', 'ان هذا لفي الصحف الاولى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 87, 19, 592, 1, 60, 30, 'صُحُفِ إِبْرَاهِيمَ وَمُوسَى', 'صحف ابراهيم وموسى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 1, 592, 1, 60, 30, 'هَلْ أَتَاكَ حَدِيثُ الْغَاشِيَةِ', 'هل اتاك حديث الغاشية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 2, 592, 1, 60, 30, 'وُجُوهٌ يَوْمَئِذٍ خَاشِعَةٌ', 'وجوه يومئذ خاشعة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 3, 592, 1, 60, 30, 'عَامِلَةٌ نَاصِبَةٌ', 'عاملة ناصبة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 4, 592, 1, 60, 30, 'تَصْلَى نَارًا حَامِيَةً', 'تصلى نارا حامية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 5, 592, 1, 60, 30, 'تُسْقَى مِنْ عَيْنٍ آنِيَةٍ', 'تسقى من عين انية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 6, 592, 1, 60, 30, 'لَيْسَ لَهُمْ طَعَامٌ إِلَّا مِنْ ضَرِيعٍ', 'ليس لهم طعام الا من ضريع');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 7, 592, 1, 60, 30, 'لَا يُسْمِنُ وَلَا يُغْنِي مِنْ جُوعٍ', 'لا يسمن ولا يغني من جوع');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 8, 592, 1, 60, 30, 'وُجُوهٌ يَوْمَئِذٍ نَاعِمَةٌ', 'وجوه يومئذ ناعمة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 9, 592, 1, 60, 30, 'لِسَعْيِهَا رَاضِيَةٌ', 'لسعيها راضية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 10, 592, 1, 60, 30, 'فِي جَنَّةٍ عَالِيَةٍ', 'في جنة عالية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 11, 592, 1, 60, 30, 'لَا تَسْمَعُ فِيهَا لَاغِيَةً', 'لا تسمع فيها لاغية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 12, 592, 1, 60, 30, 'فِيهَا عَيْنٌ جَارِيَةٌ', 'فيها عين جارية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 13, 592, 1, 60, 30, 'فِيهَا سُرُرٌ مَرْفُوعَةٌ', 'فيها سرر مرفوعة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 14, 592, 1, 60, 30, 'وَأَكْوَابٌ مَوْضُوعَةٌ', 'واكواب موضوعة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 15, 592, 1, 60, 30, 'وَنَمَارِقُ مَصْفُوفَةٌ', 'ونمارق مصفوفة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 16, 592, 1, 60, 30, 'وَزَرَابِيُّ مَبْثُوثَةٌ', 'وزرابي مبثوثة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 17, 592, 1, 60, 30, 'أَفَلَا يَنْظُرُونَ إِلَى الْإِبِلِ كَيْفَ خُلِقَتْ', 'افلا ينظرون الى الابل كيف خلقت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 18, 592, 1, 60, 30, 'وَإِلَى السَّمَاءِ كَيْفَ رُفِعَتْ', 'والى السماء كيف رفعت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 19, 592, 1, 60, 30, 'وَإِلَى الْجِبَالِ كَيْفَ نُصِبَتْ', 'والى الجبال كيف نصبت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 20, 592, 1, 60, 30, 'وَإِلَى الْأَرْضِ كَيْفَ سُطِحَتْ', 'والى الارض كيف سطحت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 21, 592, 1, 60, 30, 'فَذَكِّرْ إِنَّمَا أَنْتَ مُذَكِّرٌ', 'فذكر انما انت مذكر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 22, 592, 1, 60, 30, 'لَسْتَ عَلَيْهِمْ بِمُصَيْطِرٍ', 'لست عليهم بمصيطر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 23, 592, 1, 60, 30, 'إِلَّا مَنْ تَوَلَّى وَكَفَرَ', 'الا من تولى وكفر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 24, 592, 1, 60, 30, 'فَيُعَذِّبُهُ اللَّهُ الْعَذَابَ الْأَكْبَرَ', 'فيعذبه الله العذاب الاكبر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 25, 592, 1, 60, 30, 'إِنَّ إِلَيْنَا إِيَابَهُمْ', 'ان الينا ايابهم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 88, 26, 592, 1, 60, 30, 'ثُمَّ إِنَّ عَلَيْنَا حِسَابَهُمْ', 'ثم ان علينا حسابهم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 1, 593, 1, 60, 30, 'وَالْفَجْرِ', 'والفجر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 2, 593, 1, 60, 30, 'وَلَيَالٍ عَشْرٍ', 'وليال عشر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 3, 593, 1, 60, 30, 'وَالشَّفْعِ وَالْوَتْرِ', 'والشفع والوتر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 4, 593, 1, 60, 30, 'وَاللَّيْلِ إِذَا يَسْرِ', 'والليل اذا يسر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 5, 593, 1, 60, 30, 'هَلْ فِي ذَلِكَ قَسَمٌ لِذِي حِجْرٍ', 'هل في ذلك قسم لذي حجر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 6, 593, 1, 60, 30, 'أَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِعَادٍ', 'الم تر كيف فعل ربك بعاد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 7, 593, 1, 60, 30, 'إِرَمَ ذَاتِ الْعِمَادِ', 'ارم ذات العماد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 8, 593, 1, 60, 30, 'الَّتِي لَمْ يُخْلَقْ مِثْلُهَا فِي الْبِلَادِ', 'التي لم يخلق مثلها في البلاد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 9, 593, 1, 60, 30, 'وَثَمُودَ الَّذِينَ جَابُوا الصَّخْرَ بِالْوَادِ', 'وثمود الذين جابوا الصخر بالواد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 10, 593, 1, 60, 30, 'وَفِرْعَوْنَ ذِي الْأَوْتَادِ', 'وفرعون ذي الاوتاد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 11, 593, 1, 60, 30, 'الَّذِينَ طَغَوْا فِي الْبِلَادِ', 'الذين طغوا في البلاد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 12, 593, 1, 60, 30, 'فَأَكْثَرُوا فِيهَا الْفَسَادَ', 'فاكثروا فيها الفساد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 13, 593, 1, 60, 30, 'فَصَبَّ عَلَيْهِمْ رَبُّكَ سَوْطَ عَذَابٍ', 'فصب عليهم ربك سوط عذاب');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 14, 593, 1, 60, 30, 'إِنَّ رَبَّكَ لَبِالْمِرْصَادِ', 'ان ربك لبالمرصاد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 15, 593, 1, 60, 30, 'فَأَمَّا الْإِنْسَانُ إِذَا مَا ابْتَلَاهُ رَبُّهُ فَأَكْرَمَهُ وَنَعَّمَهُ فَيَقُولُ رَبِّي أَكْرَمَنِ', 'فاما الانسان اذا ما ابتلاه ربه فاكرمه ونعمه فيقول ربي اكرمن');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 16, 593, 1, 60, 30, 'وَأَمَّا إِذَا مَا ابْتَلَاهُ فَقَدَرَ عَلَيْهِ رِزْقَهُ فَيَقُولُ رَبِّي أَهَانَنِ', 'واما اذا ما ابتلاه فقدر عليه رزقه فيقول ربي اهانن');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 17, 593, 1, 60, 30, 'كَلَّا بَلْ لَا تُكْرِمُونَ الْيَتِيمَ', 'كلا بل لا تكرمون اليتيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 18, 593, 1, 60, 30, 'وَلَا تَحَاضُّونَ عَلَى طَعَامِ الْمِسْكِينِ', 'ولا تحاضون على طعام المسكين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 19, 593, 1, 60, 30, 'وَتَأْكُلُونَ التُّرَاثَ أَكْلًا لَمًّا', 'وتاكلون التراث اكلا لما');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 20, 593, 1, 60, 30, 'وَتُحِبُّونَ الْمَالَ حُبًّا جَمًّا', 'وتحبون المال حبا جما');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 21, 593, 1, 60, 30, 'كَلَّا إِذَا دُكَّتِ الْأَرْضُ دَكًّا دَكًّا', 'كلا اذا دكت الارض دكا دكا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 22, 593, 1, 60, 30, 'وَجَاءَ رَبُّكَ وَالْمَلَكُ صَفًّا صَفًّا', 'وجاء ربك والملك صفا صفا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 23, 593, 1, 60, 30, 'وَجِيءَ يَوْمَئِذٍ بِجَهَنَّمَ يَوْمَئِذٍ يَتَذَكَّرُ الْإِنْسَانُ وَأَنَّى لَهُ الذِّكْرَى', 'وجيء يومئذ بجهنم يومئذ يتذكر الانسان وانى له الذكرى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 24, 594, 1, 60, 30, 'يَقُولُ يَا لَيْتَنِي قَدَّمْتُ لِحَيَاتِي', 'يقول يا ليتني قدمت لحياتي');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 25, 594, 1, 60, 30, 'فَيَوْمَئِذٍ لَا يُعَذِّبُ عَذَابَهُ أَحَدٌ', 'فيومئذ لا يعذب عذابه احد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 26, 594, 1, 60, 30, 'وَلَا يُوثِقُ وَثَاقَهُ أَحَدٌ', 'ولا يوثق وثاقه احد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 27, 594, 1, 60, 30, 'يَا أَيَّتُهَا النَّفْسُ الْمُطْمَئِنَّةُ', 'يا ايتها النفس المطمئنة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 28, 594, 1, 60, 30, 'ارْجِعِي إِلَى رَبِّكِ رَاضِيَةً مَرْضِيَّةً', 'ارجعي الى ربك راضية مرضية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 29, 594, 1, 60, 30, 'فَادْخُلِي فِي عِبَادِي', 'فادخلي في عبادي');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 89, 30, 594, 1, 60, 30, 'وَادْخُلِي جَنَّتِي', 'وادخلي جنتي');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 1, 594, 2, 60, 30, 'لَا أُقْسِمُ بِهَذَا الْبَلَدِ', 'لا اقسم بهذا البلد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 2, 594, 2, 60, 30, 'وَأَنْتَ حِلٌّ بِهَذَا الْبَلَدِ', 'وانت حل بهذا البلد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 3, 594, 2, 60, 30, 'وَوَالِدٍ وَمَا وَلَدَ', 'ووالد وما ولد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 4, 594, 2, 60, 30, 'لَقَدْ خَلَقْنَا الْإِنْسَانَ فِي كَبَدٍ', 'لقد خلقنا الانسان في كبد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 5, 594, 2, 60, 30, 'أَيَحْسَبُ أَنْ لَنْ يَقْدِرَ عَلَيْهِ أَحَدٌ', 'ايحسب ان لن يقدر عليه احد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 6, 594, 2, 60, 30, 'يَقُولُ أَهْلَكْتُ مَالًا لُبَدًا', 'يقول اهلكت مالا لبدا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 7, 594, 2, 60, 30, 'أَيَحْسَبُ أَنْ لَمْ يَرَهُ أَحَدٌ', 'ايحسب ان لم يره احد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 8, 594, 2, 60, 30, 'أَلَمْ نَجْعَلْ لَهُ عَيْنَيْنِ', 'الم نجعل له عينين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 9, 594, 2, 60, 30, 'وَلِسَانًا وَشَفَتَيْنِ', 'ولسانا وشفتين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 10, 594, 2, 60, 30, 'وَهَدَيْنَاهُ النَّجْدَيْنِ', 'وهديناه النجدين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 11, 594, 2, 60, 30, 'فَلَا اقْتَحَمَ الْعَقَبَةَ', 'فلا اقتحم العقبة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 12, 594, 2, 60, 30, 'وَمَا أَدْرَاكَ مَا الْعَقَبَةُ', 'وما ادراك ما العقبة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 13, 594, 2, 60, 30, 'فَكُّ رَقَبَةٍ', 'فك رقبة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 14, 594, 2, 60, 30, 'أَوْ إِطْعَامٌ فِي يَوْمٍ ذِي مَسْغَبَةٍ', 'او اطعام في يوم ذي مسغبة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 15, 594, 2, 60, 30, 'يَتِيمًا ذَا مَقْرَبَةٍ', 'يتيما ذا مقربة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 16, 594, 2, 60, 30, 'أَوْ مِسْكِينًا ذَا مَتْرَبَةٍ', 'او مسكينا ذا متربة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 17, 594, 2, 60, 30, 'ثُمَّ كَانَ مِنَ الَّذِينَ آمَنُوا وَتَوَاصَوْا بِالصَّبْرِ وَتَوَاصَوْا بِالْمَرْحَمَةِ', 'ثم كان من الذين امنوا وتواصوا بالصبر وتواصوا بالمرحمة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 18, 594, 2, 60, 30, 'أُولَئِكَ أَصْحَابُ الْمَيْمَنَةِ', 'اولئك اصحاب الميمنة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 19, 594, 2, 60, 30, 'وَالَّذِينَ كَفَرُوا بِآيَاتِنَا هُمْ أَصْحَابُ الْمَشْأَمَةِ', 'والذين كفروا باياتنا هم اصحاب المشامة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 90, 20, 594, 2, 60, 30, 'عَلَيْهِمْ نَارٌ مُؤْصَدَةٌ', 'عليهم نار مؤصدة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 91, 1, 595, 2, 60, 30, 'وَالشَّمْسِ وَضُحَاهَا', 'والشمس وضحاها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 91, 2, 595, 2, 60, 30, 'وَالْقَمَرِ إِذَا تَلَاهَا', 'والقمر اذا تلاها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 91, 3, 595, 2, 60, 30, 'وَالنَّهَارِ إِذَا جَلَّاهَا', 'والنهار اذا جلاها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 91, 4, 595, 2, 60, 30, 'وَاللَّيْلِ إِذَا يَغْشَاهَا', 'والليل اذا يغشاها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 91, 5, 595, 2, 60, 30, 'وَالسَّمَاءِ وَمَا بَنَاهَا', 'والسماء وما بناها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 91, 6, 595, 2, 60, 30, 'وَالْأَرْضِ وَمَا طَحَاهَا', 'والارض وما طحاها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 91, 7, 595, 2, 60, 30, 'وَنَفْسٍ وَمَا سَوَّاهَا', 'ونفس وما سواها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 91, 8, 595, 2, 60, 30, 'فَأَلْهَمَهَا فُجُورَهَا وَتَقْوَاهَا', 'فالهمها فجورها وتقواها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 91, 9, 595, 2, 60, 30, 'قَدْ أَفْلَحَ مَنْ زَكَّاهَا', 'قد افلح من زكاها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 91, 10, 595, 2, 60, 30, 'وَقَدْ خَابَ مَنْ دَسَّاهَا', 'وقد خاب من دساها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 91, 11, 595, 2, 60, 30, 'كَذَّبَتْ ثَمُودُ بِطَغْوَاهَا', 'كذبت ثمود بطغواها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 91, 12, 595, 2, 60, 30, 'إِذِ انْبَعَثَ أَشْقَاهَا', 'اذ انبعث اشقاها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 91, 13, 595, 2, 60, 30, 'فَقَالَ لَهُمْ رَسُولُ اللَّهِ نَاقَةَ اللَّهِ وَسُقْيَاهَا', 'فقال لهم رسول الله ناقة الله وسقياها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 91, 14, 595, 2, 60, 30, 'فَكَذَّبُوهُ فَعَقَرُوهَا فَدَمْدَمَ عَلَيْهِمْ رَبُّهُمْ بِذَنْبِهِمْ فَسَوَّاهَا', 'فكذبوه فعقروها فدمدم عليهم ربهم بذنبهم فسواها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 91, 15, 595, 2, 60, 30, 'وَلَا يَخَافُ عُقْبَاهَا', 'ولا يخاف عقباها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 1, 595, 2, 60, 30, 'وَاللَّيْلِ إِذَا يَغْشَى', 'والليل اذا يغشى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 2, 595, 2, 60, 30, 'وَالنَّهَارِ إِذَا تَجَلَّى', 'والنهار اذا تجلى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 3, 595, 2, 60, 30, 'وَمَا خَلَقَ الذَّكَرَ وَالْأُنْثَى', 'وما خلق الذكر والانثى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 4, 595, 2, 60, 30, 'إِنَّ سَعْيَكُمْ لَشَتَّى', 'ان سعيكم لشتى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 5, 595, 2, 60, 30, 'فَأَمَّا مَنْ أَعْطَى وَاتَّقَى', 'فاما من اعطى واتقى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 6, 595, 2, 60, 30, 'وَصَدَّقَ بِالْحُسْنَى', 'وصدق بالحسنى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 7, 595, 2, 60, 30, 'فَسَنُيَسِّرُهُ لِلْيُسْرَى', 'فسنيسره لليسرى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 8, 595, 2, 60, 30, 'وَأَمَّا مَنْ بَخِلَ وَاسْتَغْنَى', 'واما من بخل واستغنى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 9, 595, 2, 60, 30, 'وَكَذَّبَ بِالْحُسْنَى', 'وكذب بالحسنى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 10, 595, 2, 60, 30, 'فَسَنُيَسِّرُهُ لِلْعُسْرَى', 'فسنيسره للعسرى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 11, 595, 2, 60, 30, 'وَمَا يُغْنِي عَنْهُ مَالُهُ إِذَا تَرَدَّى', 'وما يغني عنه ماله اذا تردى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 12, 595, 2, 60, 30, 'إِنَّ عَلَيْنَا لَلْهُدَى', 'ان علينا للهدى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 13, 595, 2, 60, 30, 'وَإِنَّ لَنَا لَلْآخِرَةَ وَالْأُولَى', 'وان لنا للاخرة والاولى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 14, 595, 2, 60, 30, 'فَأَنْذَرْتُكُمْ نَارًا تَلَظَّى', 'فانذرتكم نارا تلظى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 15, 596, 2, 60, 30, 'لَا يَصْلَاهَا إِلَّا الْأَشْقَى', 'لا يصلاها الا الاشقى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 16, 596, 2, 60, 30, 'الَّذِي كَذَّبَ وَتَوَلَّى', 'الذي كذب وتولى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 17, 596, 2, 60, 30, 'وَسَيُجَنَّبُهَا الْأَتْقَى', 'وسيجنبها الاتقى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 18, 596, 2, 60, 30, 'الَّذِي يُؤْتِي مَالَهُ يَتَزَكَّى', 'الذي يؤتي ماله يتزكى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 19, 596, 2, 60, 30, 'وَمَا لِأَحَدٍ عِنْدَهُ مِنْ نِعْمَةٍ تُجْزَى', 'وما لاحد عنده من نعمة تجزى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 20, 596, 2, 60, 30, 'إِلَّا ابْتِغَاءَ وَجْهِ رَبِّهِ الْأَعْلَى', 'الا ابتغاء وجه ربه الاعلى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 92, 21, 596, 2, 60, 30, 'وَلَسَوْفَ يَرْضَى', 'ولسوف يرضى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 93, 1, 596, 2, 60, 30, 'وَالضُّحَى', 'والضحى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 93, 2, 596, 2, 60, 30, 'وَاللَّيْلِ إِذَا سَجَى', 'والليل اذا سجى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 93, 3, 596, 2, 60, 30, 'مَا وَدَّعَكَ رَبُّكَ وَمَا قَلَى', 'ما ودعك ربك وما قلى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 93, 4, 596, 2, 60, 30, 'وَلَلْآخِرَةُ خَيْرٌ لَكَ مِنَ الْأُولَى', 'وللاخرة خير لك من الاولى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 93, 5, 596, 2, 60, 30, 'وَلَسَوْفَ يُعْطِيكَ رَبُّكَ فَتَرْضَى', 'ولسوف يعطيك ربك فترضى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 93, 6, 596, 2, 60, 30, 'أَلَمْ يَجِدْكَ يَتِيمًا فَآوَى', 'الم يجدك يتيما فاوى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 93, 7, 596, 2, 60, 30, 'وَوَجَدَكَ ضَالًّا فَهَدَى', 'ووجدك ضالا فهدى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 93, 8, 596, 2, 60, 30, 'وَوَجَدَكَ عَائِلًا فَأَغْنَى', 'ووجدك عائلا فاغنى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 93, 9, 596, 2, 60, 30, 'فَأَمَّا الْيَتِيمَ فَلَا تَقْهَرْ', 'فاما اليتيم فلا تقهر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 93, 10, 596, 2, 60, 30, 'وَأَمَّا السَّائِلَ فَلَا تَنْهَرْ', 'واما السائل فلا تنهر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 93, 11, 596, 2, 60, 30, 'وَأَمَّا بِنِعْمَةِ رَبِّكَ فَحَدِّثْ', 'واما بنعمة ربك فحدث');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 94, 1, 596, 3, 60, 30, 'أَلَمْ نَشْرَحْ لَكَ صَدْرَكَ', 'الم نشرح لك صدرك');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 94, 2, 596, 3, 60, 30, 'وَوَضَعْنَا عَنْكَ وِزْرَكَ', 'ووضعنا عنك وزرك');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 94, 3, 596, 3, 60, 30, 'الَّذِي أَنْقَضَ ظَهْرَكَ', 'الذي انقض ظهرك');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 94, 4, 596, 3, 60, 30, 'وَرَفَعْنَا لَكَ ذِكْرَكَ', 'ورفعنا لك ذكرك');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 94, 5, 596, 3, 60, 30, 'فَإِنَّ مَعَ الْعُسْرِ يُسْرًا', 'فان مع العسر يسرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 94, 6, 596, 3, 60, 30, 'إِنَّ مَعَ الْعُسْرِ يُسْرًا', 'ان مع العسر يسرا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 94, 7, 596, 3, 60, 30, 'فَإِذَا فَرَغْتَ فَانْصَبْ', 'فاذا فرغت فانصب');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 94, 8, 596, 3, 60, 30, 'وَإِلَى رَبِّكَ فَارْغَبْ', 'والى ربك فارغب');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 95, 1, 597, 3, 60, 30, 'وَالتِّينِ وَالزَّيْتُونِ', 'والتين والزيتون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 95, 2, 597, 3, 60, 30, 'وَطُورِ سِينِينَ', 'وطور سينين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 95, 3, 597, 3, 60, 30, 'وَهَذَا الْبَلَدِ الْأَمِينِ', 'وهذا البلد الامين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 95, 4, 597, 3, 60, 30, 'لَقَدْ خَلَقْنَا الْإِنْسَانَ فِي أَحْسَنِ تَقْوِيمٍ', 'لقد خلقنا الانسان في احسن تقويم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 95, 5, 597, 3, 60, 30, 'ثُمَّ رَدَدْنَاهُ أَسْفَلَ سَافِلِينَ', 'ثم رددناه اسفل سافلين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 95, 6, 597, 3, 60, 30, 'إِلَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَلَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ', 'الا الذين امنوا وعملوا الصالحات فلهم اجر غير ممنون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 95, 7, 597, 3, 60, 30, 'فَمَا يُكَذِّبُكَ بَعْدُ بِالدِّينِ', 'فما يكذبك بعد بالدين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 95, 8, 597, 3, 60, 30, 'أَلَيْسَ اللَّهُ بِأَحْكَمِ الْحَاكِمِينَ', 'اليس الله باحكم الحاكمين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 96, 1, 597, 3, 60, 30, 'اقْرَأْ بِاسْمِ رَبِّكَ الَّذِي خَلَقَ', 'اقرا باسم ربك الذي خلق');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 96, 2, 597, 3, 60, 30, 'خَلَقَ الْإِنْسَانَ مِنْ عَلَقٍ', 'خلق الانسان من علق');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 96, 3, 597, 3, 60, 30, 'اقْرَأْ وَرَبُّكَ الْأَكْرَمُ', 'اقرا وربك الاكرم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 96, 4, 597, 3, 60, 30, 'الَّذِي عَلَّمَ بِالْقَلَمِ', 'الذي علم بالقلم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 96, 5, 597, 3, 60, 30, 'عَلَّمَ الْإِنْسَانَ مَا لَمْ يَعْلَمْ', 'علم الانسان ما لم يعلم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 96, 6, 597, 3, 60, 30, 'كَلَّا إِنَّ الْإِنْسَانَ لَيَطْغَى', 'كلا ان الانسان ليطغى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 96, 7, 597, 3, 60, 30, 'أَنْ رَآهُ اسْتَغْنَى', 'ان راه استغنى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 96, 8, 597, 3, 60, 30, 'إِنَّ إِلَى رَبِّكَ الرُّجْعَى', 'ان الى ربك الرجعى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 96, 9, 597, 3, 60, 30, 'أَرَأَيْتَ الَّذِي يَنْهَى', 'ارايت الذي ينهى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 96, 10, 597, 3, 60, 30, 'عَبْدًا إِذَا صَلَّى', 'عبدا اذا صلى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 96, 11, 597, 3, 60, 30, 'أَرَأَيْتَ إِنْ كَانَ عَلَى الْهُدَى', 'ارايت ان كان على الهدى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 96, 12, 597, 3, 60, 30, 'أَوْ أَمَرَ بِالتَّقْوَى', 'او امر بالتقوى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 96, 13, 597, 3, 60, 30, 'أَرَأَيْتَ إِنْ كَذَّبَ وَتَوَلَّى', 'ارايت ان كذب وتولى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 96, 14, 597, 3, 60, 30, 'أَلَمْ يَعْلَمْ بِأَنَّ اللَّهَ يَرَى', 'الم يعلم بان الله يرى');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 96, 15, 597, 3, 60, 30, 'كَلَّا لَئِنْ لَمْ يَنْتَهِ لَنَسْفَعًا بِالنَّاصِيَةِ', 'كلا لئن لم ينته لنسفعا بالناصية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 96, 16, 597, 3, 60, 30, 'نَاصِيَةٍ كَاذِبَةٍ خَاطِئَةٍ', 'ناصية كاذبة خاطئة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 96, 17, 597, 3, 60, 30, 'فَلْيَدْعُ نَادِيَهُ', 'فليدع ناديه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 96, 18, 597, 3, 60, 30, 'سَنَدْعُ الزَّبَانِيَةَ', 'سندع الزبانية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 96, 19, 597, 3, 60, 30, 'كَلَّا لَا تُطِعْهُ وَاسْجُدْ وَاقْتَرِبْ', 'كلا لا تطعه واسجد واقترب');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 97, 1, 598, 3, 60, 30, 'إِنَّا أَنْزَلْنَاهُ فِي لَيْلَةِ الْقَدْرِ', 'انا انزلناه في ليلة القدر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 97, 2, 598, 3, 60, 30, 'وَمَا أَدْرَاكَ مَا لَيْلَةُ الْقَدْرِ', 'وما ادراك ما ليلة القدر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 97, 3, 598, 3, 60, 30, 'لَيْلَةُ الْقَدْرِ خَيْرٌ مِنْ أَلْفِ شَهْرٍ', 'ليلة القدر خير من الف شهر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 97, 4, 598, 3, 60, 30, 'تَنَزَّلُ الْمَلَائِكَةُ وَالرُّوحُ فِيهَا بِإِذْنِ رَبِّهِمْ مِنْ كُلِّ أَمْرٍ', 'تنزل الملائكة والروح فيها باذن ربهم من كل امر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 97, 5, 598, 3, 60, 30, 'سَلَامٌ هِيَ حَتَّى مَطْلَعِ الْفَجْرِ', 'سلام هي حتى مطلع الفجر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 98, 1, 598, 3, 60, 30, 'لَمْ يَكُنِ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ وَالْمُشْرِكِينَ مُنْفَكِّينَ حَتَّى تَأْتِيَهُمُ الْبَيِّنَةُ', 'لم يكن الذين كفروا من اهل الكتاب والمشركين منفكين حتى تاتيهم البينة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 98, 2, 598, 3, 60, 30, 'رَسُولٌ مِنَ اللَّهِ يَتْلُو صُحُفًا مُطَهَّرَةً', 'رسول من الله يتلو صحفا مطهرة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 98, 3, 598, 3, 60, 30, 'فِيهَا كُتُبٌ قَيِّمَةٌ', 'فيها كتب قيمة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 98, 4, 598, 3, 60, 30, 'وَمَا تَفَرَّقَ الَّذِينَ أُوتُوا الْكِتَابَ إِلَّا مِنْ بَعْدِ مَا جَاءَتْهُمُ الْبَيِّنَةُ', 'وما تفرق الذين اوتوا الكتاب الا من بعد ما جاءتهم البينة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 98, 5, 598, 3, 60, 30, 'وَمَا أُمِرُوا إِلَّا لِيَعْبُدُوا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ حُنَفَاءَ وَيُقِيمُوا الصَّلَاةَ وَيُؤْتُوا الزَّكَاةَ وَذَلِكَ دِينُ الْقَيِّمَةِ', 'وما امروا الا ليعبدوا الله مخلصين له الدين حنفاء ويقيموا الصلاة ويؤتوا الزكاة وذلك دين القيمة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 98, 6, 598, 3, 60, 30, 'إِنَّ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ وَالْمُشْرِكِينَ فِي نَارِ جَهَنَّمَ خَالِدِينَ فِيهَا أُولَئِكَ هُمْ شَرُّ الْبَرِيَّةِ', 'ان الذين كفروا من اهل الكتاب والمشركين في نار جهنم خالدين فيها اولئك هم شر البرية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 98, 7, 598, 3, 60, 30, 'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ أُولَئِكَ هُمْ خَيْرُ الْبَرِيَّةِ', 'ان الذين امنوا وعملوا الصالحات اولئك هم خير البرية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 98, 8, 599, 3, 60, 30, 'جَزَاؤُهُمْ عِنْدَ رَبِّهِمْ جَنَّاتُ عَدْنٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا رَضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ ذَلِكَ لِمَنْ خَشِيَ رَبَّهُ', 'جزاؤهم عند ربهم جنات عدن تجري من تحتها الانهار خالدين فيها ابدا رضي الله عنهم ورضوا عنه ذلك لمن خشي ربه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 99, 1, 599, 3, 60, 30, 'إِذَا زُلْزِلَتِ الْأَرْضُ زِلْزَالَهَا', 'اذا زلزلت الارض زلزالها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 99, 2, 599, 3, 60, 30, 'وَأَخْرَجَتِ الْأَرْضُ أَثْقَالَهَا', 'واخرجت الارض اثقالها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 99, 3, 599, 3, 60, 30, 'وَقَالَ الْإِنْسَانُ مَا لَهَا', 'وقال الانسان ما لها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 99, 4, 599, 3, 60, 30, 'يَوْمَئِذٍ تُحَدِّثُ أَخْبَارَهَا', 'يومئذ تحدث اخبارها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 99, 5, 599, 3, 60, 30, 'بِأَنَّ رَبَّكَ أَوْحَى لَهَا', 'بان ربك اوحى لها');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 99, 6, 599, 3, 60, 30, 'يَوْمَئِذٍ يَصْدُرُ النَّاسُ أَشْتَاتًا لِيُرَوْا أَعْمَالَهُمْ', 'يومئذ يصدر الناس اشتاتا ليروا اعمالهم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 99, 7, 599, 3, 60, 30, 'فَمَنْ يَعْمَلْ مِثْقَالَ ذَرَّةٍ خَيْرًا يَرَهُ', 'فمن يعمل مثقال ذرة خيرا يره');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 99, 8, 599, 3, 60, 30, 'وَمَنْ يَعْمَلْ مِثْقَالَ ذَرَّةٍ شَرًّا يَرَهُ', 'ومن يعمل مثقال ذرة شرا يره');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 100, 1, 599, 3, 60, 30, 'وَالْعَادِيَاتِ ضَبْحًا', 'والعاديات ضبحا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 100, 2, 599, 3, 60, 30, 'فَالْمُورِيَاتِ قَدْحًا', 'فالموريات قدحا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 100, 3, 599, 3, 60, 30, 'فَالْمُغِيرَاتِ صُبْحًا', 'فالمغيرات صبحا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 100, 4, 599, 3, 60, 30, 'فَأَثَرْنَ بِهِ نَقْعًا', 'فاثرن به نقعا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 100, 5, 599, 3, 60, 30, 'فَوَسَطْنَ بِهِ جَمْعًا', 'فوسطن به جمعا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 100, 6, 599, 3, 60, 30, 'إِنَّ الْإِنْسَانَ لِرَبِّهِ لَكَنُودٌ', 'ان الانسان لربه لكنود');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 100, 7, 599, 3, 60, 30, 'وَإِنَّهُ عَلَى ذَلِكَ لَشَهِيدٌ', 'وانه على ذلك لشهيد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 100, 8, 599, 3, 60, 30, 'وَإِنَّهُ لِحُبِّ الْخَيْرِ لَشَدِيدٌ', 'وانه لحب الخير لشديد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 100, 9, 599, 4, 60, 30, 'أَفَلَا يَعْلَمُ إِذَا بُعْثِرَ مَا فِي الْقُبُورِ', 'افلا يعلم اذا بعثر ما في القبور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 100, 10, 600, 4, 60, 30, 'وَحُصِّلَ مَا فِي الصُّدُورِ', 'وحصل ما في الصدور');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 100, 11, 600, 4, 60, 30, 'إِنَّ رَبَّهُمْ بِهِمْ يَوْمَئِذٍ لَخَبِيرٌ', 'ان ربهم بهم يومئذ لخبير');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 101, 1, 600, 4, 60, 30, 'الْقَارِعَةُ', 'القارعة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 101, 2, 600, 4, 60, 30, 'مَا الْقَارِعَةُ', 'ما القارعة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 101, 3, 600, 4, 60, 30, 'وَمَا أَدْرَاكَ مَا الْقَارِعَةُ', 'وما ادراك ما القارعة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 101, 4, 600, 4, 60, 30, 'يَوْمَ يَكُونُ النَّاسُ كَالْفَرَاشِ الْمَبْثُوثِ', 'يوم يكون الناس كالفراش المبثوث');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 101, 5, 600, 4, 60, 30, 'وَتَكُونُ الْجِبَالُ كَالْعِهْنِ الْمَنْفُوشِ', 'وتكون الجبال كالعهن المنفوش');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 101, 6, 600, 4, 60, 30, 'فَأَمَّا مَنْ ثَقُلَتْ مَوَازِينُهُ', 'فاما من ثقلت موازينه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 101, 7, 600, 4, 60, 30, 'فَهُوَ فِي عِيشَةٍ رَاضِيَةٍ', 'فهو في عيشة راضية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 101, 8, 600, 4, 60, 30, 'وَأَمَّا مَنْ خَفَّتْ مَوَازِينُهُ', 'واما من خفت موازينه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 101, 9, 600, 4, 60, 30, 'فَأُمُّهُ هَاوِيَةٌ', 'فامه هاوية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 101, 10, 600, 4, 60, 30, 'وَمَا أَدْرَاكَ مَا هِيَهْ', 'وما ادراك ما هيه');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 101, 11, 600, 4, 60, 30, 'نَارٌ حَامِيَةٌ', 'نار حامية');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 102, 1, 600, 4, 60, 30, 'أَلْهَاكُمُ التَّكَاثُرُ', 'الهاكم التكاثر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 102, 2, 600, 4, 60, 30, 'حَتَّى زُرْتُمُ الْمَقَابِرَ', 'حتى زرتم المقابر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 102, 3, 600, 4, 60, 30, 'كَلَّا سَوْفَ تَعْلَمُونَ', 'كلا سوف تعلمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 102, 4, 600, 4, 60, 30, 'ثُمَّ كَلَّا سَوْفَ تَعْلَمُونَ', 'ثم كلا سوف تعلمون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 102, 5, 600, 4, 60, 30, 'كَلَّا لَوْ تَعْلَمُونَ عِلْمَ الْيَقِينِ', 'كلا لو تعلمون علم اليقين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 102, 6, 600, 4, 60, 30, 'لَتَرَوُنَّ الْجَحِيمَ', 'لترون الجحيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 102, 7, 600, 4, 60, 30, 'ثُمَّ لَتَرَوُنَّهَا عَيْنَ الْيَقِينِ', 'ثم لترونها عين اليقين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 102, 8, 600, 4, 60, 30, 'ثُمَّ لَتُسْأَلُنَّ يَوْمَئِذٍ عَنِ النَّعِيمِ', 'ثم لتسالن يومئذ عن النعيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 103, 1, 601, 4, 60, 30, 'وَالْعَصْرِ', 'والعصر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 103, 2, 601, 4, 60, 30, 'إِنَّ الْإِنْسَانَ لَفِي خُسْرٍ', 'ان الانسان لفي خسر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 103, 3, 601, 4, 60, 30, 'إِلَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَتَوَاصَوْا بِالْحَقِّ وَتَوَاصَوْا بِالصَّبْرِ', 'الا الذين امنوا وعملوا الصالحات وتواصوا بالحق وتواصوا بالصبر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 104, 1, 601, 4, 60, 30, 'وَيْلٌ لِكُلِّ هُمَزَةٍ لُمَزَةٍ', 'ويل لكل همزة لمزة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 104, 2, 601, 4, 60, 30, 'الَّذِي جَمَعَ مَالًا وَعَدَّدَهُ', 'الذي جمع مالا وعدده');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 104, 3, 601, 4, 60, 30, 'يَحْسَبُ أَنَّ مَالَهُ أَخْلَدَهُ', 'يحسب ان ماله اخلده');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 104, 4, 601, 4, 60, 30, 'كَلَّا لَيُنْبَذَنَّ فِي الْحُطَمَةِ', 'كلا لينبذن في الحطمة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 104, 5, 601, 4, 60, 30, 'وَمَا أَدْرَاكَ مَا الْحُطَمَةُ', 'وما ادراك ما الحطمة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 104, 6, 601, 4, 60, 30, 'نَارُ اللَّهِ الْمُوقَدَةُ', 'نار الله الموقدة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 104, 7, 601, 4, 60, 30, 'الَّتِي تَطَّلِعُ عَلَى الْأَفْئِدَةِ', 'التي تطلع على الافئدة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 104, 8, 601, 4, 60, 30, 'إِنَّهَا عَلَيْهِمْ مُؤْصَدَةٌ', 'انها عليهم مؤصدة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 104, 9, 601, 4, 60, 30, 'فِي عَمَدٍ مُمَدَّدَةٍ', 'في عمد ممددة');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 105, 1, 601, 4, 60, 30, 'أَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِأَصْحَابِ الْفِيلِ', 'الم تر كيف فعل ربك باصحاب الفيل');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 105, 2, 601, 4, 60, 30, 'أَلَمْ يَجْعَلْ كَيْدَهُمْ فِي تَضْلِيلٍ', 'الم يجعل كيدهم في تضليل');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 105, 3, 601, 4, 60, 30, 'وَأَرْسَلَ عَلَيْهِمْ طَيْرًا أَبَابِيلَ', 'وارسل عليهم طيرا ابابيل');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 105, 4, 601, 4, 60, 30, 'تَرْمِيهِمْ بِحِجَارَةٍ مِنْ سِجِّيلٍ', 'ترميهم بحجارة من سجيل');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 105, 5, 601, 4, 60, 30, 'فَجَعَلَهُمْ كَعَصْفٍ مَأْكُولٍ', 'فجعلهم كعصف ماكول');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 106, 1, 602, 4, 60, 30, 'لِإِيلَافِ قُرَيْشٍ', 'لايلاف قريش');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 106, 2, 602, 4, 60, 30, 'إِيلَافِهِمْ رِحْلَةَ الشِّتَاءِ وَالصَّيْفِ', 'ايلافهم رحلة الشتاء والصيف');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 106, 3, 602, 4, 60, 30, 'فَلْيَعْبُدُوا رَبَّ هَذَا الْبَيْتِ', 'فليعبدوا رب هذا البيت');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 106, 4, 602, 4, 60, 30, 'الَّذِي أَطْعَمَهُمْ مِنْ جُوعٍ وَآمَنَهُمْ مِنْ خَوْفٍ', 'الذي اطعمهم من جوع وامنهم من خوف');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 107, 1, 602, 4, 60, 30, 'أَرَأَيْتَ الَّذِي يُكَذِّبُ بِالدِّينِ', 'ارايت الذي يكذب بالدين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 107, 2, 602, 4, 60, 30, 'فَذَلِكَ الَّذِي يَدُعُّ الْيَتِيمَ', 'فذلك الذي يدع اليتيم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 107, 3, 602, 4, 60, 30, 'وَلَا يَحُضُّ عَلَى طَعَامِ الْمِسْكِينِ', 'ولا يحض على طعام المسكين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 107, 4, 602, 4, 60, 30, 'فَوَيْلٌ لِلْمُصَلِّينَ', 'فويل للمصلين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 107, 5, 602, 4, 60, 30, 'الَّذِينَ هُمْ عَنْ صَلَاتِهِمْ سَاهُونَ', 'الذين هم عن صلاتهم ساهون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 107, 6, 602, 4, 60, 30, 'الَّذِينَ هُمْ يُرَاءُونَ', 'الذين هم يراءون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 107, 7, 602, 4, 60, 30, 'وَيَمْنَعُونَ الْمَاعُونَ', 'ويمنعون الماعون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 108, 1, 602, 4, 60, 30, 'إِنَّا أَعْطَيْنَاكَ الْكَوْثَرَ', 'انا اعطيناك الكوثر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 108, 2, 602, 4, 60, 30, 'فَصَلِّ لِرَبِّكَ وَانْحَرْ', 'فصل لربك وانحر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 108, 3, 602, 4, 60, 30, 'إِنَّ شَانِئَكَ هُوَ الْأَبْتَرُ', 'ان شانئك هو الابتر');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 109, 1, 603, 4, 60, 30, 'قُلْ يَا أَيُّهَا الْكَافِرُونَ', 'قل يا ايها الكافرون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 109, 2, 603, 4, 60, 30, 'لَا أَعْبُدُ مَا تَعْبُدُونَ', 'لا اعبد ما تعبدون');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 109, 3, 603, 4, 60, 30, 'وَلَا أَنْتُمْ عَابِدُونَ مَا أَعْبُدُ', 'ولا انتم عابدون ما اعبد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 109, 4, 603, 4, 60, 30, 'وَلَا أَنَا عَابِدٌ مَا عَبَدْتُمْ', 'ولا انا عابد ما عبدتم');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 109, 5, 603, 4, 60, 30, 'وَلَا أَنْتُمْ عَابِدُونَ مَا أَعْبُدُ', 'ولا انتم عابدون ما اعبد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 109, 6, 603, 4, 60, 30, 'لَكُمْ دِينُكُمْ وَلِيَ دِينِ', 'لكم دينكم ولي دين');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 110, 1, 603, 4, 60, 30, 'إِذَا جَاءَ نَصْرُ اللَّهِ وَالْفَتْحُ', 'اذا جاء نصر الله والفتح');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 110, 2, 603, 4, 60, 30, 'وَرَأَيْتَ النَّاسَ يَدْخُلُونَ فِي دِينِ اللَّهِ أَفْوَاجًا', 'ورايت الناس يدخلون في دين الله افواجا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 110, 3, 603, 4, 60, 30, 'فَسَبِّحْ بِحَمْدِ رَبِّكَ وَاسْتَغْفِرْهُ إِنَّهُ كَانَ تَوَّابًا', 'فسبح بحمد ربك واستغفره انه كان توابا');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 111, 1, 603, 4, 60, 30, 'تَبَّتْ يَدَا أَبِي لَهَبٍ وَتَبَّ', 'تبت يدا ابي لهب وتب');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 111, 2, 603, 4, 60, 30, 'مَا أَغْنَى عَنْهُ مَالُهُ وَمَا كَسَبَ', 'ما اغنى عنه ماله وما كسب');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 111, 3, 603, 4, 60, 30, 'سَيَصْلَى نَارًا ذَاتَ لَهَبٍ', 'سيصلى نارا ذات لهب');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 111, 4, 603, 4, 60, 30, 'وَامْرَأَتُهُ حَمَّالَةَ الْحَطَبِ', 'وامراته حمالة الحطب');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 111, 5, 603, 4, 60, 30, 'فِي جِيدِهَا حَبْلٌ مِنْ مَسَدٍ', 'في جيدها حبل من مسد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 112, 1, 604, 4, 60, 30, 'قُلْ هُوَ اللَّهُ أَحَدٌ', 'قل هو الله احد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 112, 2, 604, 4, 60, 30, 'اللَّهُ الصَّمَدُ', 'الله الصمد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 112, 3, 604, 4, 60, 30, 'لَمْ يَلِدْ وَلَمْ يُولَدْ', 'لم يلد ولم يولد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 112, 4, 604, 4, 60, 30, 'وَلَمْ يَكُنْ لَهُ كُفُوًا أَحَدٌ', 'ولم يكن له كفوا احد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 113, 1, 604, 4, 60, 30, 'قُلْ أَعُوذُ بِرَبِّ الْفَلَقِ', 'قل اعوذ برب الفلق');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 113, 2, 604, 4, 60, 30, 'مِنْ شَرِّ مَا خَلَقَ', 'من شر ما خلق');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 113, 3, 604, 4, 60, 30, 'وَمِنْ شَرِّ غَاسِقٍ إِذَا وَقَبَ', 'ومن شر غاسق اذا وقب');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 113, 4, 604, 4, 60, 30, 'وَمِنْ شَرِّ النَّفَّاثَاتِ فِي الْعُقَدِ', 'ومن شر النفاثات في العقد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 113, 5, 604, 4, 60, 30, 'وَمِنْ شَرِّ حَاسِدٍ إِذَا حَسَدَ', 'ومن شر حاسد اذا حسد');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 114, 1, 604, 4, 60, 30, 'قُلْ أَعُوذُ بِرَبِّ النَّاسِ', 'قل اعوذ برب الناس');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 114, 2, 604, 4, 60, 30, 'مَلِكِ النَّاسِ', 'ملك الناس');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 114, 3, 604, 4, 60, 30, 'إِلَهِ النَّاسِ', 'اله الناس');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 114, 4, 604, 4, 60, 30, 'مِنْ شَرِّ الْوَسْوَاسِ الْخَنَّاسِ', 'من شر الوسواس الخناس');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 114, 5, 604, 4, 60, 30, 'الَّذِي يُوَسْوِسُ فِي صُدُورِ النَّاسِ', 'الذي يوسوس في صدور الناس');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 114, 6, 604, 4, 60, 30, 'مِنَ الْجِنَّةِ وَالنَّاسِ', 'من الجنة والناس');
INSERT INTO Verses ( Sorahid, Number, Page, Quarter, Hezb, Joza, Text, SearchText) VALUES ( 2, 0, 2, 1, 1, 1, 'بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ', 'بسم الله الرحمن الرحيم');
